export function createApiGateway(services: Services) {
  masterRouter.stack = [];
  const router = Router();
  router.use(cors());
  masterRouter.use(router);
  const apispec = generateLocalOpenApiSpec(services);
  if (!apispec) return;
  const swaggerOptions = {
    oauth: {
      clientId: '89529659540-096trej1hjnt3gqogdbj5o573e463i22.apps.googleusercontent.com',
      scopes: ['https://www.googleapis.com/auth/firebase'],
    },
  };
  router.use(
    '/api-docs',
    swaggerUi.serve,
    swaggerUi.setup(apispec, {
      swaggerOptions,
    })
  );
  for (const path of Object.keys(apispec.paths)) {
    let expressPath = createExpressPath(path);
    for (const method of Object.keys(apispec.paths[path])) {
      const endpointConfig = apispec.paths[path][method];
      const authMethods = endpointConfig.security?.map((s) => Object.keys(s)).flat();
      const serviceId = path.replace('/api/v2/', '').split('/')[0];
      const pathPrefix = endpointConfig?.['x-tw']?.pathPrefix || serviceId;
      if (pathPrefix !== serviceId) {
        expressPath = expressPath.replace(`/api/v2/${serviceId}`, `/api/v2/${pathPrefix}`);
      }
      const isRunning = services[serviceId];
      if (isRunning) {
        const proxyOptions: proxyOptions = {
          target: `http://localhost:${services[serviceId]?.servicePort}`,
          changeOrigin: true,
        };
        router[method](expressPath, createAuthMiddleWare(endpointConfig, authMethods));
        if (endpointConfig['x-tw']?.rateLimits) {
          createRateLimitMiddlewares(endpointConfig['x-tw'].rateLimits).forEach((mw, i) => {
            router[method](expressPath, mw);
          });
        }
        router[method](expressPath, (req, res) => {
          res.setHeader('x-cli-by', 'api-gateway');
          req.url = req.url.replace(new RegExp(`^/api/v2/${pathPrefix}/`), '');
          proxy.all({ req, res, options: proxyOptions });
        });
      }
    }
  }
  return masterRouter;
}

export function copyFiles(info: TWRepo) {
  logger.info('Copying files from template', '...');
  const source = path.resolve(DIRNAME, 'templates', info.templateDir);
  const destination = info.path;
  fs.cpSync(source, destination, { recursive: true, force: true, verbatimSymlinks: true });
  replaceInFiles(info, destination);
}

export function replaceInFiles(info: TWRepo, destination: string) {
  for (let file of fs.readdirSync(destination)) {
    const filepath = path.join(destination, file);
    if (fs.statSync(filepath).isDirectory()) {
      replaceInFiles(info, filepath);
      continue;
    }
    const string = fs.readFileSync(filepath).toString();
    const newString = replace(string, info);
    fs.writeFileSync(filepath, newString);
  }
}

export async function getBase(): Promise<TWBase> {
  const base = await rawlist({
    message: 'Pick a template',
    choices: bases.map((base) => ({ value: base.id, name: base.name })),
  });
  const info = bases.find((b) => b.id === base);
  if (!info) {
    throw new Error('Invalid base');
  }
  return info;
}

export async function getDockerDeps(): Promise<string[]> {
  return await checkbox({
    message: 'Select docker dependencies',
    choices: ['emulators', 'redis', 'postgres', 'mongo', 'temporal'].map((dep) => ({
      name: dep,
      value: dep,
    })),
  });
}

export async function getMaintainers(): Promise<string[]> {
  const maintainers = (
    await input({
      message: `Who are the maintainers (Space-separated @triplewhale.com usernames)?`,
    })
  ).split(/\s+/);

  return maintainers.map((maintainer) => {
    if (maintainer.endsWith('@triplewhale.com')) {
      return maintainer.trim();
    }
    return maintainer.trim() + '@triplewhale.com';
  });
}

export async function getNames(
  info: TWBase
): Promise<{ humanName: string; computerName: string; providerId?: string }> {
  let humanName = await input({
    message:
      `What is the name of the ${info.isService ? 'service' : 'package'}?` +
      '\n\t(use a human name, e.g. "Google Ads" or "Test Utils")',
  });

  if (!humanName) {
    console.log('You must provide a name!');
    return await getNames(info);
  }

  let computerName = humanName.toLowerCase().replace(/\s+/g, '-');
  if (humanName.startsWith('@tw/') || humanName.match(/^[a-z-]+$/)) {
    computerName = humanName;
    humanName = humanName
      .replace('@tw/', '')
      .replace('-', ' ')
      .replace(/\b(\w)/g, (s) => s.toUpperCase());
  }

  return {
    humanName,
    computerName,
  };
}

export async function getSensoryFetcherName(info: TWBase): Promise<{
  providerId: string;
  humanName: string;
  computerName: string;
}> {
  let providerId = await input({
    message: `What is the provider id for the fetcher?`,
  });
  if (!providerId) {
    console.log('You must provide a provider id!');
    return await getSensoryFetcherName(info);
  }
  return {
    providerId,
    humanName:
      providerId.replace(/-/g, ' ').replace(/\b(\w)/g, (s) => s.toUpperCase()) + ' Fetcher',
    computerName: providerId + '-fetcher',
  };
}

export async function getTags(): Promise<string[]> {
  return (
    await input({
      message: 'Add tags (separate tags by space like `tag-a tag-b`)?',
    })
  )
    .split('s+')
    .map((t) => t.trim());
}

export function getRoot(dirName: string) {
  const pathRelativeToGit = execSync(['git', 'rev-parse', '--show-prefix'].join(' '))
    .toString()
    .trim();

  if (!pathRelativeToGit && process.cwd().split('/').pop() === dirName) {
    return process.cwd();
  }

  const relPath: string = pathRelativeToGit
    .split('/')
    .filter(Boolean)
    .map((_) => '../')
    .join('');

  const rootPath = path.resolve(relPath, dirName);
  const parts = rootPath.split('/');
  if (parts.pop() === parts.pop()) {
    return path.resolve(rootPath.split('/').slice(0, -1).join('/'));
  }

  return rootPath;
}

export async function initTW() {
  await runNpmAuth();
  const { current, latest } = await notifier.fetchInfo();
  if (current !== latest) {
    cliExit('Please update @tw/cli to latest version to continue.');
  }

  const branch = await getCurrentBranch();
  const base: TWBase = await getBase();
  let { providerId, humanName, computerName } = base.getNames
    ? await base.getNames(base)
    : await getNames(base);
  const color = await pickColor(humanName, logger);
  const path = await getPath(humanName, computerName, base.isService);
  const dirName = path.split('/').pop();
  if (dirName && dirName !== computerName) {
    computerName = dirName;
  }
  const maintainers = await getMaintainers();
  let dockerDeps: string[] | undefined = undefined;
  let tags: string[] | undefined = undefined;

  if (base.isService) {
    dockerDeps = await getDockerDeps();
    tags = await getTags();
  }

  const info: TWRepo = {
    ...base,
    ...(base.isFetcher && { providerId }),
    color,
    humanName,
    computerName,
    path,
    maintainers,
  };

  if (!!dockerDeps) {
    info.dockerDeps = [...dockerDeps];
  }
  if (!!tags) {
    info.tags = [...tags];
  }

  makeNewPath(info.path);
  try {
    makeLanguageDefaults(info);
    copyFiles(info);
    install(info);
    if (info.isService) {
      makeTWConfig(info);
      makeInfra(info);
    }
  } catch (e: any) {
    e.message = `Couldn't create ${info.computerName} repo: ${e.message}`;
    try {
      fs.rmSync(info.path, { recursive: true, force: true });
    } catch (_e: any) {
      logger.error(`Couldn't remove ${info.path}: ${_e.message}`);
    }
    throw e;
  }
}

export function install(info: TWRepo) {
  switch (info.language) {
    case 'ts':
      installNode(info);
      break;
    case 'python':
      installPython(info);
      break;
  }
}

export class Logger {
  color: string;
  name: string;
  constructor() {
    this.color = 'FFFFFF';
    this.name = '';
  }
  setColor(hex: string) {
    this.color = hex;
  }
  setName(name: string) {
    this.name = name;
  }
  info(...message: string[]) {
    console.log(chalk.hex(this.color)(this.name), ' | ', ...message);
  }
  error(...message: string[]) {
    chalk.red(console.error(...message));
  }
}

export function makeInfra(info: TWServiceRepo) {
  logger.info('Making service infra', '...');
  makeInfraPackageJson(info);
  makePulumiYamls(info);
  copyTemplateInfraFiles(info);
}

export function makePythonDefaults(info: TWPythonRepo) {
  logger.info('Making requirements.txt', '...');
  const requirementsTxt = (info.requirements || []).join('\n') + '\n';
  fs.writeFileSync(path.join(info.path, 'requirements.txt'), requirementsTxt);
}

export function makeTWConfig(info: TWRepo) {
  logger.info('Making tw-config.json...');
  const config = {
    env: {
      PORT: 8080,
      DEBUG_PORT: 9229 + info.computerName.length,
      SERVICE_ID: info.computerName,
    },
    color: info.color,
    dependencies: info.dockerDeps,
    tags: info.tags,
    maintainers: info.maintainers,
    runtime: {
      ts: 'node',
      python: 'python',
    }[info.language],
  };
  fs.writeFileSync(path.join(info.path, 'tw-config.json'), JSON.stringify(config, null, 2));
}

export async function makeTypescriptDefaults(info: TWTsRepo) {
  const source = path.resolve(DIRNAME, 'templates', info.templateDir);
  makePackageJson(info, source);
  makeTsConfigJson(info, source);
  if (info.isJest) {
    makeJestConfig(info, source);
  }
}

export function npmInstall(path: string, dependencies: string[], devDependencies?: string[]) {
  execSync('tw auth');
  const command = ['npm', 'install', '--save', '--progress=false', ...dependencies];
  if (devDependencies?.length) {
    command.push('&&', 'npm', 'install', '--save-dev', ...devDependencies);
  }
  execSync(command.join(' '), { cwd: path, stdio: 'pipe' });
}

export function makeNewPath(path: string) {
  logger.info('Making new path', path, '...');
  fs.mkdirSync(path);
  return path;
}

export async function getPath(humanName: string, computerName: string, isService: boolean) {
  logger.info(`cwd is ${process.cwd()}`);
  const dir = await input({
    message: `What is the path for ${humanName}?`,
    default: getRoot(isService ? 'services' : 'packages') + '/' + computerName,
  });
  return resolve(process.cwd(), dir);
}

export async function pickColor(humanName: string, logger: Logger): Promise<string> {
  const hex = await select({
    message: 'Pick a nice color:',
    choices: colors256Hex.map((hex, i) => ({
      name: chalk.hex(hex)(humanName),
      value: hex,
    })),
    pageSize: 20,
  });
  logger.setColor(hex);
  logger.setName(humanName);
  return '' + hex;
}

export function replace(string: string, info: TWRepo) {
  return string
    .replace(/\$SERVICE_ID/g, info.computerName)
    .replace(/\$SERVICE_NAME/g, info.humanName)
    .replace(/\$COLOR/g, info.color)
    .replace(/\$PACKAGE_NAME/g, '@tw/' + info.computerName)
    .replace(/\$PROVIDER_ID/g, info.providerId || '');
}

