export async function connectToClickhouse(argv) {
  const { cluster_name = 'sonic-cluster', replica } = argv;

  const data = await downloadFile(
    `devops-${cliConfig.projectId}`,
    `hosted-service/clickhouse/${cluster_name}.json`,
    { forceCloud: true }
  );

  let domain = data.clickhouseTcpDomain;
  if (replica !== undefined) {
    domain = data.replicas.find((r: any) => r.name === `instance-0-${replica}`)?.domain;
  }
  const username = data.username;
  const password = data.password;
  if (!domain) {
    cliExit("Didn't find domain");
  }
  await runProcess({
    command: 'clickhouse',
    commandArgs: ['client', `--user`, username, `--password`, password, `--host`, domain],
    additionalArgs: {
      stdio: ['inherit', 'inherit'],
    },
  });
}

export function getConfig(argv) {
  const key = argv.key || argv._[1];
  console.log(get(key));
}

export function get(key) {
  return configStore.get(getKey(key));
}

export function set(key, value) {
  return configStore.set(getKey(key), value);
}

export function setConfig(argv) {
  const [key, value] = [argv.key || argv._[1], argv.value || argv._[2]];
  set(key, value);
}

export async function runDeploy(argv) {
  const envs = [];
  if (argv.beam) {
    return await runDeployPipeline(argv);
  }
  if (argv.debug) {
    envs.push('LOG_LEVEL=debug');
  } else if (argv.trace) {
    envs.push('LOG_LEVEL=trace');
  }
  if (argv.force) {
    envs.push('F=true');
  }
  let serviceIds: string[] = argv.services || argv._.slice(1);
  const substitutions = {};
  if (argv.select || !serviceIds.length) {
    serviceIds = await selectServices();
  }
  if (!serviceIds) {
    cliExit('No services selected');
  }

  const services = await Promise.all(serviceIds.map((s) => getServiceInfo(s)));

  if (argv.unitTests) {
    await Promise.all(
      services
        .filter((s) => s.runtime === 'node')
        .map((s) => runNpmTest({ name: s.serviceName, absolutePath: s.absolutePath }, argv))
    );
  }

  await triggerCloudBuilds({
    argv,
    buildNames: services.map((s) => getBuildNameForService(s.serviceName)),
    substitutions,
    dirs: services.map((s) => s.absolutePath + '/infra'),
    envs,
  });
}

export async function runDeployPipeline(argv) {
  let pipelineIds: string[] = argv.services || argv._.slice(1);

  const services = await Promise.all(pipelineIds.map(async (s) => await getServiceInfo(s, true)));

  if (argv.select || !pipelineIds.length) {
    pipelineIds = await selectPipelines();
  }
  if (!pipelineIds) {
    cliExit('No pipelines selected');
  }

  const substitutions = {};

  await triggerCloudBuilds({
    argv,
    buildNames: services.map((service) => getBuildNameForPipeline(service.serviceName)),
    substitutions,
    dirs: services.map((s) => s.absolutePath + '/infra'),
  });
}

