export async function runBuildImage(argv) {
  const author = getGcloudUserEmail();
  let projects;
  const branch = await getCurrentBranch();
  if (branch === 'master') {
    projects = await selectProjects();
  } else {
    projects = ['triple-whale-staging'];
  }

  const dockerfile = argv.f || 'Dockerfile';
  const dockerRepo = argv.r || 'devops-docker';
  const extraTag = argv.t || '';
  const sha = await getGitSha();
  const baseTags = [extraTag || sha];
  const substitutions = {
    _AUTHOR: author,
    _BRANCH_NAME: branch,
    _F: dockerfile,
  };

  const builds: Revision[] = [];

  const dir = path.resolve(path.dirname(dockerfile));

  const buildArgs = ['PROJECT_ID=$PROJECT_ID'];

  let revisions: Revision[];
  if (fs.existsSync(path.join(dir, 'tw.json'))) {
    const twJson = JSON.parse(fs.readFileSync(path.join(dir, 'tw.json'), 'utf8'));
    revisions = twJson.revisions;
  }
  for (const project of projects) {
    if (revisions) {
      revisions.forEach((revision) => {
        builds.push({
          buildArgs: [...revision.buildArgs, ...buildArgs],
          projectId: project,
          tags: [...revision.tags],
        });
      });
    } else {
      builds.push({
        buildArgs,
        projectId: project,
        tags: ['latest', ...baseTags],
      });
    }
  }

  // set _IMAGE_NAME to the dir name of where the dockerfile is located
  const dockerfilePath = path.join(process.cwd(), dockerfile);
  const dirName = path.dirname(dockerfilePath);
  const imageName = path.basename(dirName);
  substitutions['_IMAGE_NAME'] = imageName;
  substitutions['_DOCKER_REPO'] = dockerRepo;
  builds.map(({ projectId, tags }) => {
    tags.map((tag) => {
      cliLog(
        `<${projectId}> us-central1-docker.pkg.dev/${projectId}/${dockerRepo}/${imageName}:${tag}`
      );
    });
  });

  await Promise.all(
    builds.map(({ buildArgs, tags, projectId }) => {
      const configFile = temp.path({ suffix: '.yaml' });
      cliLog(`File: ${configFile}`);
      fs.writeFileSync(configFile, createConfigString({ tags, buildArgs }));
      return runProcess({
        log: true,
        name: projectId,
        command: 'gcloud',
        commandArgs: [
          'builds',
          'submit',
          `--config=${configFile}`,
          `--substitutions=${objectToEnv(substitutions)}`,
          '.',
          `--project=${projectId}`,
        ],
      });
    })
  );
}

export async function runK8sJob(argv) {
  const client = getStorageClient({ forceCloud: true });
  const jobs = (
    await client.bucket(`devops-${cliConfig.projectId}`).getFiles({
      prefix: `k8s-jobs/${argv.service}`,
    })
  )[0].map((file) => file.name.split('/').pop().split('.').shift());
  if (!jobs.includes(argv.name)) {
    cliError(`Job ${argv.name} not found.`);
    cliLog(`Available jobs: ${jobs.join(', ')}`);
    return;
  }
  const config = await downloadFile(
    `devops-${cliConfig.projectId}`,
    `k8s-jobs/${argv.service}/${argv.name}.json`,
    {
      forceCloud: true,
    }
  );

  const kc = new k8s.KubeConfig();
  kubeAuth(config.context);
  kc.loadFromDefault();
  kc.setCurrentContext(config.context);
  const [_1, _2, location, cluster] = kc.currentContext.split('_');
  const k8sApi = kc.makeApiClient(k8s.BatchV1Api);
  const jobName = `${config.job.metadata.name}-${uuid.v4()}`;
  const namespace = config.job.metadata.namespace;
  const extraArgs = argv._.slice(1);
  const container = config.job.spec.template.spec.containers[0];
  container['command'] = [...(container['command'] || []), ...extraArgs];
  function convertEnvs(envs) {
    return envs.map((env) => {
      const [name, value] = env.split('=');
      return { name, value };
    });
  }
  container['env'] = container['env'].concat(convertEnvs(argv.env || []));
  container['env'].push({ name: 'AUTHOR', value: getGcloudUserEmail() });
  const result = deepMerge(config, {
    job: {
      metadata: {
        name: jobName,
      },
    },
  });
  if (argv.trace) {
    container['env'].push({ name: 'LOG_LEVEL', value: 'trace' });
  }

  cliLog(`Job info:
  name: ${jobName}
  namespace: ${namespace}
  command: ${result['job']['spec']['template']['spec']['containers'][0]['command'].join(' ')}`);
  await confirm('OK to continue?');
  try {
    const res = await k8sApi.createNamespacedJob({
      namespace: result.job.metadata.namespace,
      body: result.job,
    });
    cliSuccess(`Job ${jobName} created.`);
    console.log();
    cliLog(
      `Logs: ${logsLinkFilter(
        {
          'labels.k8s-pod/batch_kubernetes_io/controller-uid': res.metadata.uid,
        },
        cliConfig.projectId
      )}`
    );
    console.log();
    cliLog(
      `K8s: https://console.cloud.google.com/kubernetes/job/${location}/${cluster}/${namespace}/${jobName}/details?project=${cliConfig.projectId}`
    );
  } catch (e) {
    cliError(e.body?.message || e.body || e);
  }
}

export function openLogs(argv) {
  const trace = copy.paste();
  let project = 'shofifi';
  if (argv.project || argv.prod || argv.stg) {
    project = cliConfig.projectId;
  }
  spawn('open', [
    `https://console.cloud.google.com/logs/query;query=trace%3D%22projects%2F${project}%2Ftraces%2F${trace}%22;duration=P1D?project=${project}`,
  ]);

  spawn('open', [
    `https://us5.datadoghq.com/apm/traces?query=${`env:${project} operation_name:express.request @tw.traceId:${trace}&cols=core_service,core_resource_name,log_duration,log_http.method,log_http.status_code&graphType=flamegraph&historicalData=false&messageDisplay=inline&query_translation_version=v0&shouldShowLegend=true&sort=time&spanType=all&spanViewType=metadata&tq_query_translation_version=v0&traceQuery=&view=spans`}`,
  ]);
}

export async function cleanUpArtifacts(argv) {
  const packageName = argv.package;
  const repo = '//us-central1-npm.pkg.dev/shofifi/npm-packages/:_authToken=';
  const baseUrl = 'https://us-central1-npm.pkg.dev/shofifi/npm-packages';
  const url = `${baseUrl}/${packageName}`;
  const rcData = fs.readFileSync(globalNpmrcFile).toString();
  const lines = rcData.split('\n');
  const token = lines
    .find((l) => l.includes(repo))
    ?.split('=')[1]
    .replaceAll('"', '');
  const headers = {
    Authorization: `Bearer ${token}`,
  };
  const { data } = await axios.get(url, {
    headers,
  });
  const tags = Object.keys(data['dist-tags']);
  for (const tag of tags) {
    if (['latest', 'pre'].includes(tag)) continue;
    const res = await axios.delete(`${baseUrl}/-/package/${packageName}/dist-tags/${tag}`, {
      headers,
    });
    console.log(tag, res.status);
  }
  const versions = Object.keys(data['versions']);
  const latest = new SemVer(data['dist-tags']['latest']);
  for (const version of versions) {
    if (['created', 'modified'].includes(version)) continue;
    const semver = new SemVer(version);
    if (!versions.includes(version)) {
      console.log(version, 'not in versions');
      continue;
    }
    const time = data['time'][version];
    const date = moment(time);
    const diff = moment().diff(date, 'days');
    if (semver.prerelease.length) {
      if (diff > 30) {
        console.log(semver);
        await unpublish(packageName, version);
      }
    } else if (latest.minor - semver.minor >= 1 && diff > 200) {
      console.log(semver);
      await unpublish(packageName, version);
    }
  }
}

export async function linkCommand(argv) {
