export async function runPublish(argv) {
  const girUrl = await getGitUrl();
  let packages: string[] = argv.packages;
  if (argv.select || !packages.length) {
    if (girUrl === packagesGitUrl) {
      packages = await selectPackages();
    } else {
      cliExit('You must specify the package name');
    }
  }
  if (!packages) {
    cliExit('No packages selected');
  }
  const branch = await getCurrentBranch();
  argv.project = branch === 'master' ? 'shofifi' : 'triple-whale-staging';
  process.env.PROJECT_ID = argv.project;
  await gitPull();
  const pkgInfos = await Promise.all(packages.map((pkg) => getPackageInfo(pkg)));

  if (argv.unitTests) {
    const tests = pkgInfos
      .filter((p) => p.runtime === 'node')
      .map((pkg) => runNpmTest({ name: pkg.packageName, absolutePath: pkg.absolutePath }, argv));
    await Promise.all(tests);
  }

  const promises = [];
  for (const { packageName, absolutePath, packagePath, runtime } of pkgInfos) {
    const { version, substitutions } = await getNextVersion(
      argv.project,
      absolutePath,
      branch,
      runtime
    );

    promises.push(
      await triggerCloudBuilds({
        argv,
        buildNames: [runtime === 'node' ? 'publish-package' : 'publish-python-package'],
        substitutions: {
          _PACKAGE_DIR: packagePath || '.',
          _PACKAGE_NAME: packageName,
          _BRANCH_NAME: branch,
          _REPO: await getGitRepoName(),
          _GITHUB_ORG: await getGithubOrg(),
          ...substitutions,
        },
      })
    );
    if (runtime === 'node') {
      cliSuccess(`<${packageName}> Install with: ${packageName}@${version}`);
    } else {
      cliSuccess(`<${packageName}> New version will be: ${version}`);
    }
  }
  await Promise.all(promises);
}

export async function updateCommand(argv) {
  const packages = argv.packages?.length ? argv.packages : await selectPackages();
  const dirs = argv.pulumi
    ? await selectPulumiProjects()
    : (await selectServices()).map((service) => `services/${service}`);

  for (const relativeDir of dirs) {
    let serviceDir = `${cliConfig.gitRoot}/${relativeDir}`;
    await runProcess({
      name: relativeDir,
      command: 'tw',
      commandArgs: ['i', ...packages.map((pkg) => '@tw/' + pkg + '@latest')],
      additionalArgs: { cwd: resolve(serviceDir) },
      log: true,
    });
  }
}

export async function getPulumiPaths(argv) {
  const gitRoot = await getGitRoot();

  // if user passes paths as param, treat as relative to working dir
  let dirs: string[] = argv.dirs || argv._.slice(1);
  let dirsAbsolute = dirs.map((dir) => path.resolve(dir));

  // if user selects paths from list, treat as relative to git root
  if (!dirsAbsolute.length) {
    dirs = await selectPulumiProjects();
    dirsAbsolute = dirs.map((dir) => path.resolve(gitRoot, dir));
  }
  if (!dirsAbsolute) {
    cliExit('No pulumi projects selected');
  }
  return { gitRoot, dirsAbsolute };
}

export async function runPulumiRefresh(argv, justTrigger = false) {
  delete process.env.IS_LOCAL;

  const { gitRoot, dirsAbsolute } = await getPulumiPaths(argv);
  const applicableStacks = await getApplicableStacks(argv, dirsAbsolute);
  for (const dirAbsolute of dirsAbsolute) {
    const pathFromGitRoot = path.relative(gitRoot, dirAbsolute);

    if (!fs.existsSync(path.join(dirAbsolute, 'Pulumi.yaml'))) {
      cliExit('Provided path is not a pulumi project');
    }

    const pulumiProject = (await loadPulumiProject(dirAbsolute)).name;
    const repo = await getGitRepoName();
    const branch = await getCurrentBranch();
    const substitutions = {
      _SERVICE_ID: pulumiProject,
      _PULUMI_PROJECT: pulumiProject,
      _PATH: pathFromGitRoot,
      _BRANCH_NAME: branch,
      _REPO: repo,
      _GITHUB_ORG: await getGithubOrg(),
    };

    for (let stack of applicableStacks) {
      await triggerCloudBuilds({
        argv,
        buildNames: ['pulumi-refresh'],
        substitutions,
        dirs: [dirAbsolute],
        applicableStacks: [stack],
      });
    }
  }
}

export async function runPulumiUp(argv, justTrigger = false) {
  delete process.env.IS_LOCAL;

  const { gitRoot, dirsAbsolute } = await getPulumiPaths(argv);
  const applicableStacks = await getApplicableStacks(argv, dirsAbsolute);
  for (const dirAbsolute of dirsAbsolute) {
    const pathFromGitRoot = path.relative(gitRoot, dirAbsolute);

    if (!fs.existsSync(path.join(dirAbsolute, 'Pulumi.yaml'))) {
      cliExit('Provided path is not a pulumi project');
    }

    const pulumiProject = (await loadPulumiProject(dirAbsolute)).name;
    const repo = await getGitRepoName();
    const branch = await getCurrentBranch();
    const substitutions = {
      _SERVICE_ID: pulumiProject,
      _PULUMI_PROJECT: pulumiProject,
      _PATH: pathFromGitRoot,
      _BRANCH_NAME: branch,
      _REPO: repo,
      _GITHUB_ORG: await getGithubOrg(),
    };
    if (justTrigger) {
      substitutions['_JUST_TRIGGER'] = true;
    }

    let runPreviews = !argv.noPreview;

    for (let stack of applicableStacks) {
      let continueValue = 'yes';
      if (runPreviews) {
        let command = 'pulumi';
        let cmdArgs = [
          'preview',
          '--color=always',
          `--stack=${stack.startsWith('triplewhale/') ? stack : `triplewhale/${stack}`}`,
        ];
        if (dirAbsolute !== '.') {
          cmdArgs.push(`--cwd=${dirAbsolute}`);
        }
        let cmd = `${command} ${cmdArgs.join(' ')}`;
        const diffProc = spawn(command, cmdArgs.concat(['--diff']));
        let diffErr: string = '';
        diffProc.on('error', (err) => {
          diffErr = err.message;
        });
        const diffStream = new stream.PassThrough();
        diffProc.stdout.pipe(diffStream);
        await runIDirectory(dirAbsolute);
        execSync(cmd, { encoding: 'utf-8', stdio: 'inherit' });
        continueValue = await continuePrompt(stack);
        if (continueValue === 'diff') {
          if (diffErr) {
            cliExit(`Error running diff: ${diffErr}`);
          }
          process.stdout.write(Buffer.from(diffStream.read()));
          continueValue = await continuePrompt(stack, true);
        }
      }
      if (continueValue === 'yes') {
        await triggerCloudBuilds({
          argv,
          buildNames: ['pulumi-up'],
          substitutions,
          dirs: [dirAbsolute],
          applicableStacks: [stack],
        });
      } else {
        continue;
      }
    }
  }
}

export async function initVenv() {
  const venvDir = `venv`;
  const venvEnv = {
    PATH: `${venvDir}/bin:${process.env.PATH}`,
    VIRTUAL_ENV: venvDir,
  };
  const pythonCommand = await getPythonCommand();

  const noVenv = !fs.existsSync(venvDir);
  let isIsOldVenv = false;
  if (!noVenv) {
    try {
      const venvVersion = fs.readFileSync(venvDir + '/.tw-version', 'utf-8');
      if (venvVersion !== packageJson.version) {
        cliLog(`Found venv from cli version: ${venvVersion}, updating...`);
        isIsOldVenv = true;
      } else {
        cliLog(`Found venv from cli version: ${venvVersion}`);
      }
    } catch (e) {
      isIsOldVenv = true;
    }
  }
  if (noVenv || isIsOldVenv) {
    await runProcess({
      command: pythonCommand,
      commandArgs: ['-m', 'venv', ...process.argv.slice(3), 'venv'],
      log: true,
    });
    fs.writeFileSync(venvDir + '/.tw-version', packageJson.version);
    await runProcess({
      command: process.env.SHELL || 'bash',
      commandArgs: [
        '-c',
        `pip install --index-url ${indexUrl} --upgrade pip && pip install --index-url ${indexUrl} keyring keyrings.google-artifactregistry-auth debugpy`,
      ],
      additionalArgs: {
        // @ts-ignore
        env: {
          ...venvEnv,
        },
      },
      log: true,
    });
  }

  if (process.env.TW_COMMAND === 'python:init-env') {
    cliSuccess('To enable the virtual environment run: source venv/bin/activate');
  }
}

export async function runRevert(argv) {
  let serviceId: string;

  if (argv.select || !argv.service) {
    serviceId = await selectServices(false);
  } else {
    serviceId = argv.service;
  }
  if (!serviceId) {
    cliExit('No service selected');
  }
  const service = await getServiceInfo(serviceId);

  let tag: string;
  if (argv.sha) {
    tag = argv.tag;
  } else {
    tag = await selectTagForRevert(serviceId);
  }

  const dir = path.join(service.servicePath, 'infra');

  const pulumiProject = (await loadPulumiProject(dir)).name;
  const repo = await getGitRepoName();
  const branch = await getCurrentBranch();
  const substitutions = {
    _SERVICE_ID: pulumiProject,
    _PULUMI_PROJECT: pulumiProject,
    _PATH: `services/${serviceId}/infra`,
    _BRANCH_NAME: 'master',
    _GITHUB_SHA: tag,
    _REPO: repo,
    _GITHUB_ORG: await getGithubOrg(),
  };

  cliLog(`Reverting ${serviceId} to commit ${tag}`);
  await confirm('OK to continue with revert?');
  await triggerCloudBuilds({
    argv,
    buildNames: ['pulumi-up'],
    substitutions,
    dirs: [dir],
    applicableStacks: ['shofifi'],
    envs: ['F=true'],
  });
}

export async function runProviderUpdate(argv) {
  let serviceName = argv.service;
  if (argv.select || !serviceName) {
    serviceName = await selectServices();
  }
  const service = await getServiceInfo(serviceName);
  const serviceConfig = loadServiceConfig(service.absolutePath);
  const providerConfig = serviceConfig.sensory.provider;
  const policiesConfig = serviceConfig.sensory.policies;
  if (!providerConfig) {
    cliExit('No sensory provider found in tw-config.json');
  }
  try {
    const res = await callServiceEndpoint(
      'integration',
      'providers',
      { ...providerConfig, policies: policiesConfig },
      {
        forceCloud: true,
        log: false,
      }
    );
    logger.info('Provider updated', res.data);
  } catch (e) {
    cliError(JSON.stringify(e.response?.data || ''));
  }
}

export async function buildService(argv) {
  const dir: string = argv.dir || argv._.slice(1)[0];
  const branch = await getCurrentBranch();
  if (!dir) {
    cliExit('No dir.');
  }
  const runtime = fs.existsSync(`${dir}/package.json`) ? 'node' : 'python';
  await triggerCloudBuilds({
    argv,
    buildNames: ['build-service'],
    substitutions: {
      _RUNTIME: runtime,
      _SERVICE_DIR: dir,
      _SERVICE_ID: dir.split('/').pop(),
      _BRANCH_NAME: branch,
      _REPO: await getGitRepoName(),
      _GITHUB_ORG: await getGithubOrg(),
