export function getLocalIp() {
  const nets = networkInterfaces();
  const results = Object.create(null); // Or just '{}', an empty object

  for (const name of Object.keys(nets)) {
    for (const net of nets[name]) {
      if (!name) continue;
      // Skip over non-IPv4 and internal (i.e. 127.0.0.1) addresses
      // 'IPv4' is in Node <= 17, from 18 it's a number 4 or 6
      const familyV4Value = typeof net.family === 'string' ? 'IPv4' : 4;
      if (net.family === familyV4Value && !net.internal) {
        if (!results[name]) {
          results[name] = [];
        }
        results[name].push(net.address);
      }
    }
  }
  return results['en0'][0];
}

export async function pullFromPubsub(argv) {
  const pubsub = new PubSub();
  const author = getGcloudUserEmail().replace('@', '-at-').replace('.', '-dot-');
  const topicName = argv.topic;
  const subscriptionName = `${topicName}-${author}`;
  const topic = pubsub.topic(topicName);
  const [subscriptions] = await topic.getSubscriptions();
  const log = !Boolean(argv.k);
  const exists = subscriptions.find((subscription) =>
    subscription.name.endsWith(subscriptionName)
  )?.name;
  if (exists) {
    log && cliSuccess(`Using subscription ${subscriptionName}`);
  } else {
    log && cliLog(`Creating subscription...`);
    const [sub] = await topic.createSubscription(subscriptionName, {
      ackDeadlineSeconds: 600,
      expirationPolicy: {
        ttl: { seconds: 3600 * 24 * 1 },
      },
      messageRetentionDuration: {
        seconds: 3600,
      },
    });
    log && cliSuccess(`Created subscription ${subscriptionName}`);
  }
  if (argv.k) {
    console.log(subscriptionName);
    return;
  }
  const serviceId = argv.service as string;
  const endpoint = argv.endpoint as string;

  await pullPubSubMessages(
    subscriptionName,
    async (message) => {
      cliLog(`got message ${message.id}`);
      if (serviceId && endpoint) {
        try {
          await callServiceEndpoint<any[], any>(
            serviceId,
            endpoint + (endpoint.includes('?') ? '&' : '?') + 'isPubsub=true',
            {
              message: {
                data: Buffer.from(message.data).toString('base64'),
                attributes: message.attributes,
                messageId: message.id,
              },
            },
            {
              method: 'POST',
            }
          );
        } catch (e) {
          cliError(`Error: ${e}`);
          message.nack();
        }
      } else if (argv.l) {
        await callPubSub(argv.t, JSON.parse(message.data.toString()), message.attributes);
      } else {
        console.log(Buffer.from(message.data).toString());
      }
      message.ack();
    },
    {
      forceCloud: true,
      subscriptionOptions: { flowControl: { maxMessages: 1 } },
      projectId: cliConfig.projectId,
    }
  );
}

export async function modifyPulumiStack(argv) {
  const { stdout } = await runProcess({
    command: 'pulumi',
    commandArgs: ['stack', 'export'],
  });
  // get stack name
  let { stdout: stackName } = await runProcess({
    command: 'pulumi',
    commandArgs: ['stack', '--show-name'],
  });
  stackName = stackName.trim();
  const state = JSON.parse(stdout.toString());
  fs.writeFileSync(`${stackName}-bac.json`, JSON.stringify(state));
  if (argv['provider']) {
    const resources: any[] = [];
    const changedResources = [];
    const { stdout } = await runProcess({
      command: 'pulumi',
      commandArgs: ['preview', '--json'],
    });
    const diff = JSON.parse(stdout.toString());
    fs.writeFileSync(`${stackName}-diff.json`, JSON.stringify(diff));
    const providerReplaces: { urn: string; newProvider: string }[] = [];
    for (const step of diff.steps) {
      if (step.op === 'replace' && step.replaceReasons?.includes('provider')) {
        providerReplaces.push({ urn: step.urn, newProvider: step.newState.provider });
      }
    }
    for (const resource of state.deployment.resources) {
      const replacement = providerReplaces.find((r) => r.urn === resource.urn);
      if (replacement) {
        resource.provider = replacement.newProvider;
        changedResources.push(resource);
      } else {
        resources.push(resource);
      }
    }
    if (changedResources.length) {
      // insert the resource one after its provider
      for (const resource of changedResources) {
        const providerUrn = resource.provider.split('::').slice(0, 4).join('::');
        const providerId = resource.provider.split('::').pop();
        const providerIndex = resources.findIndex((r) => {
          return r.urn === providerUrn && r.id === providerId;
        });
        resources.splice(providerIndex + 1, 0, resource);
      }
    }
    state.deployment.resources = resources;
  } else {
    const resources: any[] = [];
    for (const resource of state.deployment.resources) {
      if (resource.pendingReplacement) {
        resource.pendingReplacement = false;
      }
      resources.push(resource);
    }
    state.deployment.resources = resources;
  }
  fs.writeFileSync(`${stackName}-new.json`, JSON.stringify(state));
  console.log(JSON.stringify(state));
}

