export async function confirm(message?: string): Promise<void> {
  try {
    const confirm = new Confirm({ message: message || 'Are you sure?' });
    const result = await confirm.run();
    if (!result) {
      cliLog('Aborting');
      exit(0);
    }
  } catch (e) {
    exit(0);
  }
}

export async function selectGeneric(args: {
  multi: boolean;
  message: string;
  choices: string[] | (() => Promise<string[]>);
}) {
  const { multi, message } = args;
  let { choices } = args;
  if (typeof choices === 'function') {
    choices = await choices();
  }
  const prompt = new (multi ? MultiSelect : Select)({
    name: 'value',
    message,
    choices: choices.map((v) => {
      return { name: v, value: v };
    }),
  });
  try {
    const results = await prompt.run();
    return results;
  } catch (e) {
    exit();
  }
}

