export async function getApplicableStacks(argv: any, dirs: string[]) {
  const branch = await getCurrentBranch();
  const stacks = getPulumiStacks(dirs);
  let applicableStacks = Object.keys(stacks);

  if (argv.stack) {
    applicableStacks = applicableStacks.filter((v) => v === argv.stack);
  }

  if (argv.project) {
    applicableStacks = applicableStacks.filter(
      (stackName) => stacks[stackName].config[STACK_PROJECT_KEY] === argv.project
    );
  }

  if (branch !== 'master') {
    applicableStacks = applicableStacks.filter((stackName) => stackName !== 'shofifi');
  }

  if (applicableStacks.length > 1) {
    applicableStacks = await selectStacks(applicableStacks);
  }

  if (!applicableStacks.length) {
    cliExit('No stacks selected.');
  }
  return applicableStacks;
}

export async function handleDockerDeps(serviceDependencies: string[], debug: string[] = []) {
  ensureDockerAuth();
  for (const dep of serviceDependencies) {
    const depConf = dockers[dep];
    if (!depConf) {
      cliExit(`Invalid dependency: ${dep}, try updating the cli.`);
    }
    const conf = depConf.config(process.env.PROJECT_ID);
    const dockerOptions: IDockerComposeOptions = {
      env: { ...getEnvs() },
      configAsString: conf,
      callback: (chunk, _) => {
        serviceLogStream(chunk, dep + ' (docker)', depConf.color || '42db27');
      },
      composeOptions: ['-p', 'tw-cli'],
    };
    try {
      if (depConf.keepFresh) {
        await execCompose('pull', [], dockerOptions);
      }
      cliLog(`starting ${dep}`);
      if (!debug.includes(dep)) delete dockerOptions.callback;
      execCompose(
        'up',
        ['--no-log-prefix', ...(depConf.detach ? ['-d'] : [])],
        dockerOptions
      ).catch((e) => {
        cliExit(e.err);
      });
    } catch (e) {
      cliExit(e.err);
    }
  }
}

export function getEnvs() {
  const result = { ...process.env };
  delete result['TW_DD'];
  return result;
}

export function cliExit(msg?: string): never {
  if (msg) cliError(msg);
  process.exit(1);
}

export async function getPackagesRoot() {
  if (fs.existsSync('packages')) return path.resolve('packages');
  const root = await getGitRoot();
  if ((await getGitUrl()) === packagesGitUrl) {
    return root;
  }
  const possiblePaths = [
    path.resolve(root, 'packages'), // if we're in triplewhale/backend/
    path.resolve(root, '..', 'packages'), // if we're in triplewhale/anything
    path.resolve(root, '..', 'backend', 'packages'), // if we're in triplewhale/client
  ];
  for (let p of possiblePaths) if (fs.existsSync(p)) return p;
}

export function getPackageDir(packageName) {
  const standard = `${cliConfig.packagesRoot}/${packageName}`;
  if (fs.existsSync(`${standard}/packages.json`)) return standard;
  const pathPassed = path.resolve(packageName);
  if (fs.existsSync(`${pathPassed}/packages.json`)) return pathPassed;
}

export async function getPackageInfo(pkg: string): Promise<{
  packageName: string;
  packagePath: string;
  absolutePath: string;
  runtime: 'node' | 'python';
}> {
  let packagePath;
  let packageName;
  let runtime;
  let absolutePath;
  const relativePath = (await getPathRelativeToGit()).slice(0, -1);

  if (pkg === '.') {
    // remove the trailing slash
    packagePath = relativePath;
  } else {
    packagePath = relativePath ? `${relativePath}/${pkg}` : pkg;
  }
  absolutePath = path.resolve(pkg);

  if (fs.existsSync(`${absolutePath}/package.json`)) {
    runtime = 'node';
    packageName = JSON.parse(fs.readFileSync(`${absolutePath}/package.json`).toString()).name;
  } else if (fs.existsSync(`${absolutePath}/setup.py`)) {
    runtime = 'python';
    const setupPy = fs.readFileSync(`${absolutePath}/setup.py`, 'utf8');
    // setup.py:
    // name = "tw_utils"
    packageName = setupPy.match(/name\s*=\s*['"](.*)['"]/)[1];
  } else {
    cliExit(`Could not find package.json or requirements.txt in ${pkg}`);
  }

  return { packageName, packagePath, absolutePath, runtime };
}

export async function getGitRoot() {
  const pathRelativeToGit = await getPathRelativeToGit();
  let relPath = '';
  pathRelativeToGit
    .split('/')
    .filter(Boolean)
    .map((_) => (relPath += '../'));
  const root = path.resolve(relPath);
  return root;
}

export async function getServicesRoot() {
  const gitRoot = await getGitRoot();
  const servicesRoot = gitRoot + '/services';
  return servicesRoot;
}

export async function getPipelinesRoot() {
  const gitRoot = await getGitRoot();
  const pipelinesRoot = gitRoot + '/pipelines';
  return pipelinesRoot;
}

export function serviceExits(servicePath: string) {
  return fs.existsSync(`${servicePath}/tw-config.json`);
}

export function pulumiProjectExists(dir: string) {
  return fs.existsSync(`${dir}/Pulumi.yaml`);
}

export function getServiceDir(serviceName: string) {
  const standard = `${cliConfig.servicesRoot}/${serviceName}`;
  if (fs.existsSync(`${standard}/${twConfigFile}`)) return standard;
  const pathPassed = path.resolve(serviceName);
  if (fs.existsSync(`${pathPassed}/${twConfigFile}`)) return pathPassed;
}

export function getServiceName(serviceName: string) {
  if (serviceName === '.') return path.resolve(serviceName).split('/').pop();
  if (serviceName.includes('/')) return serviceName.split('/').pop();
  return serviceName;
}

export async function getServiceInfo(
  srv: string,
  isSaber: boolean = false
): Promise<{
  serviceName: string;
  servicePath: string;
  absolutePath: string;
  runtime: 'node' | 'python';
}> {
  let serviceName;
  let servicePath;
  let absolutePath;
  let runtime;

  const root = isSaber ? cliConfig.pipelinesRoot : cliConfig.servicesRoot;

  // remove the trailing slash
  const relativePath = (await getPathRelativeToGit()).slice(0, -1);
  const isPath = fs.existsSync(`${srv}/${twConfigFile}`);

  if (isPath) {
    absolutePath = path.resolve(srv);
    if (srv === '.') {
      servicePath = relativePath;
    } else {
      if (relativePath) {
        servicePath = `${relativePath}/${srv}`;
      } else {
        servicePath = srv;
      }
    }
  } else {
    serviceName = srv;
    absolutePath = `${root}/${srv}`;
    servicePath = `${root}/${srv}`;
  }
  if (!absolutePath) {
    absolutePath = servicePath;
  }

  if (fs.existsSync(`${absolutePath}/${twConfigFile}`)) {
    serviceName = loadServiceConfig(absolutePath).env.SERVICE_ID;
  } else {
    cliExit(`Could not find tw-config.json in ${absolutePath} for service ${srv}`);
  }

  if (fs.existsSync(`${absolutePath}/package.json`)) {
    runtime = 'node';
  } else if (fs.existsSync(`${absolutePath}/requirements.txt`)) {
    runtime = 'python';
  }

  return { serviceName, servicePath, absolutePath, runtime };
}

export function guessClientRootSync() {
  const possiblePaths = [
    path.resolve('client'), // if we're in /triplewhale
    path.resolve('..', 'client'), // if we're in triplewhale/backend/ or /triplewhale/client
    path.resolve('..', '..', 'client'), // if we're in triplewhale/backend/packages
  ];
  for (let p of possiblePaths) if (fs.existsSync(p)) return p;
}

export function guessServicesRootSync() {
  const possiblePaths = [
    path.resolve('services'), // if we're in /triplewhale
    path.resolve('..', 'services'), // if we're in triplewhale/backend/ or /triplewhale/client
    path.resolve('..', '..', 'services'), // if we're in triplewhale/backend/packages
  ];
  for (let p of possiblePaths) if (fs.existsSync(p)) return p;
}

export function guessPackagesRootSync() {
  const possiblePaths = [
    path.resolve('packages'), // if we're in /triplewhale
    path.resolve('..', 'packages'), // if we're in triplewhale/backend/ or /triplewhale/client
    path.resolve('..', '..', 'packages'), // if we're in triplewhale/backend/packages
  ];
  for (let p of possiblePaths) if (fs.existsSync(p)) return p;
}

export async function getIdentityToken() {
  if (idTokenCache) return idTokenCache;
  const token = (
    await runProcess({
      log: false,
      command: 'gcloud',
      commandArgs: ['auth', 'print-identity-token'],
    })
  ).stdout.trim();
  if (!token) {
    throw Error("Can't get token from gcloud!");
  }
  idTokenCache = token;
  return token;
}

export async function getAccessToken() {
  if (accessTokenCache) return accessTokenCache;
  const token = (
    await runProcess({
      log: false,
      command: 'gcloud',
      commandArgs: ['auth', 'print-access-token'],
    })
  ).stdout.trim();
  if (!token) {
    throw Error("Can't get token from gcloud!");
  }
  accessTokenCache = token;
  return token;
}

export async function getAllPackages() {
  const packagesRoot = cliConfig.packagesRoot;
  return fs
    .readdirSync(packagesRoot)
    .filter((folder) => fs.existsSync(`${packagesRoot}/${folder}/package.json`));
}

export async function getAllPipelines() {
  return fs
    .readdirSync(cliConfig.pipelinesRoot)
    .filter((service) => serviceExits(`${cliConfig.pipelinesRoot}/${service}`));
}

export async function getAllPulumiProjects(): Promise<string[]> {
  const projectsAbsolutePaths = await getAllPulumiProjectsRecurse(cliConfig.gitRoot, 3);
  const projectsRelativePaths = projectsAbsolutePaths.map((project) =>
    path.relative(cliConfig.gitRoot, project)
  );
  return projectsRelativePaths;
}

export async function getAllServices(): Promise<ServicesIds[]> {
  const services: unknown = fs
    .readdirSync(cliConfig.servicesRoot)
    .filter((service) => serviceExits(`${cliConfig.servicesRoot}/${service}`));
  return services as ServicesIds[];
}

export async function getServiceTags(serviceId: string): Promise<
  {
    commitSha: string;
    shortSha: string;
    created: string;
  }[]
> {
  try {
    if (!client) {
      client = new ar.ArtifactRegistryClient();
      await client.initialize();
    }
    // TODO - make it available in staging too??
    const versionResponse = await client.listVersions({
      parent: `projects/shofifi/locations/us-central1/repositories/cloud-run/packages/${serviceId}`,
      view: 'FULL',
    });
    return formatList(versionResponse[0]);
  } catch (e) {
    return [];
  }
}

export async function getCurrentBranch() {
  const branch = (
    await runProcess({
      log: false,
      command: 'git',
      commandArgs: ['rev-parse', '--abbrev-ref', 'HEAD'],
    })
  ).stdout.trim();
  return branch;
}

export async function getPathRelativeToGit() {
  const path = (
    await runProcess({
      log: false,
      command: 'git',
      commandArgs: ['rev-parse', '--show-prefix'],
    })
  ).stdout.trim();
  return path;
}

export async function gitPull() {
  await runProcess({
    log: false,
    command: 'git',
    commandArgs: ['pull'],
  });
}

export async function getRepoName() {
  const repoDir = (
    await runProcess({
      log: false,
      command: 'git',
      commandArgs: ['rev-parse', '--show-toplevel'],
    })
  ).stdout.trim();
  return path.basename(repoDir);
}

export async function runAndBuildPackages(packages) {
  for (const pkg of packages) {
    const packageJson = await loadPackageJson({ packagePath: pkg });
    const name = packageJson.name + ' (package)';
    runProcess({
      name,
      command: 'tw',
      commandArgs: ['i', '--preserve'],
      color: packageJson.tw.color,
      log: true,
      additionalArgs: { cwd: pkg },
    }).then(() => {
      runProcess({
        name,
        command: 'npm',
        commandArgs: ['run', 'dev'],
        color: packageJson.tw.color,
        log: true,
        additionalArgs: { cwd: pkg },
      });
    });
  }
}

export async function linkPackage(args: {
  packages: string[];
  dir: string;
  color?: string;
  force?: boolean;
}) {
  const { packages, dir, force, color } = args;

  const globalLinks = await getGlobalLinks();
  for (const packageName of packages) {
    if (!globalLinks.includes(`@tw/${packageName}`)) {
      await createGlobalLink(packageName);
    }
  }
  const commandArgs = ['link', ...packages.map((pack) => '@tw/' + pack)];
  if (force) commandArgs.push('-f');
  await runProcess({
    command: 'npm',
    commandArgs,
    color: color,
    additionalArgs: { cwd: resolve(dir) },
    log: true,
  });
}

