export function runNodemonFromArgv(argv) {
  const args = JSON.parse(argv._[1]);
  runNodemonProcess(args);
}

export async function npmVersion(
  packagePath: string,
  type: 'patch' | 'prerelease',
  branch?: string
) {
  const packageJson = await loadPackageJson({ packagePath });
  if (!packageJson.name.startsWith('@tw/')) {
    cliExit(`Package name must start with @tw/ but got ${packageJson.name}`);
  }
  const semver = new SemVer(packageJson.version);
  let version;
  if (type === 'prerelease') {
    const sha = await getGitSha();
    version = semver.inc(type, `${branch}-${sha}`);
  } else {
    version = semver.inc(type);
  }
  return version.version.replaceAll('_', '-');
}

export async function runNpmAuth(argv?) {
  const force = argv?.f;
  const expire = moment(configStore.get('npmAuth') || new Date());
  const now = moment();
  if (expire <= now.add(10, 'minutes') || force) {
    try {
      await runProcess({
        name: 'general',
        command: 'npx',
        commandArgs: ['google-artifactregistry-auth', '--repo-config', globalNpmrcFile],
        color: 'FA910D',
        log: true,
      });
      configStore.set('npmAuth', now.add(1, 'hours'));
    } catch (e) {
      await runProcess({
        command: 'npm',
        commandArgs: [
          'config',
          'set',
          '@tw:registry',
          'https://us-central1-npm.pkg.dev/shofifi/npm-packages/',
        ],
      }).catch(console.error);
      cliError('Failed to refresh auth, please trying again.');
      await runNpmAuth(argv);
    }
  } else {
    cliLog('No need to refresh auth.');
  }
}

export async function pipVersion(currentVersion: string, type: 'patch' | 'prerelease') {
  const semver = new SemVer(currentVersion);
  let version;
  let versionSuffix: string;
  if (type === 'prerelease') {
    const branch = await getCurrentBranch();
    const sha = (await getGitSha()).slice(0, 7);
    versionSuffix = `${branch}.${sha}`.replaceAll('-', '.');
    version = semver.inc(type, versionSuffix);
  } else {
    version = semver.inc(type);
  }
  if (versionSuffix) {
    versionSuffix = `+${versionSuffix}`;
    version.version = version.version.slice(0, -2);
  }
  return { version: version.version, versionSuffix };
}

export function getPulumiStacks(dirs: string[]): Record<string, any> {
  const result = {};
  if (!dirs?.length) {
    for (const projectId of projectIds) {
      result[projectId] = { config: { [STACK_PROJECT_KEY]: projectId } };
    }
    return result;
  }
  const allStacks = [];
  for (const dir of dirs) {
    const stackFileNames = fs
      .readdirSync(dir)
      .filter((v) => v.endsWith('.yaml') && v.startsWith('Pulumi.') && v !== 'Pulumi.yaml');
    allStacks.push(...stackFileNames);
  }
  const stackFileNames = [...new Set(allStacks)].filter(
    (v) => allStacks.filter((a) => a === v).length === dirs.length
  );
  for (const stackFileName of stackFileNames) {
    const stackName = stackFileName.split('.')[1];
    result[stackName] = yaml.parse(fs.readFileSync(`${dirs[0]}/${stackFileName}`).toString());
  }
  return result;
}

export async function selectStacks(stacks: string[]) {
  const stacksPrompt = new MultiSelect({
    name: 'value',
    message: 'Choose the Stacks',
    choices: stacks.map((projectId) => {
      return { name: projectId, value: projectId };
    }),
  });
  try {
    const stacksRes = await stacksPrompt.run();
    return stacksRes;
  } catch (e) {
    exit();
  }
}

export async function selectStack(stacks: string[]) {
  const stacksPrompt = new Select({
    name: 'value',
    message: 'Choose the Stack',
    choices: stacks.map((projectId) => {
      return { name: projectId, value: projectId };
    }),
  });
  try {
    const stacksRes = await stacksPrompt.run();
    return stacksRes;
  } catch (e) {
    exit();
  }
}

export async function loadPulumiProject(dir: string): Promise<{
  name: string;
  runtime: 'nodejs';
  description: string;
}> {
  const yamlContents = yaml.parse(fs.readFileSync(`${dir}/Pulumi.yaml`).toString());
  return yamlContents;
}

export async function getPythonCommand() {
  if (python) {
    return python;
  }
  if (commandExistsSync('python')) {
    python = 'python';
  } else if (commandExistsSync('python3.10')) {
    // 11 is not widely supported yet
    python = 'python3.10';
  } else if (commandExistsSync('python3')) {
    python = 'python3';
  } else {
    cliExit('Could not find python or python3, trying to install python3.');
  }
  return python;
}

export async function getPipCommand() {
  if (pip) {
    return pip;
  }
  if (commandExistsSync('pip')) {
    pip = 'pip';
  } else if (commandExistsSync('pip3')) {
    pip = 'pip3';
  } else {
    cliExit('Could not find pip or pip3, trying to install pip3.');
  }
  return pip;
}

export async function runNpmTest(
  args: {
    name: string;
    absolutePath: string;
  },
  argv: any
) {
  const { name, absolutePath } = args;

  let serviceEnv = {};
  try {
    const serviceConfig = loadServiceConfig(absolutePath);
    serviceEnv = serviceConfig.env || {};
  } catch (e) {}

  return new Promise<void>((resolve, reject) => {
    const proc = spawn(
      'npm',
      ['test', '--if-present', '--prefix', absolutePath, '--', '--silent'],
      {
        stdio: 'inherit',
        env: {
          ...process.env,
          ...serviceEnv,
        },
      }
    );

    proc.on('close', (code) => {
      if (code === 0) {
        resolve();
      } else {
        reject(new Error(`Tests for ${args.name} failed`));
      }
    });
  });
}

export function runProcess(args: {
  name?: string;
  command: string;
  commandArgs?: string[];
  color?: string;
  additionalArgs?: SpawnOptions;
  log?: boolean;
}): Promise<{ stdout: string; stderr: string }> {
  const { name, command, commandArgs, color, additionalArgs, log } = args;
  let onData;
  let onClose;
  if (log) {
    onData = (data) => serviceLogStream(data, name, color);
    onClose = (code) => serviceLogStream(`finished with code: ${code}`, name, color);
  }
  return runCommand({ command, commandArgs, additionalArgs, log, onData, onClose });
}

