export default function setServiceAccount() {
  process.env.GOOGLE_APPLICATION_CREDENTIALS = path.resolve(
    `service-account-${process.env.PROJECT_ID}.json`
  );
}

export async function getPipelineId(argv: any) {
  let pipelineIds: string[] = argv.services[0] || argv._.slice(1)[0];

  if (argv.select || !pipelineIds.length) {
    pipelineIds = await selectPipelines(false);
  }
  return [pipelineIds];
}

export async function getServiceIds(argv: any) {
  let serviceIds: string[] = argv.services;

  if (argv.tag) {
    serviceIds = serviceIds.concat(await getServicesFromTags(argv.tag));
    serviceIds = [...new Set(serviceIds)];
  }
  if (argv.select || !serviceIds.length) {
    serviceIds = await selectServices();
  }
  return serviceIds;
}

export async function checkEmulators(args: { projectId: string }) {
  let resourcesCreated = false;
  let times = 0;

  try {
    const res = await axios.get('http://0.0.0.0:8027/health');
    resourcesCreated = res.data.resourcesCreated;
    cliConfig.emulatorsReady = true;
    if (resourcesCreated) {
      cliSuccess('Emulators Ready!');
    }
  } catch {}
  while (!cliConfig.emulatorsReady && cliConfig.proxyPort) {
    times++;
    cliWarning('Emulators Not Ready Yet.');
    await sleep(4000);
  }

  if (!resourcesCreated) {
    type EmulatorResources = {
      topics: any[];
      subscriptions: any[];
      buckets: any[];
      queues: any[];
    };
    let resources: any;
    try {
      const stagingResources: EmulatorResources = await downloadFile(
        `devops-triple-whale-staging`,
        'emulators/cloud-resources.json',
        {
          forceCloud: true,
        }
      );
      const prodResources: EmulatorResources = await downloadFile(
        `devops-shofifi`,
        'emulators/cloud-resources.json',
        {
          forceCloud: true,
        }
      );
      resources = {
        'triple-whale-staging': stagingResources,
        shofifi: prodResources,
      };
    } catch {}
    const maxTries = 5;
    let tries = 0;
    let loadedResources = false;
    while (resources && !loadedResources && tries++ < maxTries) {
      try {
        await axios.post(`http://0.0.0.0:8027/start-emulators`, resources, {
          headers: {
            'Content-Type': 'application/json',
          },
        });
        loadedResources = true;
      } catch {
        await sleep(2000);
      }
    }
    if (!loadedResources) {
      cliError('Failed to load emulator resources');
    }
  }
}

export async function checkTemporal() {
  let times = 0;
  const connectionOptions = {
    address: '0.0.0.0:7233',
    connectTimeout: 1000,
  };
  while (!cliConfig.temporalReady) {
    try {
      await Connection.connect(connectionOptions);
      cliSuccess('Temporal Ready');
      cliConfig.temporalReady = true;
      break;
    } catch {}
    times++;
    cliWarning('Temporal Not Ready Yet.');
    await sleep(4000);
    if (times > 15) {
      break;
    }
  }
}

export async function testVpn(baseUrl: string): Promise<{ ok: boolean; old?: boolean }> {
  const promise = new Promise<{ ok: boolean; old?: boolean }>((resolve, reject) => {
    try {
      const addr = baseUrl.split('//')[1];
      if (checked[addr] !== undefined) {
        return resolve({ ok: checked[addr], old: true });
      }
      const socket = net.createConnection(80, addr, () => {
        clearTimeout(timeout);
        socket.destroy();
        checked[addr] = true;
        resolve({ ok: true });
      });
      socket.on('error', () => {
        socket.destroy();
        checked[addr] = false;
        resolve({ ok: false });
      });
      const timeout = setTimeout(() => {
        socket.destroy();
        resolve({ ok: false });
      }, 10000);
    } catch {
      reject({ ok: false });
    }
  });
  return promise;
}

export function vpnErrorLog() {
  cliError('You are trying to proxy to a internal service, but you are not connected to the VPN.');
  cliError(`Please connect to the VPN on project ${process.env.PROJECT_ID} and try again.`);
}

export function serviceLogStream(stream, service?, color?) {
  const str = stream.toString();
  const lines = str.split('\n');
  lines.forEach((line: any) => {
    let message = line;
    if (!line) return;
    if (service) {
      message = `${chalk.hex(color || '5032a8')(service)} | ` + message;
    }
    console.log(message);
  });
}

export function loadServiceConfig(serviceDir: string): ServiceConfig {
  const data = fs.readFileSync(`${serviceDir}/tw-config.json`);
  return JSON.parse(data.toString());
}

export async function loadPackageJson(args: { packagePath?: string }) {
  const { packagePath } = args;
  const data = fs.readFileSync(`${packagePath}/package.json`);
  return JSON.parse(data.toString());
}

export function getDefaultNodemonConfig(entrypoint: string = 'dist/app.js') {
  const command = `npm run build && node --expose-gc --inspect=0.0.0.0:$\{DEBUG_PORT\} ${entrypoint}`;
  return {
    watch: ['src/'],
    execMap: {
      js: command,
      ts: command,
    },
    ext: 'js,ts',
    delay: 2,
  };
}

export async function getSecrets(serviceId: string, projectId?: string): Promise<string> {
  const project = projectId || process.env.PROJECT_ID;
  try {
    const client = new SecretManagerServiceClient();
    const [accessResponse] = await client.accessSecretVersion({
      name: `projects/${project}/secrets/${serviceId}-env/versions/latest`,
    });
    const responsePayload = accessResponse.payload.data.toString();
    return responsePayload;
  } catch (e) {
    if (e.code === 7) {
      if (project === 'shofifi') {
        cliWarning(
          `Could not get secrets for ${serviceId} on ${project}, trying to get from triple-whale-staging.`
        );
        return await getSecrets(serviceId, 'triple-whale-staging');
      }
      cliExit(`You do not have access to Secret Manger for ${serviceId} on project ${project}!`);
    }
    cliWarning('no secrets for ' + serviceId);
    return '';
  }
}

export function getUserEnv(serviceDir) {
  if (fs.existsSync(`${serviceDir}/.env`)) {
    const envData = fs.readFileSync(`${serviceDir}/.env`);
    return envToObject(envData.toString());
  }
}

export function getLinkedPackages(serviceDir) {
  const twDepsDir = `${serviceDir}/node_modules/@tw`;
  if (!fs.existsSync(twDepsDir)) return [];
  const packages = fs.readdirSync(twDepsDir);
  return packages
    .filter((packagePath) => fs.lstatSync(`${twDepsDir}/${packagePath}`).isSymbolicLink())
    .map((pkg) => path.resolve(twDepsDir, fs.readlinkSync(`${twDepsDir}/${pkg}`)));
}

export function envToObject(envString) {
  let result = {};
  envString.split('\n').map((line) => {
    const [key, value] = line.split('=', 2);
    if (key && value) {
      result[key] = value;
    }
  });
  return result;
}

export function objectToEnv(envObject) {
  const result = [];
  Object.keys(envObject).map((key) => {
    result.push(`${key}=${envObject[key]}`);
  });
  return result;
}

