/**
 * @description Type that represents POJO - Plain Old Javascript Object.
 * Takes a generic T, depending on the level of type safety desired.
 */
export type PlainObject<T = unknown> = Record<string, T>;

export const computeFeatureFlagValue = (
  featureFlagId: FeatureFlag,
  ffMetadata: Partial<FeatureFlagMetaData>,
  ffPackagesConfig: FeatureFlagPackagesConfig,
  ffPackagesMetadata: PackageMetaData[]
): NullableFeatureFlagValueWithMetaData => {
  const metadata = ffMetadata[featureFlagId];
  if (!metadata) {
    return {
      value: null,
      metadata: {},
    };
  }

  const comboConfig = comboMethodConfig[metadata.combinationMethod];
  const packageMetaDataMap = ffPackagesMetadata.reduce(
    (acc, pm) => ((acc[pm.id] = pm), acc),
    {} as PackageMetaDataMap
  );

  let highestConfigRank = 0,
    ffConfigValues: FeatureFlagConfigValue[] = [],
    highestSources = new Set<FeatureFlagValueSource>();

  for (const plan in ffPackagesConfig) {
    const planKey = plan as FeatureFlagConfigKey;
    const planConfig = ffPackagesConfig[planKey];

    // only deal with configs that have a value for this feature flag
    const ffConfig = planConfig[featureFlagId];
    if (!ffConfig) continue;

    const { rank = 0, sources = [] } = packageMetaDataMap[planKey] || {};
    if (highestConfigRank && rank < highestConfigRank) continue;

    if (rank === highestConfigRank) {
      ffConfigValues.push(ffConfig.value);
      sources.forEach((source) => highestSources.add(source));
      continue;
    }

    highestConfigRank = rank;
    highestSources = new Set(sources);
    ffConfigValues = [ffConfig.value];
  }

  const value = !ffConfigValues.length
    ? metadata.defaultValue ?? comboConfig.defaultValue
    : comboConfig.combinationFunction(ffConfigValues);

  return {
    value,
    metadata: {
      sources: [...highestSources],
    },
  };
};

export const computePackageConfigKeys = (
  ffConfigKeys: (string | null)[]
): FeatureFlagConfigKey[] => {
  return ffConfigKeys
    .filter(
      (key): key is FeatureFlagConfigKey =>
        !!key?.startsWith(CONFIG_KEY_PREFIX)
    )
    .sort((a, b) => {
      if (!(a in PACKAGE_CONFIG_RANKS)) return -1;
      if (!(b in PACKAGE_CONFIG_RANKS)) return -1;
      return PACKAGE_CONFIG_RANKS[a] - PACKAGE_CONFIG_RANKS[b];
    });
};

export const computePackagesConfig = (
  ffAllPackagesConfig: FeatureFlagPackagesConfig,
  subFeatureKeys: (string | null)[]
): FeatureFlagPackagesConfig => {
  const packageConfigKeys = computePackageConfigKeys(subFeatureKeys);

  const keys = Object.keys(
    ffAllPackagesConfig
  ) as FeatureFlagConfigKey[];

  return keys.reduce((acc, key) => {
    if (!packageConfigKeys.includes(key)) return acc;
    acc[key] = ffAllPackagesConfig[key];
    return acc;
  }, {} as FeatureFlagPackagesConfig);
};

export function assertHasKeys<T extends object, K extends string>(
  objName: string,
  obj: T,
  fields: K[]
): asserts obj is T & { [P in K]: P extends keyof T ? T[P] : unknown } {
  const missingKeys: string[] = [];

  for (const field of fields) {
    if (!(field in obj)) {
      missingKeys.push(field);
    }
  }

  if (missingKeys.length) {
    throw new Error(`Invalid ${objName} - missing fields: "${missingKeys.join()}"`);
  }
}

export function assertValidBlockingMethod(
  method: unknown
): asserts method is FeatureFlagBlockingMethod {
  if (typeof method !== "string") {
    throw new TypeError("Invalid FeatureFlagBlockingMethod - must be a string");
  }

  if (!(method in FeatureFlagBlockingMethod)) {
    throw new TypeError(
      `Invalid FeatureFlagBlockingMethod - ${method} isn't a FeatureFlagBlockingMethod`
    );
  }
}

export function assertValidCombinationMethod(
  comboMethod: unknown
): asserts comboMethod is FeatureFlagCombinationMethod {
  if (
    typeof comboMethod !== "string" ||
    comboMethod in FeatureFlagCombinationMethod === false
  ) {
    throw new TypeError(
      `Invalid FeatureFlagCombinationMethod - ${comboMethod} isn't a valid FeatureFlagCombinationMethod`
    );
  }
}

export function assertValidConfigValue(value: unknown): asserts value is FeatureFlagConfigValue {
  if (typeof value === 'number') return;
  if (typeof value === 'boolean') return;
  if (typeof value === 'object' && Array.isArray(value)) {
    if (value.every((val) => typeof val === 'string')) return;
    if (value.every((val) => isFeatureFlagRankedControlListValue(val))) return;
  }

  throw new TypeError(
    `Invalid FeatureFlagConfigValue - ${value} isn't a valid FeatureFlagConfigValue`
  );
}

export function assertValidFeatureFlag(ff: unknown): asserts ff is FeatureFlag {
  if (typeof ff === "string" && ff in FeatureFlag) return;
  throw new TypeError(`Invalid FeatureFlag - ${ff} isn't a valid FeatureFlag`);
}

export function assertValidFeatureFlagConfigKey(
  key: unknown
): asserts key is FeatureFlagConfigKey {
  if (typeof key !== "string" || !key.startsWith(CONFIG_KEY_PREFIX)) {
    throw new TypeError(
      `Invalid FeatureFlagConfigKey - ${key} isn't a valid FeatureFlagConfigKey`
    );
  }
}

export function assertValidFeatureFlagValueSource(
  source: unknown
): asserts source is FeatureFlagValueSource {
  if (typeof source !== 'string') {
    throw new TypeError(`Invalid FeatureFlagValueSource: ${source} isn't a string.`);
  }

  // Just using the "as any" to make ts quiet.  We have to check source this way.
  if (!featureFlagValueSources.has(source as any)) {
    throw new TypeError(`Invalid FeatureFlagValueSource: ${source} isn't in the predefined list.`);
  }
}

export function assertValidFeatureFlagValueType(
  valType: unknown
): asserts valType is FeatureFlagValueType {
  if (
    typeof valType !== "string" ||
    valType in FeatureFlagValueType === false
  ) {
    throw new TypeError(
      `Invalid FeatureFlagValueType - ${valType} isn't a valid FeatureFlagValueType`
    );
  }
}

export function assertValidFeatureFlagValueWithMetaData<M extends Mode>(
  obj: unknown,
  mode: M
): asserts obj is M extends 'complete'
  ? FeatureFlagValueWithMetaData
  : FeatureFlagValueOrNullWithMetaData {
  assertValidPOJO(obj, 'FeatureFlagValueWithMetaData');
  assertHasKeys('FeatureFlagValueWithMetaData', obj, ['value', 'metadata']);

  if (!(mode === 'partial' && obj.value === null)) {
    assertValidConfigValue(obj.value);
  }

  assertValidPOJO(obj.metadata, 'FeatureFlagValueWithMetaData[metadata]');

  if ('sources' in obj.metadata) {
    if (!Array.isArray(obj.metadata.sources)) {
      throw new TypeError(
        'Invalid FeatureFlagValueWithMetaData[metadata][sources]:>> "sources" must be an array'
      );
    }

    for (const source of obj.metadata.sources) {
      assertValidFeatureFlagValueSource(source);
    }
  }
}

export function assertValidMetadata(
  metadata: unknown
): asserts metadata is Partial<FeatureFlagMetaData> {
  if (!metadata || typeof metadata !== 'object') {
    throw new TypeError('Invalid metadata - metadata must be an object');
  }

  const metadataKeys = Object.keys(metadata).filter(
    (key): key is FeatureFlag => key in FeatureFlag
  );

  metadataKeys.forEach((key) => {
    type UnresolveFeatureFlagMetaData = { [K in FeatureFlag]?: object };
    const ffMetadata = (metadata as UnresolveFeatureFlagMetaData)[key];
    if (!ffMetadata) return;

    // check that we have required fields
    assertHasKeys('metadata', ffMetadata, [
      'ffKey',
      'valueType',
      'combinationMethod',
      'description',
      'blockingMethod',
    ]);

    const { valueType, combinationMethod, description, blockingMethod, ffKey } = ffMetadata;

    if (typeof ffKey !== 'string' || !(ffKey in FeatureFlag)) {
      throw new TypeError(`Invalid metadata: field "ffKey" isn't a valid FeatureFlag`);
    }

    if (typeof description !== 'string') {
      throw new TypeError('Invalid metadata: field "description" must be a string');
    }

    if ('blockingText' in ffMetadata && typeof ffMetadata.blockingText !== 'string') {
      throw new TypeError('Invalid metadata: field "blockingText" must be a string if provided');
    }

    assertValidBlockingMethod(blockingMethod);
    assertValidFeatureFlagValueType(valueType);
    assertValidCombinationMethod(combinationMethod);

    if (!validCombos[valueType].has(combinationMethod)) {
      throw new Error(
        `metadata provided is invalid - combination method ${combinationMethod} 
        isn't compatible with type ${valueType}`
      );
    }

    if ('defaultValue' in ffMetadata && !valueToValueTypeMap[valueType](ffMetadata.defaultValue)) {
      throw new Error(
        `defaultValue for metadata provided is invalid - valueType ${valueType} 
        isn't compatible with value ${ffMetadata.defaultValue}`
      );
    }
  });
}

/**
 * @description Asserts that an input is a POJO - Plain Old Javascript Object
 * @param obj - The object to check.
 * @param objectName - Optional parameter to allow for more descriptive error messages, in
 * order to specify for what type this check is coming from.
 */
export function assertValidPOJO(obj: unknown, objectName?: string): asserts obj is PlainObject {
  if (typeof obj !== 'object' || !obj || Array.isArray(obj)) {
    throw new TypeError(`Invalid ${objectName || 'POJO'}: must be non-null/non-array object`);
  }
}

export function assertValidPackageMetaData(metadata: unknown): asserts metadata is PackageMetaData {
  if (!metadata) {
    throw new TypeError('Invalid PackageMetaData: metadata must be defined.');
  }

  if (typeof metadata !== 'object') {
    throw new TypeError('Invalid PackageMetaData: metadata must be an object.');
  }

  if (!('id' in metadata)) {
    throw new TypeError('Invalid PackageMetaData: missing field "id"');
  }

  if (!('name' in metadata)) {
    throw new TypeError('Invalid PackageMetaData: missing field "name"');
  }

  if ('rank' in metadata && typeof metadata.rank !== 'number') {
    throw new TypeError('Invalid PackageMetaData: rank must be a number');
  }

  if ('isPackage' in metadata && typeof metadata.isPackage !== 'boolean') {
    throw new TypeError('Invalid PackageMetaData: isPackage must be a boolean');
  }

  if ('sources' in metadata) {
    if (!Array.isArray(metadata.sources)) {
      throw new TypeError('Invalid PackageMetaData: sources must be an array');
    }

    for (const source of metadata.sources) {
      assertValidFeatureFlagValueSource(source);
    }
  }

  assertValidFeatureFlagConfigKey(metadata.id);

  if (typeof metadata.name !== 'string') {
    throw new TypeError(
      `Invalid PackageMetaData - ${metadata.name}: metadata value must be of type "string"`
    );
  }
}

export function assertValidPackageMetaDataList(
  list: unknown
): asserts list is PackageMetaData[] {
  if (!Array.isArray(list)) {
    throw new TypeError("Invalid PackageMetaData[]: list must be an array");
  }

  for (const metadata of list) {
    assertValidPackageMetaData(metadata);
  }
}

export function assertValidPackagesConfig(
  config: unknown
): asserts config is FeatureFlagPackagesConfig {
  if (!config || typeof config !== "object") {
    throw new TypeError("Invalid config - config must be an object");
  }

  Object.keys(config).forEach((plan) => {
    assertValidFeatureFlagConfigKey(plan);
    if (!hasField(config, plan)) return;

    const packageConfig: any = config[plan];
    if (typeof packageConfig !== "object" || packageConfig === null) {
      throw new Error(
        `Package config for package "${plan}" must be a non-null object`
      );
    }

    for (const featureFlag in packageConfig) {
      if (!(featureFlag in FeatureFlag)) continue;

      const { value } = packageConfig[featureFlag];
      assertValidConfigValue(value);
    }
  });
}

export const isFeatureFlagRankedControlListValue = (
  v: unknown
): v is FeatureFlagRankedControlListValue => {
  if (typeof v !== 'object' || v === null || Array.isArray(v)) return false;

  if (!('type' in v && 'rank' in v && 'id' in v)) return false;

  if (!v.id) return false; // id must be non empty

  return (v.type === 'allow' || v.type === 'block') && typeof v.rank === 'number';
};

export function isValidFeatureFlagConfigKey(
  key: unknown
): key is FeatureFlagConfigKey {
  return typeof key === "string" && key.startsWith(CONFIG_KEY_PREFIX);
}

export const formatChangeValue = (change: any, currency: string) => {
  if (!isNaN(change) && !isNaN(parseFloat(change))) {
    return formatNumber(+change, {
      currency: currency,
      style: 'currency',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    });
  } else return change;
};

export function creativeHubMedia(media: MediaItemType, isStaging = false): MediaItemType {
  const bucketName = isStaging
    ? 'file-hosting-bucket-triple-whale-staging'
    : 'file-hosting-bucket-shofifi';
  if (!media.url) {
    let publicUrl = `${storageUrl}/${bucketName}/${media.source}`;
    switch (media.source) {
      case 'influencer-creatives':
        publicUrl += `/${media.media_type}s/${media.shop_id}/${media.custom_id}`;
        break;
      case 'products':
        if (media.service_id === 'shopify') {
          publicUrl += `/${media.shop_id}/${media.service_id}/${media.custom_id}`;
        } else {
          publicUrl += `/${media.shop_id}/${media.service_id}/${media.media_type}s`;
        }
        break;
      default:
        publicUrl += `/${media.shop_id}/${media.service_id}/${media.media_type}s`;
        //I dont think this is necessary
        // if (media.custom_id) {
        //   publicUrl += `${media.custom_id}/`;
        // }
        break;
    }
    publicUrl += media.gcs_path;
    media.url = publicUrl;
  }
  if (media.media_type === 'video' && media.thumbnail) {
    let regex = '/storage.googleapis.com/';
    if (!media.thumbnail.match(regex)) {
      let thumbnailUrl = `${storageUrl}/${bucketName}/${media.source}`;
      switch (media.source) {
        case 'influencer-creatives':
          thumbnailUrl += `/${media.media_type}s/${media.shop_id}/${media.custom_id}`;
          break;
        default:
          thumbnailUrl += `/${media.shop_id}/${media.service_id}/thumbnails`;
          break;
      }
      thumbnailUrl += media.thumbnail;
      media.thumbnail = thumbnailUrl;
    }
  }
  return { ...media };
}

/**
 * Recursively merges the properties of multiple source objects into a target object.
 * @param target - The target object to merge the sources into.
 * @param sources - The source objects to merge into the target object.
 * @returns The merged object.
 */
export function deepMerge(target: any, ...sources: any) {
  if (!sources.length) return target;
  const source = sources.shift();

  if (isObject(target) && isObject(source)) {
    for (const key in source) {
      if (isObject(source[key])) {
        if (!target[key]) Object.assign(target, { [key]: {} });
        deepMerge(target[key], source[key]);
      } else if (Array.isArray(source[key]) && (Array.isArray(target[key]) || !target[key])) {
        if (!target[key]) Object.assign(target, { [key]: [] });
        target[key] = target[key].concat(source[key]);
      } else {
        Object.assign(target, { [key]: source[key] });
      }
    }
  }

  return deepMerge(target, ...sources);
}

export function maybeAdd(str: string, add: string, mode: 'append' | 'prepend' = 'append') {
  if (!str) return '';
  if (mode == 'append') {
    return `${str}${add}`;
  }
  return `${add}${str}`;
}

export const forecastingRows = (
  products: any[],
  isScaled: boolean,
  openAll: boolean,
  view: string | null = null,
  hasMoreProducts: boolean = false,
  adsServicesToDisplay: any[] = []
): ForecastingRow[] => {
  if (view === 'cohort') {
    return cohortForecastingRows(adsServicesToDisplay);
  } else if (view === 'channel') {
    return channelForecastingRows(adsServicesToDisplay);
  } else if (view === 'inventory_revenue' || view === 'inventory_collection_revenue') {
    return inventoryRevenueForecastingRows(products, isScaled, hasMoreProducts, openAll);
  } else if (view === 'inventory_units' || view === 'inventory_collection_units') {
    return inventoryUnitsForecastingRows(products, hasMoreProducts, openAll);
  } else if (view === 'inventory_days_of_stock') {
    return inventoryDaysOfStockForecastingRows(products, hasMoreProducts, openAll);
  } else {
    return [
      ...cohortForecastingRows(adsServicesToDisplay),
      ...channelForecastingRows(adsServicesToDisplay),
      ...inventoryForecastingRows(products, isScaled, hasMoreProducts, openAll),
    ];
  }
};

