export interface TwNumberFormatOptions extends Intl.NumberFormatOptions {
  style: valueFormats;
  dateFormat?: string;
}

export const formatNumber = (value: number, options: TwNumberFormatOptions): string => {
  // if you explicitly want a string, you get a string
  if (options.style === 'string') {
    return value.toString();
  }
  if (!value || Number.isNaN(+value) || !Number.isFinite(+value)) {
    value = 0;
  }
  return value.toLocaleString(undefined, options);
};

export function getNotificationSubtopic(notification: LighthouseNotification) {
  if (notification.subtopic) {
    return notification.subtopic;
  }
  switch (notification.type) {
    case 'generative':
      return 'ads';
    case 'inventory':
      return notification?.data?.type;
    case 'anomaly':
      return notification?.data?.metric;
    case 'cdp-audience':
      return notification?.data?.eventType;
    case 'data-stories':
      return 'data-stories';
    case 'metrics-report':
      return 'total_impact';
    case 'opportunity-report':
      return 'opportunity-report';
    case 'rule':
      return 'facebook-ads';
    case 'rules-report':
      return 'facebook';
    case 'klaviyo':
      return 'campaign_sent';
    case 'data-stories':
      return 'data-stories';
  }
  return;
}

export function notificationDefaultSettingOn(topic: string, subtopic: string) {
  if (topic === 'activities') {
    if (['shopify_product_in_stock', 'shopify_product_out_of_stock'].includes(subtopic)) {
      return false;
    }
    return true;
  }
  return true;
}

export class Promiser {
  private _promises: Promise<any>[] = [];

  add(p: any) {
    this._promises.push(p);
    if (isPromise(p)) {
      // this will avoid a unawaited uncaught promise rejection
      p.catch((_e: any) => {});
    }
    return p;
  }

  all() {
    if (this._promises.length) {
      return Promise.all(this._promises);
    }
  }
}

export const sensoryProviderConvertResponse: (providerResponse: Provider[]) => {
  [key in ServicesIds]: { integrations: Integration[]; credentials: ProviderCredential[] };
} = (providerResponse: Provider[]) => {
  return providerResponse.reduce((acc, provider) => {
    const { integrations, id, credentials } = provider;
    return {
      ...acc,
      [id]: {
        integrations,
        credentials,
      },
    };
  }, {} as { [key in ServicesIds]: { integrations: Integration[]; credentials: ProviderCredential[] } });
};

