exports.text:
export function createClickhousePipeline(args: ClickhousePipelineArgs) {
  const { tableArgs, pipelineArgs = {} as PipelineArgs, loading, name } = args;
  let { mode } = args;

  if (name.includes('_')) {
    throw new Error('Pipeline name must not contain underscores');
  }

  mode =
    mode ||
    (tableArgs.metadata?.dateField || tableArgs.metadata?.providerAccountField
      ? 'STREAM_SPLIT'
      : 'STREAM');

  tableArgs.columns = tableArgs.columns.concat([
    {
      name: sonicUpdatedAtColumn,
      type: 'DateTime',
    },
  ]);
  tableArgs.cluster = tableArgs.cluster || useCluster(tableArgs.engine);

  if (!tableArgs.partitionBies?.length && tableArgs.metadata) {
    tableArgs.partitionBies = getPartitionBies({
      metadata: tableArgs.metadata,
      columns: tableArgs.columns,
      allowTwoPartitionBies: mode === 'LEVEL_SPLIT',
    });
  }

  pipelineArgs.topic = pipelineArgs.topic || name;
  pipelineArgs.streamEngine = pipelineArgs.streamEngine || 'saber';

  const {
    engine,
    cluster,
    engineParams,
    indices,
    orderBies,
    metadata,
    columns,
    partitionBies,
    settings,
    comment,
    protect = isProduction,
  } = tableArgs;

  const { projectId } = getConfigs();

  if (mode === 'STREAM_SPLIT' || mode === 'LEVEL_SPLIT') {
    pipelineArgs.splitNumDays = pipelineArgs.splitNumDays ?? 1;
  }

  new gcp.storage.BucketObject(
    `${name}-table`,
    {
      bucket: `devops-${projectId}`,
      content: toJSONOutput(tableArgs),
      name: `sonic-tables/${name}.json`,
    },
    { retainOnDelete: true }
  );

  const table = createClickhouseTable({
    name,
    database: clickhouseSystemDatabase,
    cluster,
    engine,
    engineParams,
    indices,
    orderBies,
    partitionBies,
    metadata,
    columns,
    settings,
    comment,
    protect,
  });

  createMainView({
    table,
    tableName: table.name,
    name,
    mode,
    splitNumDays: pipelineArgs.splitNumDays,
    columns,
    metadata,
    cluster,
  });

  if (pipelineArgs.streamEngine === 'saber') {
    const nameUnderscore = name.replace(/-/g, '_');
    let tableName: string, oldTableName: string;
    if (mode === 'LEVEL') {
      tableName = nameUnderscore;
    } else if (mode === 'LEVEL_SPLIT') {
      tableName = `${nameUnderscore}__live`;
      oldTableName = nameUnderscore;
    } else if (mode === 'STREAM') {
      tableName = `${nameUnderscore}__old`;
    } else if (mode === 'STREAM_SPLIT') {
      tableName = nameUnderscore;
      oldTableName = `${nameUnderscore}__old`;
    }

    deploySaber({
      name: name,
      main: 'kafkaCHPipeline',
      concurrencyLimit: 40000,
      concurrencyTarget: 0,
      createK8sDeploymentArgs: {
        envs: {
          KAFKA_TOPIC_NAME: pipelineArgs.topic,
          CLICKHOUSE_TABLE_NAME: tableName,
          CLICKHOUSE_OLD_TABLE_NAME: oldTableName,
          CLICKHOUSE_DATABASE: clickhouseSystemDatabase,
          CLICKHOUSE_DATE_FIELD: metadata?.dateField,
          CLICKHOUSE_SPLIT_NUM_DAYS: pipelineArgs.splitNumDays,
          CLICKHOUSE_DIRECT: mode === 'LEVEL' || undefined,
        },
        maxReplicas: 20,
        podArgs: {
          k8sServiceAccountName: getConfigs().serviceId,
          image: getServiceImage({ serviceId: 'sonic', imageTag: 'latest', resolve: true }),
        },
      },
    });
  }

  if (mode === 'STREAM' || mode === 'STREAM_SPLIT') {
    createStream({
      name,
      mode,
      pipelineArgs,
      tableArgs,
      table,
    });
  } else if (mode === 'LEVEL' || mode === 'LEVEL_SPLIT') {
    createLevel({
      name,
      mode,
      database: clickhouseSystemDatabase,
      pipelineArgs,
      tableArgs,
    });
  }

  return { table };
}

export function getClickhouseProvider(args?: { name?: string; replicaName?: string }) {
  const { name = 'sonic-cluster', replicaName = 'instance-0-2' } = args || {};
  const clusterReplicaName = `${name}__${replicaName}`;
  if (providers[clusterReplicaName]) return providers[clusterReplicaName];
  const secret = getSecretValue(`${name}-clickhouse`).apply(JSON.parse);
  const username = secret.apply((s) => s.username);
  const password = pulumi.secret(secret.apply((s) => s.password));

  const replica = secret.apply((s) => s.replicas.find((r: any) => r.name === replicaName).domain);
  const provider = new clickhouse.Provider(
    clusterReplicaName,
    {
      username,
      password,
      host: replica,
      port: 9000,
    },
    { aliases: [{ name: 'clickhouse' }] }
  );
  providers[clusterReplicaName] = provider;
  return provider;
}

export function createStream(args: {
  name: string;
  tableArgs: ClickhousePipelineArgs['tableArgs'];
  pipelineArgs: ClickhousePipelineArgs['pipelineArgs'];
  table: Table;
  mode: 'STREAM' | 'STREAM_SPLIT';
}) {
  const { mode, tableArgs, pipelineArgs, table } = args;

  const { metadata, columns, engineParams, orderBies, partitionBies, settings } = tableArgs;

  const {
    topic,
    consumerGroup,
    kafkaFormat,
    kafkaThreadPerConsumer,
    kafkaNumConsumers,
    streamEngine,
    splitNumDays,
    interval = '5 minutes',
  } = pipelineArgs || {};

  const { projectId } = getConfigs();

  const tableName = args.name.replace(/-/g, '_');
  const name = args.name.replace(/_/g, '-');

  createKafkaTopic({ name });

  // TODO mode is top level, streamEngine is inner level
  let oldTable;
  if (mode === 'STREAM_SPLIT') {
    oldTable = createClickhouseTable({
      name: `${tableName}__old`,
      database: clickhouseSystemDatabase,
      engine: 'MergeTree',
      metadata,
      // 15 minute chunks
      partitionBies: [
        { by: sonicUpdatedAtColumn, partitionFunction: 'toYYYYMMDD' },
        { by: sonicUpdatedAtColumn, partitionFunction: 'toHour' },
        { by: `intDiv(toMinute(${sonicUpdatedAtColumn}), 15)` },
      ],
      orderBies: (partitionBies.length ? partitionBies.map((p) => p.by) : orderBies) as string[],
      columns,
      settings,
    });
    createClickhouseTable({
      name: `${tableName}__temp`,
      database: clickhouseSystemDatabase,
      engine: 'ReplacingMergeTree',
      engineParams,
      orderBies,
      partitionBies,
      metadata,
      columns,
      settings,
    });
  }

  if (streamEngine === 'clickhouse-table') {
    createStreamKafkaEngine({
      tableName,
      topic,
      consumerGroup,
      columns,
      kafkaFormat,
      kafkaThreadPerConsumer,
      kafkaNumConsumers,
      mode,
      splitNumDays,
      metadata,
      table,
      oldTable,
    });
  }

  if (streamEngine === 'kafka-connect') {
    const defaultTable = mode === 'STREAM_SPLIT' ? oldTable.name : table.name;
    createKafkaConnectorClickhouse({
      name: name,
      provider: getK8sProvider({ cluster: 'sonic-cluster', namespace: 'kafka-connect' }),
      topics: [pipelineArgs.topic],
      defaultTable,
      realtimeTable: table.name,
      dateField: metadata?.dateField || 'event_date',
      splitDays: splitNumDays,
    });
  }

  if (mode === 'STREAM_SPLIT') {
    createTemporalSchedule(name, {
      projectId,
      serviceId: 'clickhouse-ops',
      namespace: 'clickhouse-ops-ns',
      scheduleId: name,
      spec: {
        intervals: [{ every: interval }],
      },
      action: {
        type: 'startWorkflow',
        workflowType: 'mergePartition',
        taskQueue: 'queue',
        args: [
          {
            table: table.name,
            partitioning: partitionBies[0],
            splitNumDays,
          },
        ],
        searchAttributes: {
          merge_table: [tableName], // 'table' is reserved
        },
      },
      policies: { overlap: ScheduleOverlapPolicy.BUFFER_ONE },
    });

    if (metadata.dateField) {
      // once a day, run optimize on the day that just dropped off the end of the dedup window
      // run at a random hour between 1 and 22
      createTemporalSchedule(`${name}-optimize`, {
        projectId,
        serviceId: 'clickhouse-ops',
        namespace: 'clickhouse-ops-ns',
        scheduleId: `${name}-optimize`,
        spec: {
          calendars: [{ second: 0, ...getScheduleOffset(name) }],
        },
        action: {
          type: 'startWorkflow',
          workflowType: 'optimize',
          taskQueue: 'queue',
          args: [
            { database: clickhouseSystemDatabase, table: tableName, daysBack: splitNumDays + 1 },
          ],
        },
        policies: { overlap: ScheduleOverlapPolicy.BUFFER_ONE },
      });
    }
  }
}

