export function createKafkaTable(args: KafkaArgs & { database: string }) {
  const {
    name,
    database,
    topic,
    consumerGroup = topic,
    kafkaFormat: kafka_format = 'JSONEachRow',
    columns,
    kafkaThreadPerConsumer = 0,
    kafkaNumConsumers = 4,
  } = args;

  const kafkaProvider = getKafkaProvider();

  return createClickhouseTable({
    database,
    engine: 'Kafka',
    settings: {
      kafka_broker_list: kafkaProvider.bootstrapServers.apply((s: any) => JSON.parse(s)[0]),
      kafka_topic_list: topic,
      kafka_group_name: consumerGroup,
      kafka_format,
      kafka_thread_per_consumer: String(kafkaThreadPerConsumer),
      kafka_num_consumers: String(kafkaNumConsumers),
      // kafka_handle_error_mode: 'stream',
      kafka_commit_every_batch: '1',
      kafka_handle_error_mode: 'stream',
    },
    metadata: null,
    name,
    columns: columnsForKafka(columns),
  });
}

export function createClickhouseTable(tableArgs: ClickhouseTableArgs) {
  const {
    engine,
    cluster = useCluster(engine),
    database,
    name,
    columns,
    indices,
    orderBies,
    engineParams = [],
    comment,
    metadata,
    protect,
  } = tableArgs;

  const tableName = name.replace(/-/g, '_');

  if (!/^[a-zA-Z0-9_]+$/.test(tableName)) {
    throw new Error('Table name must contain only characters, numbers and underscores');
  }

  var { settings, partitionBies } = tableArgs;

  if (!partitionBies?.length && metadata) {
    partitionBies = getPartitionBies({ metadata, columns });
  }

  settings = {
    ...defaultSettings(engine),
    ...settings,
  };

  const unnestedColumns = columns.map((column) => convertColumn(column)).flat();
  const table = new Table(
    `${database}.${tableName}`,
    {
      cluster,
      database,
      name: tableName,
      columns: unnestedColumns,
      engine,
      engineParams,
      indices,
      orderBies,
      partitionBies,
      comment,
      settings,
    },
    {
      replaceOnChanges: engine === 'Kafka' || engine === 'Null' ? ['columns'] : undefined,
      protect,
      deleteBeforeReplace: true,
      provider: getClickhouseProvider(),
      aliases: [{ name: `${database}.${name.replace(/_/g, '-')}` }],
    }
  );
  return table;
}

export function useCluster(engine: string) {
  return engine.includes('Replicated') ? 'main' : undefined;
}

export function getPartitionBies(args: {
  metadata: any;
  columns: ClickhouseColumn[];
  allowTwoPartitionBies?: boolean;
}) {
  const { metadata, columns, allowTwoPartitionBies = false } = args;
  const partitionBies: types.input.TablePartitionBy[] = [];

  if (metadata?.dateField) {
    const dateField = columns.find((column) => column.name === metadata.dateField);
    const partitionBy = ['DateTime', 'DateTime64'].includes(dateField?.type)
      ? {
          by: metadata.dateField,
          partitionFunction: 'toYYYYMMDD',
        }
      : {
          by: metadata.dateField,
        };

    partitionBies.push(partitionBy);
  }

  if (metadata?.providerAccountField && (allowTwoPartitionBies || !metadata?.dateField)) {
    partitionBies.push({
      by: metadata.providerAccountField,
      partitionFunction: 'sipHash64',
      mod: '1000',
    });
  }

  return partitionBies;
}

