export function setPos(pos, widget: Partial<DashboardWidget>): Partial<DashboardWidget> {
  const layout = widget.widgetLayout;
  const width = layout.width;
  const total = 12;
  widget.widgetLayout.x = pos.x;
  widget.widgetLayout.y = pos.y;
  if (pos.x * (total / width) > total) {
    pos.x = 0;
    pos.y++;
  } else {
    pos.x += width;
  }
  return widget;
}

export async function callApi(url: string) {
  const authClient = new GoogleAuth();
  const accessToken = await authClient.getAccessToken();
  const response = await axios.get(url, {
    headers: {
      Authorization: `Bearer ${accessToken}`,
      Accept: 'application/json',
    },
  });
  return response;
}

// currently no sdk support for this
export async function getMostRecentJob(): Promise<Job | undefined> {
  const { projectId, serviceId, location } = getConfigs();
  const response = await callApi(
    `https://datapipelines.googleapis.com/v1/projects/${projectId}/locations/${location}/pipelines/${serviceId}/jobs`
  );
  return response.data.jobs[0];
}

export async function isJobActive(job: Job): Promise<boolean> {
  const { projectId, location } = getConfigs();
  const dataflowClient = new JobsV1Beta3Client();
  const jobInfo = await dataflowClient.getJob({
    projectId,
    location,
    jobId: job.id,
  });
  return jobInfo[0].currentState === 'JOB_STATE_RUNNING';
}

// currently no sdk support for this
export async function getPipeline(): Promise<boolean> {
  const { projectId, serviceId, location } = getConfigs();
  try {
    await callApi(
      `https://datapipelines.googleapis.com/v1/projects/${projectId}/locations/${location}/pipelines/${serviceId}`
    );
    return true;
  } catch (e) {
    return false;
  }
}

// the sdk function for this, listJobMessages, does not return what we need - IListJobMessagesResponse (client is beta)
export async function getCurrentNumWorkers(jobId: string): Promise<number | undefined> {
  const { projectId, location } = getConfigs();
  let pageToken;
  const autoscalingEvents = [];
  do {
    const pageTokenParam = pageToken ? `&pageToken=${pageToken}` : '';
    const response = await callApi(
      `https://dataflow.googleapis.com/v1b3/projects/${projectId}/locations/${location}/jobs/${jobId}/messages${pageTokenParam}`
    );
    const data: ListJobMessagesResponse = response.data;
    pageToken = data.nextPageToken;
    if (data.autoscalingEvents) {
      autoscalingEvents.push(...data.autoscalingEvents);
    }
  } while (pageToken);
  autoscalingEvents.reverse(); // most recent last -> most recent first

  const mostRecentNumWorkers = autoscalingEvents.find((event) => event.currentNumWorkers);
  if (mostRecentNumWorkers) {
    return Number(mostRecentNumWorkers.currentNumWorkers);
  }
}

export function createDataFlowClassic(args: {
  parameters?: Record<string, pulumi.Input<string>>;
  maxWorkers?: number;
  runtime: 'node' | 'python';
  entrypoint?: string;
  secretVersion?: gcp.secretmanager.SecretVersion;
  envs?: Record<string, any>;
}) {
  const { parameters = {}, maxWorkers = 10, runtime, secretVersion } = args;
  const { projectId, serviceId, location, serviceConfig, stack } = getConfigs();

  const entrypoint = args.entrypoint || (runtime == 'node' ? 'dist/app.js' : 'src/app.py');

  if (secretVersion) {
    parameters['tw_secret_name'] = secretVersion.name;
  }

  const envs = {
    ...serviceConfig?.env,
    SERVICE_ID: serviceId,
    TW_VERSION: getBuildVersion(),
    TW_DEPLOYMENT: serviceId,
    STACK_NAME: stack,
  };

  parameters['tw_envs'] = toJSONOutput({ ...envs, ...args.envs });

  const parametersString = dataflowParametersToCMDArgs(parameters);

  const gitSha = pulumi.output(getGitSha(false));

  const gcsPath = pulumi.interpolate`gs://dataflow-templates-${projectId}/classic/${serviceId}/${gitSha}.json`;

  const standardArgs = pulumi.interpolate`--project=${projectId} --runner=dataflow --region=${location} \
    --temp_location gs://tw-dataflows-${projectId}/${serviceId}/temp --staging_location gs://tw-dataflows-${projectId}/${serviceId}/temp \
    --sdkContainerImage ${getDevopsImage(`beam-worker-${runtime}`)} ${parametersString}`;

  let command = pulumi.output('');

  if (runtime === 'node') {
    command = pulumi.interpolate`node ${entrypoint} ${standardArgs} --template_location="${gcsPath}"`;
  } else if (runtime === 'python') {
    command = pulumi.interpolate`python3 ${entrypoint} ${standardArgs} --template_location="${gcsPath}"`;
  }

  const secretValue = secretVersion?.secret && getSecretValue(secretVersion.secret);
  const dockerArgs: docker.ContainerArgs = {
    image: getServiceImage(),
    attach: true,
    logs: false,
    mustRun: false,
    entrypoints: ['bash'],
    envs: [
      `PROJECT_ID=${projectId}`,
      pulumi.interpolate`COMMIT_SHA=${gitSha}`,
      `GOOGLE_CLOUD_PROJECT=${projectId}`,
      pulumi.interpolate`TW_SECRETS=${secretValue}`,
    ],
    command: ['-c', command],
  };

  if (process.env.IS_CLOUD_BUILD) {
    dockerArgs.networksAdvanced = [{ name: 'cloudbuild' }];
  } else {
    dockerArgs.mounts = [
      {
        source: `${process.env.HOME}/.config/gcloud`,
        target: '/root/.config/gcloud',
        type: 'bind',
      },
    ];
  }

  const container = new docker.Container(serviceId, dockerArgs, { retainOnDelete: true });

  // container.name.apply((name) => {
  //   try {
  //     docker.getLogs({ name }).then((logs) => {
  //       console.log(logs.logsListStrings.join('\n'));
  //     });
  //   } catch {}
  // });

  const pipeline = createDataFlowPipeline({
    name: serviceId,
    parameters: parameters,
    maxWorkers: maxWorkers,
    gcsPath,
    templateType: 'classic',
    dependsOn: [container],
  });
  return { pipeline };
}

