export type EnvironmentArgs =
  | gcp.types.input.dataflow.PipelineWorkloadDataflowFlexTemplateRequestLaunchParameterEnvironment
  | gcp.types.input.dataflow.PipelineWorkloadDataflowLaunchTemplateRequestLaunchParametersEnvironment;

export function dataflowParametersToCMDArgs(parameters: Record<string, pulumi.Input<string>>) {
  const parametersString = pulumi
    .all(
      Object.entries(parameters || {}).map(([key, value]) => pulumi.interpolate`--${key}=${value}`)
    )
    .apply((v) => v.join(' '));
  return parametersString;
}

export function getDataformDefaultServiceAccountEmail(projectNumber: pulumi.Input<String>) {
    return pulumi.interpolate`service-${projectNumber}@gcp-sa-dataform.iam.gserviceaccount.com`;
}

export function buildRoutine(buildRoutineArgs: buildRoutineArgs) {
  setDefaultLabelsForAllResources();

  // Set defaults for nested object arguments
  buildRoutineArgs = { ...buildViewDefaults, ...buildRoutineArgs };

  // extracting keys and values from viewQueryParamsObject
  const routineDefinitionBodyParamsObject =
    buildRoutineArgs.routineDefinitionBodyParamsObject || defaultRoutineDefinitionBodyParamsObject;
  let ks = Object.keys(routineDefinitionBodyParamsObject);
  let vs = pulumi.all(ks.map((k) => routineDefinitionBodyParamsObject[k])).apply((v) => v);

  const bqRoutine = new gcp.bigquery.Routine(
    buildRoutineArgs.routineName,
    {
      datasetId: pulumi.interpolate`${buildRoutineArgs.routineDataset}`,
      language: buildRoutineArgs.routineLanguage,
      routineType: buildRoutineArgs.routineType,
      routineId: buildRoutineArgs.routineName,
      definitionBody: vs.apply(
        (t: any) =>
          `${buildRoutineArgs.routineDefinitionBody(
            t.reduce(
              (
                obj: Record<string, pulumi.Output<string> | string>,
                value: pulumi.Output<string> | string,
                index: number
              ) => {
                obj[ks[index]] = value;
                return obj;
              },
              {} as Record<string, pulumi.Output<string> | string>
            )
          )}`
      ),
      arguments: buildRoutineArgs.routineArguments,
      description: buildRoutineArgs.routineDescription,
      returnType: buildRoutineArgs.routineReturnType,
    },
    {
      dependsOn: buildRoutineArgs.dependencies,
    }
  );

  if (buildRoutineArgs.createViewForSchemaDefinition) {
    if (!buildRoutineArgs.schema)
      throw new Error('When creating a point the routine you must provide the schema');
    const viewName = buildRoutineArgs.renameViewName
      ? buildRoutineArgs.renameViewName
      : buildRoutineArgs.routineName;

    const routineExample = buildRoutineArgs.usingRoutineExample
      ? buildRoutineArgs.usingRoutineExample
      : buildRoutineArgs.routineArguments.map(
          ({ name, dataType }) => `${name}:${JSON.parse(dataType).typeKind}`
        );

    const colSelection = `select ${buildRoutineArgs.schema.map(
      (col) => `CAST(NULL AS ${columnType(col)}) AS ${col.name}`
    )}`;

    const viewQuery = `${colSelection}
      from (${colSelection})
      where ERROR("USE - SELECT * FROM \`${buildRoutineArgs.routineDataset}.${buildRoutineArgs.routineName}\`(${routineExample})")
      `;

    buildView({
      pulumiResourceName: `${viewName}-tvf-public-output`,
      viewName: viewName,
      viewDataset: publicDatasetId,
      viewQuery: ({}) => viewQuery,
      viewQueryParamsObject: {},
      viewSchema: buildRoutineArgs.schema,
      dependencies: [bqRoutine],
    });
  }

  return bqRoutine;
}

export function buildView(buildViewArgs: buildViewArgs): gcp.bigquery.Table {
  setDefaultLabelsForAllResources();
  // Set defaults for nested object arguments
  buildViewArgs = { ...buildViewDefaults, ...buildViewArgs };

  // extracting keys and values from viewQueryParamsObject
  let ks = Object.keys(buildViewArgs.viewQueryParamsObject);
  let vs = pulumi.all(ks.map((k) => buildViewArgs.viewQueryParamsObject[k])).apply((v) => v);

  const query = vs.apply(
    (t: any) =>
      `${buildViewArgs.viewQuery(
        t.reduce(
          (
            obj: Record<string, pulumi.Output<string> | string>,
            value: pulumi.Output<string> | string,
            index: number
          ) => {
            obj[ks[index]] = value;
            return obj;
          },
          {} as Record<string, pulumi.Output<string> | string>
        )
      )}`
  );

  const pulumiResourceName = buildViewArgs.pulumiResourceName || buildViewArgs.viewName;
  const bqView = new gcp.bigquery.Table(
    pulumiResourceName,
    {
      datasetId: buildViewArgs.viewDataset,
      tableId: buildViewArgs.viewName,
      view: !buildViewArgs.materializedView
        ? {
            query: query,
            useLegacySql: false,
          }
        : undefined,
      materializedView: buildViewArgs.materializedView
        ? {
            query: query,
            enableRefresh: buildViewArgs.materializedView.EnableRefresh,
            refreshIntervalMs: buildViewArgs.materializedView.RefreshIntervalMs,
          }
        : undefined,
      schema: buildViewArgs.viewSchema
        ? JSON.stringify(asBqSchema(buildViewArgs.viewSchema, true))
        : undefined,
      description: buildViewArgs.viewDescription,
      labels: { ...buildViewArgs.viewLabels },
      deletionProtection: false,
    },
    {
      replaceOnChanges: ['schema'],
      deleteBeforeReplace: true,
      dependsOn: buildViewArgs.dependencies,
    }
  );
  return bqView;
}

export function createRecentCdcPipeline({
  pipelineProperties, // Params with a pipeline-wide effect (name, labels)
  schedulerConfig, // Scheduling configuration for the pipeline and its subruns
  schema,
  partitioning,
  clusterings, // Output data model schema and datasets
  inputDataModel, // Configuration for input datasets, used in pipelines with an upstream pipeline.
  testConfig, // Output data model schema and datasets
}: CdcRecentPipelineArgs) {
  // Set defaults for nestesd object arguments
  pipelineProperties = { ...pipelinePropertiesDefault, ...pipelineProperties };
  schedulerConfig = { ...schedulerConfigArgsDefault, ...schedulerConfig };
  inputDataModel = { ...inputDataModelDefault, ...inputDataModel };
  testConfig = { ...testConfigDefault, ...testConfig };

  const { serviceId: pipelineId, stack, projectId, location } = getConfigs();

  let serviceAccountEmail;
  if (stack === "pre-prod") {
    serviceAccountEmail = `dl-${pipelineId}@${projectId}.iam.gserviceaccount.com`;
  } else {
    const { serviceAccount } = createServiceAccount({
      name: `dl-${pipelineId}`,
      roles: [`projects/${projectId}/roles/datalandService`],
    });
    serviceAccountEmail = serviceAccount.email;

    if (stack === "shofifi") {
      addRolesToServiceAccount(serviceAccount, Projects.DataLandOps, [
        "roles/dataform.editor",
        "roles/bigquery.jobUser",
        "roles/bigquery.dataOwner",
        "roles/bigquery.resourceViewer",
      ]);
      addRolesToServiceAccount(serviceAccount, "triple-whale-ops", [
        "roles/dataform.editor",
        "roles/bigquery.jobUser",
        "roles/bigquery.dataOwner",
        "roles/bigquery.resourceViewer",
      ]);
    }
    if (stack === "triple-whale-staging") {
      addRolesToServiceAccount(serviceAccount, "shofifi", [
        "roles/bigquery.dataViewer",
        "roles/bigquery.connectionUser",
      ]);
      addRolesToServiceAccount(serviceAccount, "triple-whale-ops", [
        "roles/bigquery.dataViewer",
      ]);
    }
  }

  // Get labels from current stack and apply to all resources
  setDefaultLabelsForAllResources();

  // Override current stack with test config

  const bqDifferentDataformResource =
    pipelineProperties.runnerOnDifferentProject && stack === "shofifi"; // On development testing it manually using BQ UI

  const dataformProject = bqDifferentDataformResource
    ? pipelineProperties.runnerOnDifferentProject
    : projectId;
  const dataformStack = bqDifferentDataformResource
    ? pipelineProperties.runnerOnDifferentProject
    : stack;

  const chronosStateStackRef = new pulumi.StackReference(
    `triplewhale/chronos-state/${stack}`
  );
  const chronosStateServiceUrlOutput =
    chronosStateStackRef.getOutput("serviceUrl");

  const tableArgs = buildTableArgs({
    schema: schema,
    clusterings: clusterings,
    labels: pipelineProperties.pipelineLabels,
    partitioning: partitioning,
  });

  new gcp.bigquery.Table(`${pipelineId}-output-recent`, {
    ...tableArgs,
    tableId: pipelineId,
    datasetId: recentDatasetId,
  });

  // Set dataform branch. Use the one specified in test config. If none is specified:
  // (1) This is a DLCP pipeline, use the appropriate branch name.
  // (2) For legacy pipelines, 'develop' for staging and pre-prod, 'master' for prod.
  var dataformBranch =
    testConfig.forceDataformBranch ||
    pipelineProperties.dlcpGitRef ||
    (stack === "shofifi" ? "master" : "develop"); // Legacy!

  // If the pipeline is running on a ideosyncratic stack (not staging, pre-prod or prod), ceate a new workspace
  if (!isCanonicalStack()) {
    const dataformWorkspaceId = getUniqueNameInProject(pipelineId) as string;
    new pulumiGoogleNative.dataform.v1beta1.Workspace("workspace", {
      repositoryId: getDataformRepositoryNameForProject(dataformStack),
      workspaceId: dataformWorkspaceId,
    }); // TODO push branch. options: 1) trigger gcp to push branch. 2) user manually pushes branch. 3) create branch in github, then they will connect on their own
    dataformBranch = dataformWorkspaceId;
  }

  // Create and configure workflow
  const dirname = __dirname.replace("/pulumi/module/", "/pulumi/src/");

  const workflowDefinition = fs.readFileSync(
    resolve(dirname + "/cdcWorkflow.yaml"),
    "utf8"
  );
  const workflow = new gcp.workflows.Workflow("workflow", {
    name: getUniqueNameInProject(`dataland-pipeline-${pipelineId}`),
    sourceContents: workflowDefinition,
    serviceAccount: serviceAccountEmail,
    labels: { ...pipelineProperties.pipelineLabels },
  });
  // pulumi does not currently expose a way to get the workflow execution URL. See ticket linked below.
  // However, we can construct it ourselves based on the google docs, linked in that ticket
  // https://github.com/pulumi/pulumi-gcp/issues/553
  const workflowUri = pulumi.interpolate`https://workflowexecutions.googleapis.com/v1/projects/${projectId}/locations/${location}/workflows/${workflow.name}/executions`;

  const workflowArgs = {
    dataformRepositoryId: getDataformRepositoryIdForProject(
      dataformStack,
      dataformProject
    ),
    pipeline: pipelineProperties?.pipelineName || pipelineId,
    serviceUrl: chronosStateServiceUrlOutput,
    inputBQDataset: inputDataModel?.inputDatasetName || rawDatasetId,
    inputBQTable: inputDataModel?.inputTableName || pipelineId,
    isDerived: inputDataModel.isDerived,
    dependencies: inputDataModel?.dependencies || {},
    recentBQDataset: recentDatasetId,
    publicBQDataset: latestDatasetId,
    changesetDatasetId: changesetDatasetId,
    outputBQTable: pipelineId,
    dataformBranch: dataformBranch,
    stack,
    bqProjectResource: bqDifferentDataformResource
      ? pipelineProperties?.runnerOnDifferentProject
      : projectId,
  };
  for (const conf of [
    { label: "recent", schedule: schedulerConfig.schedule },
    ...schedulerConfig.subRuns,
  ]) {
    new gcp.cloudscheduler.Job(`job-${conf.label}`, {
      name: getUniqueNameInProject(`dataland-${pipelineId}-${conf.label}`),
      paused: schedulerConfig.paused,
      httpTarget: {
        uri: workflowUri,
        httpMethod: "POST",
        body: toBase64Output(
          toJSONOutput({
            argument: toJSONOutput({
              ...workflowArgs,
              labels: [conf.label],
              subrun: conf.label,
            }),
            call_log_level: "LOG_ERRORS_ONLY",
          })
        ),
        headers: {
          "Content-Type": "application/json",
        },
        oauthToken: {
          serviceAccountEmail: serviceAccountEmail,
        },
      },
      schedule: conf.schedule as pulumi.Input<string>,
    });
  }
}

export class SubRunConfig {
  constructor(schedule: string, label: string) {
    this.schedule = schedule;
    this.label = label;
  }
  schedule: string;
  label: string;
}

// Parameters for the task scheduler
export interface schedulerConfigArgs {
  schedule?: string;
  paused?: boolean;
  subRuns?: Array<SubRunConfig>;
}

export const schedulerConfigArgsDefault: schedulerConfigArgs = {
  schedule: '0 * * * *', // hourly
  paused: false,
  subRuns: [],
};

export interface JobDetailsDep {
  proc_id_to?: string;
  proc_id_from?: string;
}

// Parameters regarding input datasets, used in pipelines with an upstream pipeline.
export interface inputDataModelArgs {
  inputTableName?: string;
  inputDatasetName?: string;
  dependencies?: Record<string, JobDetailsDep>;
  isDerived?: boolean;
}

export const inputDataModelDefault: inputDataModelArgs = {
  inputTableName: '',
  inputDatasetName: '',
  dependencies: {},
  isDerived: false,
};

// Paramaters used for testing, including forcing parts of the pipeline to run on different stacks or branches.
export interface testConfigArgs {
