export type GCPServiceAccount = gcp.serviceaccount.Account & {
  account_id: string;
  uuid: string;
};

export function createIamServiceAccount(args: { accountId: string; name?: string }) {
  const { name } = args;
  const fullAccountId = (getUniqueNameInProject(args.accountId, '-', true) as string).substring(
    0,
    30
  );
  // valid account regex: "^[a-z](?:[-a-z0-9]{4,28}[a-z0-9])$"
  const accountId = fullAccountId.replace(/[^a-z0-9]$/, '');

  const serviceAccount = new gcp.serviceaccount.Account(name || args.accountId, {
    accountId,
    displayName: name,
  }) as GCPServiceAccount;

  serviceAccount.account_id = accountId;
  serviceAccount.uuid = createUUID(accountId);
  return serviceAccount;
}

export function getDefaultComputeServiceAccount() {
  return gcp.compute.getDefaultServiceAccount({}).then((sa) => sa.email);
}

export function deployAirbyte(args: { provider: K8sProvider; name: string }) {
  const { provider, name } = args;
  const airbyteHelm = createHelmRelease({
    name: `airbyte-${name}-${provider.uuid}`,
    releaseArgs: {
      chart: 'airbyte',
      name,
      namespace: provider.namespace,
      createNamespace: true,
      valueYamlFiles: [new pulumi.asset.FileAsset(`${dirname}/helm/charts/airbyte/values.yaml`)],
      repositoryOpts: {
        repo: 'https://airbytehq.github.io/helm-charts',
      },
    },
    provider,
  });

  createK8sIngress({
    name: `airbyte-${name}`,
    twDomain: new TWDomain('triplestack.io', `${name}.airbyte`, 'iap', projectIdAsSubDomain),
    selector: {
      'app.kubernetes.io/instance': name,
      'app.kubernetes.io/name': 'webapp',
    },
    ingressMode: 'iap',
    provider,
    dependsOn: [airbyteHelm],
  });

  createK8sService({
    name: `airbyte-${name}`,
    twDomain: new TWDomain('triplestack.io', `${name}.airbyte`, 'internal'),
    selector: {
      'app.kubernetes.io/instance': name,
      'app.kubernetes.io/name': 'airbyte-api-server',
    },
    ingressMode: 'internal',
    type: 'LoadBalancer',
    provider,
    ports: [{ targetPort: 8006, port: 80 }],
    dependsOn: [airbyteHelm],
  });

  deployToK8s({
    name: `airbyte-temporal-ui-${name}`,
    serviceAccount: null,
    createK8sDeploymentArgs: {
      podArgs: { image: 'temporalio/ui:2.17.0' },
      envs: {
        TEMPORAL_CSRF_COOKIE_INSECURE: 'true',
        TEMPORAL_ADDRESS: `${name}-temporal:7233`,
      },
      maxReplicas: 1,
    },
    providers: [provider],
    domain: new TWDomain('triplestack.io', `${name}.temporal-ui.airbyte`, 'internal'),
  });

  createK8sService({
    name: `airbyte-web-${name}`,
    twDomain: new TWDomain('triplestack.io', `${name}.web.airbyte`, 'internal'),
    selector: {
      'app.kubernetes.io/instance': name,
      'app.kubernetes.io/name': 'webapp',
    },
    ingressMode: 'internal',
    type: 'LoadBalancer',
    provider,
    ports: [{ targetPort: 8080, port: 80 }],
    dependsOn: [airbyteHelm],
  });

  return { resources: [airbyteHelm] };
}

export function deployDatadog(args: { provider: K8sProvider; jmx?: boolean }) {
  const { provider, jmx } = args;
  const values = {
    datadog: { site: 'us5.datadoghq.com.', apiKey: getSecretValue('datadog-api-key') },
  };
  if (jmx) {
    values['agents'] = { image: { tagSuffix: 'jmx' } };
  }
  const datadogHelm = createHelmRelease({
    name: `datadog-${provider.cluster_uuid}`,
    releaseArgs: {
      chart: 'datadog',
      name: 'datadog-agent',
      namespace: 'datadog',
      createNamespace: true,
      valueYamlFiles: [new pulumi.asset.FileAsset(`${dirname}/helm/charts/datadog/values.yaml`)],
      values,
      repositoryOpts: {
        repo: 'https://helm.datadoghq.com',
      },
    },
    provider,
  });
  return { resources: [datadogHelm] };
}

export function createElasticCluster(args: { name: string; provider: K8sProvider }) {
  const { name, provider } = args;

  const password = createPassword({ name: `${name}-es`, special: false }).result;

  const userpassSecret = new kubernetes.core.v1.Secret(
    k8sUniqueName(name, provider),
    {
      metadata: {
        name: name,
      },
      type: 'kubernetes.io/basic-auth',
      stringData: {
        username: 'admin',
        password,
        // https://www.elastic.co/guide/en/elasticsearch/reference/current/built-in-roles.html
        roles: 'superuser,kibana_admin,ingest_admin,monitor,viewer',
      },
    },
    { provider }
  );

  const esCluster = new kubernetes.apiextensions.CustomResource(
    k8sUniqueName(name, provider),
    {
      apiVersion: 'elasticsearch.k8s.elastic.co/v1',
      kind: 'Elasticsearch',
      metadata: {
        name,
      },
      spec: {
        version: '8.13.0',
        auth: {
          fileRealm: [
            {
              secretName: userpassSecret.metadata.name,
            },
          ],
        },
        nodeSets: [
          {
            name: 'default',
            config: {
              'node.roles': ['master', 'data'],
              'node.attr.attr_name': 'attr_value',
              'node.store.allow_mmap': false,
            },
            volumeClaimTemplates: [
              {
                metadata: {
                  name: 'elasticsearch-data',
                },
                spec: {
                  accessModes: ['ReadWriteOnce'],
                  resources: {
                    requests: {
                      storage: '1000Gi',
                    },
                  },
                  storageClassName: 'standard',
                },
              },
            ],
            podTemplate: {
              metadata: {
                labels: {
                  foo: 'bar',
                },
              },
              spec: {
                containers: [
                  {
                    name: 'elasticsearch',
                    resources: {
                      requests: {
                        memory: '64Gi',
                        cpu: '8',
                      },
                      limits: {
                        memory: '64Gi',
                        cpu: '16',
                      },
                    },
                  },
                ],
              },
            },
            count: 3,
          },
        ],
      },
    },
    { provider }
  );
  return { password, esCluster };
}

export function createJaegerCluster(
  args: { name: string; provider: K8sProvider; esPassword: any } & K8sResources
) {
  const { name, esPassword } = args;
  const provider = dependOnK8sClusterOperators(args.provider, ['jaeger']);
  new kubernetes.apiextensions.CustomResource(
    k8sUniqueName(name, provider),
    {
      apiVersion: 'jaegertracing.io/v1',
      kind: 'Jaeger',
      metadata: {
        name: `${name}-jaeger`,
      },
      // https://www.jaegertracing.io/docs/1.57/operator/
      spec: {
        strategy: 'production',
        collector: {
          maxReplicas: 5,
          resources: {
            requests: {
              cpu: '100m',
              memory: '128Mi',
            },
            limits: {
              cpu: '500m',
              memory: '800Mi',
            },
          },
        },
        storage: {
          type: 'elasticsearch',
          options: {
            es: {
              'server-urls': `https://${name}-es-internal-http:9200`,
              tls: {
                enabled: true,
                key: '/es/certificates/tls.key',
                cert: '/es/certificates/tls.crt',
                'skip-host-verify': true,
              },
              username: 'admin',
              password: esPassword,
            },
          },
        },
        volumeMounts: [
          {
            name: 'certificates',
            mountPath: '/es/certificates/',
            readOnly: true,
          },
        ],
        volumes: [
          {
            name: 'certificates',
            secret: {
              secretName: `${name}-es-http-certs-internal`,
            },
          },
        ],
        ingress: {
          enabled: false,
        },
      },
    },
    { provider }
  );
  createK8sIngress({
    name: name,
    twDomain: new TWDomain('triplestack.io', `${name}.jaeger`, 'iap', projectIdAsSubDomain),
    selector: {
      'app.kubernetes.io/name': 'saber-jaeger-query',
    },
    ingressMode: 'iap',
    provider,
    port: 80,
    healthCheckPath: '/',
    targetPort: 16686,
  });
}

export function createKafkaExporter(args: {
  provider: K8sProvider;
  name: string;
  servers: string[];
}) {
  const { name, servers } = args;
  const provider = getK8sProvider({ provider: args.provider, namespace: 'monitoring' });
  const app = `${name}-kafka-exporter`;
  deployToK8s({
    name: app,
    providers: [provider],
    serviceAccount: null,
    createK8sDeploymentArgs: {
      podArgs: {
        image: 'danielqsj/kafka-exporter',
        args: servers.map((server) => `--kafka.server=${server}`),
        CPURequest: '500m',
        memoryRequest: '500Mi',
        allowSpot: false,
      },
      maxReplicas: 1,
    },
    useTcpProbe: true,

    ports: [{ containerPort: 9308, name: 'metrics' }],
    ingresses: null,
  });

  createPrometheusMonitor({
    name: app,
    provider,
    labels: {
      'app.kubernetes.io/name': app,
    },
    kind: 'Pod',
    endpoints: [{ portName: 'metrics', path: '/metrics', scrapeTimeout: '25s', interval: '30s' }],
    release: 'devops',
    namespaced: false,
  });
}

