    {
      apiVersion: 'networking.gke.io/v1',
      kind: 'ManagedCertificate',
      metadata: {
        name,
      },
      spec: {
        domains,
      },
    },
    { provider: provider || getK8sProvider() }
  );
  return k8sManagedCertificate;
}

