export function deployToK8s(args: DeployToK8sArgs): {
  k8sDeployment?: kubernetes.apps.v1.Deployment;
  deployment: ServiceEntryDeployment;
  ingressesResult: ReturnType<typeof k8sDiscovery>[];
} {
  const { serviceId } = getConfigs();
  const {
    createK8sDeploymentArgs,
    ingresses = [{ ingressMode: 'internal', ingressType: 'service' }],
    name = serviceId,
    serviceAccount,
    secretVersion,
    providers = [getK8sProvider()],
    useTcpProbe = false,
    ports = [{ containerPort: 8080 }],
  } = args;
  monitoringState.k8s.enabled = true;
  if (ports?.length) {
    monitoringState.apmHttp.enabled = true;
  }

  const deployment: ServiceEntryDeployment = {
    name: name,
    endpoints: {},
  };

  let k8sDeployment = null;
  const isMain = name == serviceId;
  const sub = isMain ? serviceId : `${name}.${serviceId}`;
  const domain = args.domain || new TWDomain(defaultDomain, sub, 'srv');

  for (const provider of providers) {
    createK8sNamespace({ provider });

    const args: CreateK8sDeploymentArgs = {
      name,
      provider,
      ...createK8sDeploymentArgs,
      podArgs: deepMerge(
        {
          terminationGracePeriodSeconds: 60,
          useTcpProbe,
          ports,
        },
        createK8sDeploymentArgs?.podArgs
      ),
    };

    if (serviceAccount !== null) {
      const k8sServiceAccount = createK8sServiceAccount({
        serviceAccount,
        provider,
      }).k8sServiceAccount;
      if (k8sServiceAccount) {
        args.podArgs.k8sServiceAccountName = k8sServiceAccount.metadata.name;
      }
    }

    if (secretVersion) {
      const k8sSecret = createK8sSecret({ gcpSecretVersion: secretVersion, provider }).k8sSecret;
      args.podArgs.k8sSecret = k8sSecret;
    }

    k8sDeployment = createK8sDeployment(args).k8sDeployment;

    if (isProduction && !createK8sDeploymentArgs?.podArgs?.allowSpot) {
      createPdb({
        name,
        provider,
        labels: {
          'triplewhale.com/deployment': name,
        },
        minAvailable: '90%',
      });
    }
  }

  const autoNegConfigEntries: AutoNegConfigEntry[] = [];
  const ingressesResult: ReturnType<typeof k8sDiscovery>[] = [];
  if (!(ports && !ports.length)) {
    for (const ingress of ingresses || []) {
      const { ingressMode, ingressType, loadBalancerConfig } = ingress;
      const res = k8sDiscovery({
        name,
        deployment,
        ingressMode,
        ingressType,
        provider: providers[0],
        domain,
        loadBalancerConfig,
        useTcpProbe,
      });
      ingressesResult.push(res);
      const { autoNegConfigEntry } = res;
      if (autoNegConfigEntry) {
        autoNegConfigEntries.push(autoNegConfigEntry);
      }
    }

    if (autoNegConfigEntries.length) {
      for (const provider of providers) {
        createK8sService({
          name,
          autoNeg: {
            backend_services: { '80': autoNegConfigEntries },
          },
          provider,
        });
      }
    }

    if (!deployment.endpoints['authenticated']) {
      deployment.endpoints['authenticated'] = {
        type: 'authenticated',
        audience: serviceId,
        url: createAuthProxyUrl(domain.subDomain || serviceId) as any,
      };
    }

    // deployment.endpoints['cluster-local'] = {
    //   type: 'cluster-local',
    //   url: 'http://' + createClusterLocalDnsName(name),
    //   cluster: provider.cluster_urn,
    // };
    addServiceEntryDeployment({ deployment, type: 'k8s' });
  }

  return { k8sDeployment, deployment, ingressesResult };
}

export function deployToKnative(args: DeployToKnativeArgs) {
  const { serviceId } = getConfigs();
  const { ingressMode, name = serviceId, createKnativeServingArgs = {}, serviceAccount } = args;
  const provider = getKnativeProvider();
  monitoringState.k8s.enabled = true;
  monitoringState.apmHttp.enabled = true;
  createK8sNamespace({ provider: provider });

  const k8sServiceAccount = createK8sServiceAccount({
    serviceAccount,
    provider,
  }).k8sServiceAccount;

  const deployment: ServiceEntryDeployment = {
    name: name,
    endpoints: {},
  };

  const isMain = name == serviceId;
  const sub = isMain ? serviceId : `${name}.${serviceId}`;
  const domain = args.domain || new TWDomain(defaultDomain, sub, 'srv');
  const { knativeServing } = createKnativeServing(
    {
      name,
      ...createKnativeServingArgs,
      podArgs: {
        k8sServiceAccountName: k8sServiceAccount.metadata.name,
        ...createKnativeServingArgs.podArgs,
      },
    },
    domain
  );

  if (isProduction) {
    createPdb({
      name,
      provider,
      labels: {
        'triplewhale.com/deployment': name,
      },
      minAvailable: '90%',
    });
  }

  const { dnsRecord: internalDnsRecord } = createKnativeDomainMapping({
    mode: 'internal',
    twDomain: domain,
    provider,
  });
  deployment.endpoints['internal'] = {
    type: 'internal',
    url: dnsUrl(internalDnsRecord) as any,
    cluster: provider.cluster_urn,
  };

  if (ingressMode === 'open') {
    const twDomain = createTWDomain({ twDomain: domain, domainGroup: 'api' });
    const { dnsRecord } = createKnativeDomainMapping({
      mode: ingressMode,
      twDomain,
      provider,
    });
    deployment.endpoints['open'] = { type: 'open', url: dnsUrl(dnsRecord) as any };
  }
  deployment.endpoints['authenticated'] = {
    type: 'authenticated',
    audience: serviceId,
    url: createAuthProxyUrl(domain.subDomain) as any,
  };
  // deployment.endpoints['cluster-local'] = {
  //   type: 'cluster-local',
  //   url: 'http://' + createClusterLocalDnsName(name),
  //   cluster: provider.cluster_urn,
  // };
  addServiceEntryDeployment({ deployment, type: 'k8s' });
  return { deployment, knativeServing };
}

export function createUfDeployment(args: DeployToKnativeArgs) {
  deployToKnative({
    name: 'uf',
    serviceAccount: args.serviceAccount,
    createKnativeServingArgs: {
      timeoutSeconds: 300,
      maxReplicas: 100,
      minReplicas: 1,
      podArgs: {
        memoryRequest: '1Gi',
        CPURequest: '500m',
        nodeSelector: {
          'cloud.google.com/machine-family': 'c2',
        },
      },
      target: 6,
      ...args.createKnativeServingArgs,
    },
  });
}

export function createIapConfig(args?: { provider?: K8sProvider }) {
  const { provider = getK8sProvider() } = args || {};
  const key = `iap-${provider.uuid}`;
  if (iapConfigs[key]) return iapConfigs[key];
  const { clientId, clientSecret } = getIapInfo();
  const iapConfig = new kubernetes.core.v1.Secret(
    key,
    {
      metadata: {
        name: 'iap-config',
      },
      stringData: {
        client_id: clientId,
        client_secret: clientSecret,
      },
    },
    { provider }
  );
  iapConfigs[key] = iapConfig;
  return iapConfig;
}

export function getIapInfo() {
  return {
    clientId: iapClientId,
    clientSecret: getSecretValue('iap-client-secret'),
  };
}

export function createK8sIngress(args: {
  provider: K8sProvider;
  name: string;
  twDomain: TWDomain;
  ingressMode?: IngressMode;
  selector?: Record<string, string>;
  port?: number;
  targetPort?: number;
  healthCheckPath?: string;
  sessionAffinity?: AffinityType;
  dependsOn?: DependsOn;
  rules?: kubernetes.types.input.networking.v1.IngressRule[];
}) {
  const {
    provider,
    name,
    ingressMode = 'internal',
    twDomain,
    selector = { 'triplewhale.com/deployment': name },
    port = 80,
    targetPort = 8080,
    healthCheckPath = '/ping',
    sessionAffinity,
    dependsOn,
    rules,
  } = args;
  const labels = {};

  let ingressName = name;
  if (ingressMode !== 'internal') {
    ingressName += '-' + ingressMode;
  }

  const backendConfigArgs: BackendConfigArgs = {
    healthCheck: {
      checkIntervalSec: 1,
      timeoutSec: 1,
      healthyThreshold: 1,
      unhealthyThreshold: 1,
      type: 'HTTP',
      requestPath: healthCheckPath,
      port: targetPort,
    },
    timeoutSec: 3600,
  };

  if (sessionAffinity) {
    backendConfigArgs.sessionAffinity = {
      affinityCookieTtlSec: 86400,
      affinityType: sessionAffinity,
    };
  }

  if (ingressMode === 'iap') {
    backendConfigArgs.iap = {
      enabled: true,
      oauthclientCredentials: {
        secretName: 'iap-config',
      },
    };
  }

  const backendConfig = createK8sServiceBackendConfig({
    name: `${ingressName}-backend-config`,
    spec: backendConfigArgs,
    provider,
  });

  const { k8sService } = createK8sService({
    ingressMode,
    name: ingressName,
    provider: provider,
    selector,
    ports: [
      {
        name: 'http-web',
        port,
        targetPort,
      },
    ],
    dependsOn,
    backendConfig,
  });

  const host = twDomain.fqdn;

  const annotations = {
    [HOST]: host,
  };

  if (['open', 'iap'].includes(ingressMode)) {
    const frontendConfig = createK8sIngressFrontendConfig({
      name: `${ingressName}-ingress-frontend-config`,
      spec: {
        redirectToHttps: {
          enabled: ingressMode === 'open',
        },
      },
      provider,
    });

    const ssl = createK8sManagedCertificate({
      name: `${ingressName}-managed-ssl-ingress`,
      domains: [host],
      provider,
    });
    annotations['networking.gke.io/managed-certificates'] = ssl.metadata.name;
    annotations['networking.gke.io/v1beta1.FrontendConfig'] = frontendConfig.metadata.name;
  } else {
    annotations['kubernetes.io/ingress.class'] = 'gce-internal';
  }

  if (twDomain) {
    annotations[DOMAIN] = twDomain.fqdn;
    labels[DOMAIN] = getLabelSafeDomain(twDomain);
  }

  const k8sIngress = new kubernetes.networking.v1.Ingress(
    ingressName,
    {
      apiVersion: 'networking.k8s.io/v1',
      metadata: {
        annotations,
        labels: objectBool(labels) ? labels : undefined,
        name: ingressName,
      },
      spec: {
        defaultBackend: {
          service: {
            name: k8sService.metadata.name,
            port: { number: k8sService.spec.ports[0].port },
          },
        },
        rules,
      },
    },
    { provider: provider, dependsOn }
  );

  addDnsRecord({
    name: twDomain.fullSubDomain,
    domainName: twDomain.domain,
    type: 'A',
    value: k8sIngress.status.loadBalancer.ingress[0].ip,
    proxied: ingressMode === 'iap' && false,
  });

  return { k8sIngress, k8sService };
}

