export function createKedaScaler(args: CreateKedaScalerArgs) {
  const {
    name,
    maxReplicas,
    minReplicas,
    averageCPUUtilization,
    provider,
    triggers = [],
    scaleToZero = false,
  } = args;

  const keda = new kubernetes.apiextensions.CustomResource(
    // https://keda.sh/docs/2.14/concepts/scaling-deployments/#scaledobject-spec
    k8sUniqueName(name, provider),
    {
      apiVersion: 'keda.sh/v1alpha1',
      kind: 'ScaledObject',
      metadata: {
        name,
        annotations: {
          'scaledobject.keda.sh/transfer-hpa-ownership': 'true',
        },
      },
      spec: {
        scaleTargetRef: {
          name,
        },
        pollingInterval: 1,
        cooldownPeriod: 10,
        idleReplicaCount: scaleToZero ? 0 : undefined,
        minReplicaCount: minReplicas,
        maxReplicaCount: maxReplicas,
        triggers: [
          {
            type: 'cpu',
            metricType: 'Utilization',
            metadata: {
              value: String(averageCPUUtilization),
            },
          },
          ...triggers,
        ],
        advanced: {
          horizontalPodAutoscalerConfig: {
            name,
            behavior: {
              scaleUp: {
                policies: [
                  {
                    type: 'Pods',
                    value: 1000,
                    periodSeconds: 1,
                  },
                  {
                    type: 'Percent',
                    value: 1000,
                    periodSeconds: 1,
                  },
                ],
              },
            },
          },
        },
      },
    },
    { provider: provider, deleteBeforeReplace: true }
  );
  return keda;
}

export function createK8sNamespace(args: {
  provider: K8sProvider;
  name?: string;
  labels?: Record<string, string>;
}): kubernetes.core.v1.Namespace {
  const { labels } = args;

  const aliases = [];

  let name = args.name;
  let provider = args.provider;
  if (!name) {
    name = getNamespace(provider);
  } else if (provider) {
    aliases.push({ name: `${name}-${provider.cluster_uuid}` });
    aliases.push({ name: `namespace-${provider.uuid}` });
    provider = getK8sProvider({ provider, namespace: name });
  }
  const clusterKey = `${name}-${provider.cluster_uuid}`;

  if (_k8sNamespaces[clusterKey]) return _k8sNamespaces[clusterKey];
  const namespace = new kubernetes.core.v1.Namespace(
    clusterKey,
    {
      metadata: {
        name: name,
        labels: {
          ...labels,
        },
      },
    },
    {
      provider: provider,
      aliases,
    }
  );
  _k8sNamespaces[clusterKey] = namespace;
  return namespace;
}

