export function renderToYaml(args: pulumi.ResourceTransformationArgs) {
  if (!process.env.IS_CLOUD_BUILD) return;
  const { props, opts, name } = args;
  const provider = opts?.provider as K8sProvider;
  // its a get
  if (!props.kind) return;
  if (!props.metadata) props.metadata = {};
  const filename = `${props.apiVersion}-${props.kind}-${props.metadata.name || name}.yaml`
    .replace('/', '-')
    .toLowerCase();
  const dir = './.yaml';
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir);
  }
  if (!props.metadata?.namespace) {
    props.metadata.namespace = provider.namespace;
  }
  toYamlOutput(props).apply((o) => {
    fs.writeFileSync(`${dir}/${filename}`, o);
  });
}

export function createK8sServiceAccount(args?: {
  provider?: K8sProvider;
  serviceAccount?: GCPServiceAccount;
  name?: string;
}): { k8sServiceAccount: kubernetes.core.v1.ServiceAccount } {
  const { serviceId } = getConfigs();
  const {
    provider = getK8sProvider(),
    serviceAccount = k8sGetGCPServiceAccount(),
    name = serviceId,
  } = args || {};
  if (!serviceAccount) return;
  const key = `${name}-${provider.uuid}`;
  let k8sServiceAccount;
  if (!serviceAccounts[key]) {
    k8sServiceAccount = new kubernetes.core.v1.ServiceAccount(
      key,
      {
        apiVersion: 'v1',
        kind: 'ServiceAccount',
        metadata: {
          name: name,
          annotations: {
            'iam.gke.io/gcp-service-account': serviceAccount.email,
          },
        },
      },
      { provider: provider }
    );

    addWorkloadIdentityUserToSa({
      serviceAccount,
      provider,
      name,
    });
    serviceAccounts[key] = k8sServiceAccount;
  } else {
    k8sServiceAccount = serviceAccounts[key];
  }
  return { k8sServiceAccount };
}

