export function getKafkaProvider(name: string = 'sonic-cluster') {
  if (!kafkaProviders[name]) {
    const secret = getSecretValue(`${name}-kafka`).apply(JSON.parse);
    const domain = secret.apply((s) => s.bootstrapDomain + ':9092');
    kafkaProviders[name] = new kafka.Provider(name, {
      bootstrapServers: [domain],
      tlsEnabled: false,
    });
  }
  return kafkaProviders[name];
}

export function createKafkaTopic(args: {
  name: string;
  provider?: kafka.Provider;
  partitions?: number;
  replicationFactor?: number;
}) {
  const { name, provider = getKafkaProvider(), partitions = 20, replicationFactor = 1 } = args;
  const topic = new kafka.Topic(
    name,
    {
      name,
      partitions,
      replicationFactor,
      config: {
        'retention.ms': '604800000',
        'max.message.bytes': 15 * 1024 * 1024,
      },
    },
    { provider, deleteBeforeReplace: true }
  );
  return { topic };
}

