export function createMongoDbUser() {
  const { serviceId, projectId } = getConfigs();
  const globalInfraRef = getStackReference(`infra`, projectId);
  const userPassword = createPassword({ name: 'mongo-db-user-password', special: false });

  const mongoDbUser = new mongodbatlas.DatabaseUser(
    'mongo-db-user',
    {
      projectId: globalInfraRef.getOutput('mongoDBProjectId'),
      roles: [{ roleName: 'readWriteAnyDatabase', databaseName: 'admin' }],
      username: `${serviceId}-user`,
      authDatabaseName: 'admin',
      password: userPassword.result,
    },
    {
      provider: getMongoAtlasProvider(),
    }
  );
  return mongoDbUser;
}

export function getMongoAtlasProvider() {
  if (!mongoAtlasProvider) {
    mongoAtlasProvider = new mongodbatlas.Provider('mongodbatlas', {
      privateKey: getSecretValue('mongodb-atlas-private-key'),
      publicKey: getSecretValue('mongodb-atlas-public-key'),
    });
  }
  return mongoAtlasProvider;
}

export function getRelativeTier(tier: AtlasTier, jump) {
  const ind = atlasTiers.indexOf(tier);
  return atlasTiers[ind + jump];
}

export function getMongoDbConnectionString(
  mongoDbUser,
  additionalOptions: {
    cluster?: mongodbatlas.Cluster;
    defaultDb?: string;
    privateLink?: boolean;
    clusterName?: string;
  } = {}
) {
  const { serviceId, projectId } = getConfigs();
  const { defaultDb = serviceId, privateLink = true, cluster } = additionalOptions;
  let clusterName = additionalOptions.clusterName || cluster?.name || 'shared-cluster';
  monitoringState.mongo.enabled = true;
  monitoringState.mongo.resourceNames.push(clusterName);
  let mongoSrvAddressId;
  if (cluster) {
    mongoSrvAddressId = cluster?.srvAddress?.apply((i) => i?.split('.')?.[1]);
  } else {
    const globalInfraRef = getStackReference(`infra`, projectId);
    mongoSrvAddressId = globalInfraRef.getOutput('mongoSrvAddressId');
  }
  const auth = pulumi.interpolate`${mongoDbUser.username}:${mongoDbUser.password}`;
  clusterName = privateLink ? pulumi.interpolate`${clusterName}-pl-0` : clusterName;
  const result = pulumi.interpolate`mongodb+srv://${auth}@${clusterName}.${mongoSrvAddressId}.mongodb.net/${defaultDb}`;
  return result;
}

