export function createDefaultPubsubAlerts(excludePubsubSubs: gcp.pubsub.Subscription[]) {
  const { serviceId } = getConfigs();
  let filter: any = `resource.type = "pubsub_subscription" AND metric.type = "pubsub.googleapis.com/subscription/oldest_unacked_message_age" AND metadata.user_labels.service-id = "${serviceId}"`;
  if (excludePubsubSubs.length) {
    filter = pulumi.interpolate`${filter} AND ${pulumi
      .all(excludePubsubSubs.map((v) => v.name))
      .apply((subIds) => subIds.map((v) => `resource.labels.subscription_id != "${v}"`))}`;
  }
  createAlert({
    name: 'default-pubsub-alert',
    displayName: `Pubsub ${serviceId} oldest unacked`,
    slack: true,
    conditions: [
      {
        displayName: 'Cloud Pub/Sub Subscription - Oldest unacked message age',
        conditionThreshold: {
          aggregations: [
            {
              alignmentPeriod: '300s',
              perSeriesAligner: 'ALIGN_MEAN',
            },
          ],
          comparison: 'COMPARISON_GT',
          duration: '0s',
          filter,
          thresholdValue: 3600,
          trigger: {
            count: 1,
          },
        },
      },
    ],
  });
}

export function createPushConfig(endpoint: string, service: serviceTarget) {
  monitoringState.pubsubPush.enabled = true;
  const query = new URL(endpoint, 'http://localhost');

  if (!query.searchParams.get('isPubsub')) {
    query.searchParams.set('isPubsub', 'true');
    endpoint = query.pathname.replace('/', '') + query.search;
  }

  return {
    pushEndpoint: getFullUrl(service, endpoint),
    oidcToken: {
      audience: getAudience(service),
      serviceAccountEmail: getServiceAccountForService(),
    },
  };
}

export function createDeadLetterPolicy(args?: {
  maxDeliveryAttempts?: number;
  deadLetterTopic?: pulumi.Input<string>;
}): gcp.types.input.pubsub.SubscriptionDeadLetterPolicy {
  const { maxDeliveryAttempts = 5, deadLetterTopic = 'message-graveyard' } = args || {};
  return {
    maxDeliveryAttempts,
    deadLetterTopic: getPubsubTopicId(deadLetterTopic),
  };
}

export function localPullSub(args: {
  serviceId?: string;
  topicName?: pulumi.Output<string> | string;
  subscriptionName?: string;
  projectIds?: string[];
}) {
  const { projectId } = getConfigs();
  const { serviceId, subscriptionName } = args;
  const projectIds = args.projectIds || ['triple-whale-staging'];
  const topicName = args.topicName || 'local-webhooks-topic';
  if (projectIds?.length > 0 && !projectIds.includes(projectId)) return;

  const subOptions: gcp.pubsub.SubscriptionArgs = {
    name: subscriptionName,
    topic: topicName,
    messageRetentionDuration: '600s',
  };
  if (serviceId) {
    subOptions.filter = `attributes.serviceId="${serviceId}"`;
  }

  return new gcp.pubsub.Subscription(`${subscriptionName}-sub`, subOptions);
}

