export function createSecret(
  secretValue: pulumi.Input<Record<string, pulumi.Input<string | number | any[]>>>,
  name?: string
) {
  const { serviceId } = getConfigs();
  name = (getUniqueNameInProject(name) || `${getUniqueNameInProject(serviceId)}-env`) as string;
  const secret = new gcp.secretmanager.Secret(
    name,
    {
      secretId: name,
      replication: {
        auto: {},
      },
    },
    {
      aliases: [{ name: 'secret' }],
    }
  );

  const secretVersion = new gcp.secretmanager.SecretVersion(
    name,
    {
      secret: secret.id,
      secretData: toJSONOutput(secretValue),
    },
    { retainOnDelete: true, aliases: [{ name: 'secret-version' }] }
  );

  return { secret, secretVersion };
}

export function getSecretValue(secret: pulumi.Output<string> | string) {
  return gcp.secretmanager.getSecretVersionOutput({ secret }).secretData;
}

export function createPassword(args: {
  name: string;
  special?: boolean;
  overrideSpecial?: string;
}) {
  const { name, special = true, overrideSpecial = `!#%&*()-_=+[]{}<>:?` } = args;
  const { config } = getConfigs();
  const password = new RandomPassword(name, {
    length: 16,
    special,
    overrideSpecial,
    keepers: { rotate: config.get('rotatePassword') || 'false' },
  });
  return password;
}

