export function loadPackageJson() {
  try {
    const packageJson = JSON.parse(fs.readFileSync(resolve('../package.json')).toString());
    return packageJson;
  } catch {
    return { name: '', version: '' };
  }
}

export function loadServiceConfig(): ServiceConfig {
  if (serviceConfig) return serviceConfig;
  try {
    serviceConfig = JSON.parse(fs.readFileSync(resolve(serviceConfigFile)).toString());
  } catch {
    // local prob
    serviceConfig = { color: 'none', contacts: [], maintainers: [] };
  }
  return serviceConfig;
}

export function updateServiceConfig(serviceConfig: ServiceConfig) {
  pulumi.output(serviceConfig).apply((o) => {
    fs.writeFileSync(resolve(serviceConfigFile), JSON.stringify(o, null, 2) + '\n');
  });
}

export function updateServiceEntry(args: { serviceConfig: ServiceConfig; isK8s; cloudBuild }) {
  const { serviceConfig, isK8s, cloudBuild } = args;
  const k8s = isK8s ?? objectBool(cloudBuild.k8s);
  const { projectId, serviceId, isMultiPerProject } = getConfigs();
  const serviceEntry: ServiceConfig = {
    ...serviceConfig,
    serviceId,
    gitRepo: pulumi.output(getGitRepoName()) as any,
    k8s,
    version: 2.0,
  };

  updateServiceConfig({
    ...serviceEntry,
    ...(isProduction ? { deployments: getDeployments(k8s ? 'k8s' : 'cloud-run') } : null),
  });

  serviceEntry.deployments = getDeployments(k8s ? 'k8s' : 'cloud-run');
  new gcp.storage.BucketObject(
    `service-config`,
    {
      bucket: `devops-${projectId}`,
      content: toJSONOutput(serviceEntry),
      name: `service-config/${getUniqueNameInProject(serviceId, '_')}.json`,
    },
    {
      retainOnDelete: true,
    }
  );
}

export function addServiceEntryDeployment(args: {
  deployment: ServiceEntryDeployment;
  type: DeploymentType;
}) {
  const { deployment, type } = args;
  if (!deployments[type]) deployments[type] = {};
  deployments[type][deployment.name] = deployment;
}

export function getDeployments(type: DeploymentType): Record<string, ServiceEntryDeployment> {
  return {
    ...Object.keys(deployments).reduce((acc, key) => {
      return {
        ...acc,
        ...deployments[key],
      };
    }, {}),
    ...deployments[type],
  };
}

export function createServiceEnvs(envs: Record<string, pulumi.Input<string>>): ServiceEnvs {
  serviceEnvs = envs;
  return new ServiceEnvs('service-envs', { name: 'service-envs', envs });
}

export function getServiceDefaultEnvs(): {
  name: string;
  value: any;
}[] {
  const envs = [
    { name: 'STACK_NAME', value: getConfigs().stack },
    { name: 'PULUMI_STACK_NAME', value: getConfigs().stack },
    { name: 'PULUMI_PROJECT_NAME', value: getConfigs().serviceId },
    {
      name: 'TW_VERSION',
      value: getBuildVersion(),
    },
  ];
  if (process.env.LOG_LEVEL) {
    envs.push({ name: 'LOG_LEVEL', value: process.env.LOG_LEVEL });
  }
  return envs;
}

export function serviceInfraConfig(
  args: {
    apiGateway?: { service: serviceTarget; removePrefix?: boolean };
    cloudBuild?: Partial<CreateBuildTriggerArgs>;
    isK8s?: boolean;
    alerts?: { excludePubsubSubs: gcp.pubsub.Subscription[] };
  } = {}
) {
  const { apiGateway, cloudBuild = {}, isK8s, alerts } = args;
  const { isMultiPerProject } = getConfigs();
  if (!fs.existsSync(serviceConfigFile)) return;
  const serviceConfig = loadServiceConfig();

  if (!cloudBuild?.k8s?.clusters && objectBool(k8sClusters)) {
    if (!cloudBuild?.k8s) cloudBuild.k8s = {};
    cloudBuild.k8s.clusters = Object.values(k8sClusters).map(({ name, location }) => {
      return { name, location: location };
    });
  }

  if (objectBool(k8sClusters)) {
    deployedToK8s = true;
  }

  if (cloudBuild.k8s?.clusters && !cloudBuild.workerPool) {
    cloudBuild.workerPool = 'app-pool';
    if (cloudBuild.machineType) {
      cloudBuild.workerPool = `${cloudBuild.workerPool}-${cloudBuild.machineType
        .toLowerCase()
        .replaceAll('_', '-')}`;
    }
  }

  // api config
  if (apiGateway?.service && fs.existsSync(openApiFile)) {
    uploadAPIConfig(openApiFile, apiGateway.service, {
      removePrefix: apiGateway?.removePrefix,
    });
  }

  // cloud build
  createBuildTrigger({ runtime: serviceConfig.runtime || 'node', ...cloudBuild });

  // dashboard
  createDefaultServiceDashboards();

  // upload serviceEntry
  updateServiceEntry({ serviceConfig, isK8s, cloudBuild });

  if (isProduction) {
    const datadogDashboard = createDefaultDatadogDashboard();

    createDefaultDatadogMonitors({
      datadogDashboard,
      excludePubsubSubs: alerts?.excludePubsubSubs,
    });

    createDatadogService({ serviceConfig, datadogDashboard });
  }
}

