export function createSaberK8sPodArgs(args: SaberArgs) {
  monitoringState.saber.enabled = true;
  const cmdArgs = args.saberPipelines?.map((v) => `--pipeline=${v}`) || [];
  if (args.main) {
    cmdArgs.push(`--main=${args.main}`);
  }
  const podArgs: PodTemplateArgs = {
    allowSpot: true,
    memoryRequest: '2Gi',
    CPURequest: 1,
    args: cmdArgs.length ? cmdArgs : undefined,
    ports: [],
    envs: convertEnvs({
      IS_SABER: '1',
      TW_ENTRY: 'node_modules/@tw/saber/module/saber.js',
      SABER_MAX_PARALLEL: args.concurrencyLimit ? String(args.concurrencyLimit) : undefined,
      LOG_THROTTLE: JSON.stringify({
        maxPerSecond: 50,
        maxBytesPerSecond: 1024 * 50, // 50KB
      }),
      // 10 minutes
      TW_OUTBOUND_REQUEST_TIMEOUT_MS: '600000',
    }),
    otel: true,
  };
  return podArgs;
}

export function deploySaber(args: DeployToK8sArgs & SaberArgs) {
  args.concurrencyLimit = args.concurrencyLimit || 5000;
  args.name = args.name || 'saber';
  const provider = getK8sProvider({ cluster: 'pipelines-cluster' });
  const { serviceId } = getConfigs();
  args.concurrencyTarget = args.concurrencyTarget ?? Number((args.concurrencyLimit / 5).toFixed(2));
  const kedaTriggers = args.createK8sDeploymentArgs?.kedaTriggers || [];
  if (args.concurrencyTarget) {
    kedaTriggers.push(
      createKedaPrometheusTrigger({
        threshold: String(args.concurrencyTarget),
        query: `sum by(service) (observability_saber_budget_usage_size{service="${serviceId}",triplewhale_com_deployment="${args.name}"})`,
      })
    );
  }
  const base: Partial<DeployToK8sArgs> = {
    ingresses: null,
    ports: [],
    providers: [provider],
    createK8sDeploymentArgs: {
      maxReplicas: 80,
      averageCPUUtilization: 65,
      scaleDownPolicies: [{ periodSeconds: 1, type: 'Percent', value: 1000 }],
      kedaTriggers,
      useKeda: true,
      podArgs: createSaberK8sPodArgs(args),
      datadogConfig: {
        profiling: true,
        sampleRate: 0.1,
      },
    },
  };

  const opts = deepMerge(base, args);
  return deployToK8s(opts);
}

export function createSaberK8sJobConfig(args: K8sJobArgs & SaberArgs) {
  const base: Partial<K8sJobArgs> = {
    podArgs: createSaberK8sPodArgs(args),
    name: 'saber',
  };
  const opts = deepMerge(base, args) as K8sJobArgs;
  return createK8sJobConfig({
    ...opts,
    provider: getK8sProvider({ cluster: 'pipelines-cluster' }),
  });
}

export function createDefaultSqlAlerts() {
  if (!monitoringState.sql.enabled) return;
  const { serviceId } = getConfigs();
  createAlert({
    name: 'default-sql-cpu-alert',
    displayName: `SQL ${serviceId} - CPU Utilization`,
    slack: true,
    conditions: [
      {
        displayName: 'Cloud SQL Database - CPU utilization',
        conditionThreshold: {
          filter: `resource.type = "cloudsql_database" AND metric.type = "cloudsql.googleapis.com/database/cpu/utilization" AND metadata.user_labels.service-id = "${serviceId}"`,
          aggregations: [
            {
              alignmentPeriod: '300s',
              crossSeriesReducer: 'REDUCE_NONE',
              perSeriesAligner: 'ALIGN_MEAN',
            },
          ],
          comparison: 'COMPARISON_GT',
          duration: '0s',
          trigger: {
            count: 1,
          },
          thresholdValue: 0.5,
        },
      },
    ],
  });

  createAlert({
    name: 'default-sql-memory-alert',
    displayName: `SQL ${serviceId} - Memory utilization`,
    slack: true,
    conditions: [
      {
        displayName: 'Cloud SQL Database - Memory utilization',
        conditionThreshold: {
          filter: `resource.type = "cloudsql_database" AND metric.type = "cloudsql.googleapis.com/database/memory/utilization" AND metadata.user_labels.service-id = "${serviceId}"`,
          aggregations: [
            {
              alignmentPeriod: '300s',
              crossSeriesReducer: 'REDUCE_NONE',
              perSeriesAligner: 'ALIGN_MEAN',
            },
          ],
          comparison: 'COMPARISON_GT',
          duration: '0s',
          trigger: {
            count: 1,
          },
          thresholdValue: 0.5,
        },
      },
    ],
  });
}

export type SqlDatabaseInstance = gcp.sql.DatabaseInstance & {
  instance_name: string;
};

export function createPostgresInstance(args: {
  cpu?: number;
  memoryGb: number;
  nonProdRatio?: number;
  name?: string;
  enableInsights?: boolean;
  maxConnections?: string;
  version?: '13' | '14';
  availabilityType?: 'ZONAL' | 'REGIONAL';
  pointInTimeRecoveryEnabled?: boolean;
  databaseFlags?: gcp.types.input.sql.DatabaseInstanceSettingsDatabaseFlag[];
  backup?: boolean;
  retainedBackups?: number;
  databaseInstanceArgs?: Partial<gcp.sql.DatabaseInstanceArgs>;
  monitor?: boolean;
}) {
  const { serviceId, location } = getConfigs();
  const {
    cpu,
    memoryGb,
    nonProdRatio = 4,
    enableInsights,
    maxConnections,
    version = '14',
    databaseFlags = [],
    backup = isProduction,
    retainedBackups = 7,
    monitor = false,
  } = args;
  const availabilityType = isProduction ? args.availabilityType || 'ZONAL' : 'ZONAL';
  const pointInTimeRecoveryEnabled =
    args.pointInTimeRecoveryEnabled || availabilityType === 'REGIONAL';
  let tier = `db-custom-${cpu}-${memoryGb * MB}`;
  if (maxConnections) {
    databaseFlags.push({ name: 'max_connections', value: maxConnections });
  }

  databaseFlags.push({ name: 'track_activity_query_size', value: '4096' });
  databaseFlags.push({ name: 'pg_stat_statements.track', value: 'all' });
  databaseFlags.push({ name: 'pg_stat_statements.max', value: '10000' });
  databaseFlags.push({ name: 'pg_stat_statements.track_utility', value: 'off' });
  databaseFlags.push({ name: 'track_io_timing', value: 'on' });

  if (!isProduction) {
    // cpu must be 1 or an even number less than or equal to 96.
    let cpus = Math.round(Math.max(cpu / nonProdRatio, 1));
    cpus = cpus % 2 === 0 || cpus === 1 ? cpus : cpus + 1;
    tier = `db-custom-${cpus}-${Math.max(memoryGb / nonProdRatio, 3.75) * MB}`;
  }
  const name = getUniqueNameInProject(args.name || `${serviceId}-instance`) as string;
  const sqlInstance = new gcp.sql.DatabaseInstance(
    name as string,
    {
      databaseVersion: `POSTGRES_${version}`,
      name,
      settings: {
        deletionProtectionEnabled: true,
        userLabels: { ...createLabels() },
        tier,
        edition: 'ENTERPRISE',
        backupConfiguration: backup
          ? {
              backupRetentionSettings: { retainedBackups, retentionUnit: 'COUNT' },
              binaryLogEnabled: false,
              enabled: true,
              location: 'us',
              pointInTimeRecoveryEnabled,
              startTime: '21:00',
              transactionLogRetentionDays: 7,
            }
          : null,
        ipConfiguration: {
          ipv4Enabled: false,
          privateNetwork: getNetworkId('app'),
        },
        availabilityType,
        databaseFlags,
        insightsConfig: enableInsights
          ? {
              queryInsightsEnabled: true,
              recordApplicationTags: true,
            }
          : null,
      },
      region: location,
      deletionProtection: true,
      ...args.databaseInstanceArgs,
    },
    { aliases: [{ name: 'sql-instance' }], protect: true }
  ) as SqlDatabaseInstance;
  sqlInstance.instance_name = name;
  // @ts-ignore
  // TODO: this removes to dependency on the instance for anyone using this field
  monitoringState.sql.enabled = true;
  if (monitor) createDatadogSqlMonitor({ instance: sqlInstance, name });
  return sqlInstance;
}

