export function createStorageBucket(name: string, opts: Partial<gcp.storage.BucketArgs> = {}) {
  return new gcp.storage.Bucket(name, {
    location: getConfigs().location,
    versioning: {
      enabled: true,
    },
    ...opts,
  });
}

export function createDataLakeAndNotification(bucketName: string, location: string) {
  const dataLake = new gcp.storage.Bucket(bucketName, {
    location: location,
    name: bucketName,
  });

  const onFinalizeTopic = new gcp.pubsub.Topic(`${bucketName}-object-on-finalize`, {
    name: pulumi.interpolate`${dataLake.name}-object-on-finalize`,
  });

  createStorageNotification(bucketName, onFinalizeTopic);

  return {
    dataLake,
    onFinalizeTopic,
  };
}

export function createStorageNotification(
  bucketName: string,
  topic: gcp.pubsub.Topic,
  objectNamePrefix?: string
) {
  let gcsAccount;
  if (!process.env.IS_LOCAL) {
    gcsAccount = gcp.storage.getProjectServiceAccount({});
  }

  const topicIAMBindingName = objectNamePrefix? `${bucketName}-${objectNamePrefix}-binding` : `${bucketName}-binding`;
  const binding = new gcp.pubsub.TopicIAMBinding(topicIAMBindingName, {
    topic: topic.id,
    role: 'roles/pubsub.publisher',
    members: [
      gcsAccount
        ? gcsAccount.then((gcsAccount) => `serviceAccount:${gcsAccount.emailAddress}`)
        : 'u@d.io',
    ],
  });

  const notificationOptions: gcp.storage.NotificationArgs = {
    bucket: bucketName,
    payloadFormat: 'JSON_API_V1',
    topic: topic.id,
    eventTypes: ['OBJECT_FINALIZE'],
  };
  if (objectNamePrefix) {
    notificationOptions.objectNamePrefix = objectNamePrefix;
  }
  const notificationName = objectNamePrefix? `${bucketName}-${objectNamePrefix}-datalake-notification` : `${bucketName}-datalake-notification`;
  new gcp.storage.Notification(notificationName, notificationOptions, {
    dependsOn: [binding],
  });
}

export function uploadDir(args: { bucket: string; dir: string }) {
  const { bucket, dir } = args;
  const objects = [];
  for (const file of fs.readdirSync(dir)) {
    const object = new gcp.storage.BucketObject(`dir-${file}`, {
      name: file,
      bucket,
      source: `${dir}/${file}`,
    });
    objects.push(object);
  }
  return objects;
}

export function deployTemporalWorker(args: Omit<DeployToK8sArgs, 'providers'>) {
  monitoringState.temporal.enabled = true;
  const base: Partial<DeployToK8sArgs> = {
    createK8sDeploymentArgs: {
      podArgs: {
        labels: {
          'triplewhale.com/worker': 'temporal',
        },
        allowSpot: true,
        memoryRequest: '1Gi',
        otel: true,
      },
      averageMemoryUtilization: 80,
      averageCPUUtilization: 75,
      envs: {
        IS_TEMPORAL_WORKER: 'true',
        FETCHER_MAINTAINERS_EMAILS: getAlertEmails().toString(),
      },
    },
    providers: [getK8sProvider({ cluster: 'temporal-cluster' })],
  };

  const opts = deepMerge(base, args);

  return deployToK8s(opts);
}

