export function getFQDN(subDomain: string, domain: string) {
  if (!subDomain) {
    return domain;
  }
  return `${subDomain}.${domain}`;
}

export function getLabelSafeDomain(twDomain: TWDomain) {
  let labelSafeDomain = twDomain.fqdn;
  if (labelSafeDomain.length > 63) {
    labelSafeDomain = labelSafeDomain.slice(0, 63);
    if (labelSafeDomain.endsWith('.')) {
      labelSafeDomain = labelSafeDomain.slice(0, 62);
    }
  }
  return labelSafeDomain;
}

export function sortEnvs(
  envs: { name: pulumi.Input<string>; value?: pulumi.Input<string>; valueFrom?: any }[]
) {
  // sort and remove empty values, and duplicate values
  return envs
    .filter(
      (env) =>
        (env.value !== '' &&
          env.value !== undefined &&
          env.value !== null &&
          env.value !== 'undefined') ||
        env.valueFrom
    )
    .filter((env, index, self) => {
      const firstIndex = self.findIndex((e) => e.name === env.name);
      return firstIndex === index;
    })
    .sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      return 1;
    });
}

export function addEnvIfNotExists(
  envs: kubernetes.types.input.core.v1.EnvVar[],
  env: kubernetes.types.input.core.v1.EnvVar
) {
  if (
    !envs.find((e) => {
      e.name === env.name;
    })
  ) {
    envs.push(env);
  }
}

export function exportTopicToBigquery(args: {
  topicName: pulumi.Input<string>;
  name: string;
  tableArgs?: Partial<gcp.bigquery.TableArgs>;
  datasetId?: string;
  useTopicSchema?: boolean;
  customSchema?: any[];
  protectTable?: boolean;
  deployToStg?: boolean;
}) {
  const { projectId, serviceId } = getConfigs();
  const {
    topicName,
    name,
    tableArgs,
    customSchema = [],
    datasetId = 'etl_staging',
    useTopicSchema = false,
    protectTable = true,
    deployToStg = false,
  } = args || {};

  if (isStaging && !deployToStg) return;

  const schema = [...bigqueryBaseSchema, ...customSchema];

  const bigQueryTable = new gcp.bigquery.Table(
    `${name}-bq-table`,
    {
      datasetId,
      tableId: name,
      timePartitioning: {
        type: 'HOUR',
        field: 'publish_time',
        expirationMs: 12960000000,
      },
      labels: {
        ...createLabels(),
      },
      schema: JSON.stringify(schema),
      ...tableArgs,
    },
    { protect: protectTable }
  );

  new gcp.pubsub.Subscription(`${name}-bq-export-sub`, {
    topic: topicName,
    bigqueryConfig: {
      table: pulumi.interpolate`${projectId}:${bigQueryTable.datasetId}.${bigQueryTable.tableId}`,
      dropUnknownFields: true,
      useTopicSchema,
      writeMetadata: true,
    },
    deadLetterPolicy: {
      deadLetterTopic: getPubsubTopicId('message-graveyard'),
      maxDeliveryAttempts: 10,
    },
  });

  return { bigQueryTable };
}

