export function generateEndpoint(endpoint: string, params: EndpointParams = {}): string {
  if (objectBool(params)) {
    endpoint += '?';
    for (const [key, value] of Object.entries(params)) {
      if (value !== undefined) {
        endpoint += `${key}=${encodeURIComponent(value as string)}&`;
      }
    }
    endpoint = endpoint.slice(0, -1); // remove trailing '&' character
  }
  return endpoint;
}

export async function getAllServices() {
  const { projectId } = getConfigs();

  const services = JSON.parse(
    (
      await gcp.storage.getBucketObjectContent({
        bucket: `devops-${projectId}`,
        name: `cloud-run-services/services.json`,
      })
    ).content
  );
  const servicesNoMods = {};
  Object.keys(services[projectId]).map((serviceId) => {
    if (services[projectId][serviceId]?.metadata?.labels?.['service-id'] === serviceId) {
      servicesNoMods[serviceId] = services[projectId][serviceId];
    }
  });
  return servicesNoMods;
}

export function getBaseUrl(service: serviceTarget): pulumi.Output<string> {
  const { serviceId } = getConfigs();
  if (isLocal) {
    return pulumi.interpolate`http://localhost/${serviceId}`;
  } else {
    if (service instanceof CloudRun) {
      return getCloudRunUrl(service);
    } else if (service instanceof K8sIngress) {
      // most likely iap, but might just be a open ingress.
      return getIngressUrl(service);
    } else if (service instanceof K8sService) {
      // internal pass through lb using k8s service.
      return getIngressUrl(service);
    } else if (objectBool(service)) {
      // service entry deployment
      service = service as ServiceEntryDeployment;
      if (service.endpoints.authenticated) {
        return pulumi.output(service.endpoints.authenticated.url);
      }
      return pulumi.output(service.endpoints.open.url);
    }
    if (service === null) {
      throw Error('service is set to null, this is prob a mistake.');
    }
  }
}

export function getFullUrl(
  service: serviceTarget,
  endpoint: pulumi.Input<string>
): pulumi.Output<string> {
  const baseUrl = getBaseUrl(service);
  return pulumi.interpolate`${baseUrl}/${endpoint}`;
}

export function getTriggerUrl(triggerId: pulumi.Input<string>): pulumi.Output<string> {
  const { projectId } = getConfigs();
  return pulumi.interpolate`https://cloudbuild.googleapis.com/v1/projects/${projectId}/triggers/${triggerId}:run`;
}

export function getAudience(service: serviceTarget) {
  const { serviceId } = getConfigs();
  if (isLocal) {
    return pulumi.interpolate`http://localhost/${serviceId}`;
  }
  if (service instanceof gcp.cloudrun.Service) {
    return getCloudRunUrl(service);
  } else if (service instanceof K8sIngress) {
    return iapClientId;
  }
  // auth-proxy
  return (service as ServiceEntryDeployment).endpoints.authenticated.audience;
}

export function createAuthProxyUrl(subDomain: string, canonicalForProject = false) {
  const { projectId } = getConfigs();
  const authProxySubDomain = getProjectSubDomain(projectId);
  const serviceName = canonicalForProject ? subDomain : getUniqueNameInProject(subDomain, '_');
  return `https://${authProxySubDomain}auth-proxy.api.whale3.io/${serviceName}`;
}

export function getConfigs() {
  if (cache) return cache;
  const serviceId = pulumi.getProject();
  const config = new pulumi.Config();
  const stack = pulumi.getStack();
  const projectNumber = gcp.organizations.getProjectOutput({}).number;
  const gcpConfig = new pulumi.Config('gcp');
  const location = config.require('gcpLocation');
  const projectId = gcpConfig.require('project');
  const isMultiPerProject = stack !== projectId;
  const isAService =
    config.get('notAService') === 'false' ||
    (config.get('notAService') !== 'true' && fs.existsSync(serviceConfigFile));
  const serviceConfig = loadServiceConfig();
  const isSensory = Boolean(serviceConfig?.sensory);
  cache = {
    projectId,
    serviceId,
    location,
    config,
    gcpConfig,
    stack,
    isMultiPerProject,
    projectNumber,
    isAService,
    serviceConfig,
    isSensory,
  };
  return cache;
}

export function getPubsubTopicId(topicName: pulumi.Input<string>) {
  const { projectId } = getConfigs();
  return pulumi.interpolate`projects/${projectId}/topics/${topicName}`;
}

export function getSecretId(secretName: string) {
  const { projectId } = getConfigs();
  return `projects/${projectId}/secrets/${secretName}/versions/latest`;
}

export function getServiceAccountId(ServiceAccountName: string) {
  const { projectId } = getConfigs();
  return `projects/${projectId}/serviceAccounts/${ServiceAccountName}`;
}

export function getCloudRunServiceId(serviceName: string) {
  const { projectId, location } = getConfigs();
  return `locations/${location}/namespaces/${projectId}/services/${serviceName}`;
}

// passing projectId is temporarily necessary because of the way dataform repos set up in triplewhale-dataland-ops
export function getDataformRepoId(projectId: string, repoName: string) {
  const { location } = getConfigs();
  return `projects/${projectId}/locations/${location}/repositories/${repoName}`;
}

export function getWorkflowId(workflowName: pulumi.Input<string>) {
  const { location, projectId } = getConfigs();
  return pulumi.interpolate`projects/${projectId}/locations/${location}/workflows/${workflowName}`;
}

export function getNetworkId(networkName: string) {
  const { projectId } = getConfigs();
  return `projects/${projectId}/global/networks/${networkName}`;
}

export function getSubnetworkId(subnetworkName: string, location: string) {
  const { projectId } = getConfigs();
  return `projects/${projectId}/regions/${location}/subnetworks/${subnetworkName}`;
}

export function getServiceAccountForService() {
  const serviceAccountEmail = k8sGetGCPServiceAccount()?.email;
  if (serviceAccountEmail) return serviceAccountEmail;
  return getDefaultComputeServiceAccount();
}

export function getServiceImage(
  args: { serviceId?: string; imageTag?: string; projectId?: string; resolve?: boolean } = {}
) {
  const { serviceId: configServiceId, location } = getConfigs();
  const {
    serviceId = configServiceId,
    imageTag = process.env.GITHUB_SHA || 'latest',
    projectId = getConfigs().projectId,
    resolve,
  } = args;

  const imageName = pulumi.interpolate`${projectId}/cloud-run/${getUniqueNameInProject(serviceId)}`;

  const registryUrl = `${location}-docker.pkg.dev`;

  const image = pulumi.interpolate`${registryUrl}/${imageName}:${imageTag}`;
  if (!resolve) return image;

  const registryImage = docker.getRegistryImageOutput(
    {
      name: image,
    },
    { provider: getDockerProvider() }
  );
  return registryImage.sha256Digest.apply(
    (digest) => pulumi.interpolate`${registryUrl}/${imageName}@${digest}`
  );
}

export function getStackReference(
  pulumiProject: string,
  stackName?: string
): pulumi.StackReference {
  if (isLocal) {
    return {
      // @ts-ignore
      getOutput: () => '',
    };
  }
  // allows for calling multiple times in one stack
  const stackId = `triplewhale/${pulumiProject}/${stackName || getConfigs().stack}`;
  if (!stacks[stackId]) stacks[stackId] = new pulumi.StackReference(stackId);
  return stacks[stackId];
}

export function getUniqueNameInProject(
  name: pulumi.Input<string> | string,
  delimiter: string = '-',
  stackFirst: boolean = false
) {
  const { isMultiPerProject, stack } = getConfigs();
  if (!name) {
    return isMultiPerProject ? stack : '';
  }
  if (typeof name === 'string') {
    return stackFirst
      ? `${isMultiPerProject ? `${stack}${delimiter}` : ''}${name}`
      : `${name}${isMultiPerProject ? `${delimiter}${stack}` : ''}`;
  }

  return pulumi.interpolate`${
    stackFirst
      ? `${isMultiPerProject ? `${stack}${delimiter}` : ''}${name}`
      : `${name}${isMultiPerProject ? `${delimiter}${stack}` : ''}`
  }`;
}

export async function getGitFullUrl() {
  const { serviceId } = getConfigs();
  const name = await getGitRepoName();
  return `https://github.com/Triple-Whale/${name}/tree/master/services/${serviceId}`;
}

export function convertEnvs(envs) {
  return envs
    ? Object.keys(envs)
        .filter((k) => k !== 'PORT')
        .map((k) => {
          // check if is pulumi output
          let v = envs[k];
          if (!(v instanceof Object && v.apply)) {
            v = String(v);
          }
          return { name: k, value: v };
        })
    : [];
}

export function storeHostedServiceInfo(args: {
  type: 'rabbitmq' | 'clickhouse' | 'kafka';
  name: string;
  data: any;
}) {
  const { type, name, data } = args;
  const { projectId } = getConfigs();
  createSecret(data, `${name}-${type}`);

  new gcp.storage.BucketObject(`${name}-${type}`, {
    bucket: `devops-${projectId}`,
    content: toJSONOutput(data),
    name: `hosted-service/${type}/${name}.json`,
  });
}

