}

export function createUUID(name: string) {
  return crypto.createHash('shake256', { outputLength: 4 }).update(name).digest('hex');
}

// the resulting rows are returned in a variable with the name specified
// in the resultsVariable argument,
// which are a series of JSON f,v objects for indicating fields and values
export function bigQueryQuery(args: { name: string; query: string; resultsKey: string }): {
  [k: string]: Step;
} {
  const { name, query, resultsKey } = args;
  const { projectId } = getConfigs();

  const insertResultKey = `${name}InsertResult`;
  const pageResultKey = `${name}PageResult`;
  return {
    [name]: {
      steps: [
        {
          [`${name}-init`]: {
            assign: [
              {
                pageToken: null,
              },
              {
                [resultsKey]: [],
              },
            ],
          },
        },
        {
          [`${name}-submitQuery`]: {
            try: {
              call: 'googleapis.bigquery.v2.jobs.insert',
              args: {
                projectId,
                body: {
                  configuration: {
                    query: {
                      useLegacySql: false,
                      query,
                    },
                  },
                },
              },
              result: insertResultKey,
            },
            retry: {
              predicate: '${http.default_retry_predicate}',
            },
          },
        },
        {
          [`${name}-getPage`]: {
            try: {
              call: 'googleapis.bigquery.v2.jobs.getQueryResults',
              args: {
                projectId,
                jobId: `\${${insertResultKey}.jobReference.jobId}`,
                pageToken: '${pageToken}',
              },
              result: pageResultKey,
            },
            retry: {
              predicate: '${http.default_retry_predicate}',
            },
          },
        },
        {
          [`${name}-processPage`]: {
            for: {
              value: 'row',
              in: `\${${pageResultKey}.rows}`,
              steps: [
                {
                  processRow: {
                    assign: [
                      {
                        [resultsKey]: `\${list.concat(${resultsKey},row)}`,
                      },
                    ],
                  },
                },
              ],
            },
          },
        },
      ],
    },
  };
}

export function createWorkflow(args: {
  name: string;
  workflow: GoogleCloudWorkflows;
  serviceAccountEmail: pulumi.Input<string>;
  forceMonitor?: boolean;
}) {
  const { name = 'workflow', workflow, serviceAccountEmail, forceMonitor = false } = args;
  const { projectId } = getConfigs();
  const workflowName = getUniqueNameInProject(name);

  if (isProduction || forceMonitor) {
    createDatadogMonitor({
      id: `workflow-errors-${workflowName}`,
      sendOnAlert: false,
      settings: {
        name: `<${workflowName}> Workflow Failed Executions`,
        query: `sum(last_1h):sum:gcp.workflows.finished_execution_count{workflow_id:${workflowName}, project_id:${projectId}, status:failed}.as_count() > 0`,
        type: 'query alert',
        monitorThresholds: {
          critical: '0',
        },
      },
    });
  }

  workflow[CHILD_WORKFLOW_PREDICATE] = {
    params: ['e'],
    steps: [
      {
        checkForError: {
          switch: [
            {
              condition: '${"OperationError" in e.tags}',
              return: true,
            },
            {
              condition: true,
              return: false,
            },
          ],
        },
      },
    ],
  };

  workflow[DATAFORM_PREDICATE] = {
    params: ['e'],
    steps: [
      {
        checkForError: {
          switch: [
            {
              // http.default_retry_predicate with 4xx added
              condition:
                '${(e.code >= 400 and e.code < 500) or e.code == 502 or e.code == 503 or e.code == 504 or "ConnectionError" in e.tags or "ConnectionFailedError" in e.tags or "TimeoutError" in e.tags}',
              return: true,
            },
            {
              condition: true,
              return: false,
            },
          ],
        },
      },
    ],
  };

  return new gcp.workflows.Workflow(name, {
    name: workflowName,
    sourceContents: toJSONOutput(workflow, true),
    serviceAccount: serviceAccountEmail,
  });
}

