      },
      except: {
        as: 'e',
        steps: [
          {
            [`${name}-fallback`]: {
              assign: [
                {
                  [resultsKey]: `\${${inputKey}}`,
                },
              ],
            },
          },
        ],
      },
    },
  };
}

