export function invokeWorkflows(args: { name; workflowInvocations: WorkflowInvocation[] }): {
  [k: string]: Step;
} {
  const { name, workflowInvocations } = args;
  return {
    [name]: {
      parallel: {
        shared: workflowInvocations
          .map((workflowInvocation) => workflowInvocation.resultKey)
          .filter(Boolean),
        branches: workflowInvocations.map((workflowInvocation) =>
          invokeWorkflow(workflowInvocation)
        ),
      },
    },
  };
}

export function invokeWorkflow(args: WorkflowInvocation): {
  [k: string]: Step;
} {
  const {
    name,
    workflowId,
    workflowArgs,
    resultKey,
    exceptionKey,
    failOnChildFailure = true,
    maxRetries = 5,
    timeout = 31536000, // max. 1 year
  } = args;
  const { projectId, location } = getConfigs();
  const tempResultKey = `${name.replaceAll('-', '_')}_tempResult`;
  const assignStep = exceptionKey
    ? [
        {
          [`${name}-assign`]: {
            assign: [
              {
                [exceptionKey]: '',
                [tempResultKey]: '',
              },
            ],
          },
        },
      ]
    : [];

  // @ts-ignore
  return {
    [name]: {
      steps: [
        ...assignStep,
        {
          [`${name}-invoke`]: {
            try: {
              try: {
                call: 'googleapis.workflowexecutions.v1.projects.locations.workflows.executions.create',
                args: {
                  parent: `projects/${projectId}/locations/${location}/workflows/${workflowId}`,
                  body: {
                    argument: JSON.stringify(workflowArgs),
                  },
                  connector_params: {
                    timeout,
                  },
                },
                result: tempResultKey,
              },
              retry: {
                predicate: '${http.default_retry_predicate}',
              },
            },
            retry: {
              predicate: `\${${CHILD_WORKFLOW_PREDICATE}}`,
              max_retries: maxRetries,
            },
            except: {
              as: 'e',
              steps: failOnChildFailure
                ? [
                    {
                      [`${name}-raise`]: {
                        raise: '${e}',
                      },
                    },
                  ]
                : [
                    {
                      [`${name}-handleFaillure`]: {
                        call: 'sys.log',
                        args: {
                          data: { [`${name} - workflow failed`]: '${e}' },
                        },
                      },
                    },
                    {
                      [`${name}-assignFailure`]: {
                        assign: [
                          {
                            [exceptionKey]: '${e}',
                          },
                        ],
                      },
                    },
                  ],
            },
          },
        },
        {
          [`${name}-logExecutionUrl`]: {
            call: 'sys.log',
            args: {
              text: `\${"child workflow started: https://console.cloud.google.com/workflows/workflow/${location}/${workflowId}/execution/" + text.find_all_regex(${tempResultKey}.name, "[^/]+$")[0].match + "?project=${projectId}"}`,
            },
          },
        },
        {
          [`${name}-assignResult`]: {
            assign: [
              {
                [resultKey]: `\${json.decode(${tempResultKey}.result)}`,
              },
            ],
          },
        },
      ],
    },
  };
}

/**
 * @description Creates a store with all the methods of `$store`
 * by receiving a callback that takes in the values of the stores as
 * arguments and returns some computed value using those arguments, as
 * well as an array of stores that this store will depend on.
 *
 * This function returns either a store containing the type of the return
 * type of the provided callback or a store containing a "promisified" version
 * of the initial data if the return type of the callback is a Promise. This
 * wrapped version is of type `PromisifiedData`.
 *
 * The values in the callback match the order of the stores that are in the
 * array.
 */
export function $derived<
  S extends Store[],
  TCallback extends (...args: StoreReturnTypes<S>) => unknown | Promise<unknown>,
  TData = ReturnType<TCallback> extends Promise<unknown>
    ? PromisifiedData<Awaited<ReturnType<TCallback>>>
    : ReturnType<TCallback>,
  TReturned = TData extends Promise<TData> ? PromisifiedData<TData> : TData
>(cb: TCallback, stores: [...S]): Store<TReturned> {
  const initialValue = cb(...getValues(stores));

  if (isPromise(initialValue)) {
    return createDerivedFromAsync(
      initialValue,
      cb as (...args: StoreReturnTypes<S>) => Promise<TData>,
      stores
    ) as unknown as Store<TReturned>;
  }

  return createDefaultDerived(
    initialValue,
    cb as (...args: StoreReturnTypes<S>) => TData,
    stores
  ) as unknown as Store<TReturned>;
}

/**
 * Runs the provided callback whenever any of the subscribed-to stores emit an update.
 * `$effect` also does an initial run of the callback right when its called.
 *
 * It returns an "off" function to turn off the effect/remove subscriptions.
 *
 * One important thing is that if the callback is asynchronous, the taskQueue will
 * automatically make sure it's being called in order.  If you don't want this kind of
 * sequential execution, don't make the callback return a Promise.
 */
export function $effect<S extends Store[], TCallback extends EffectCallback<S>>(
  cb: TCallback,
  stores: [...S]
): () => void {
  const taskQueue = new OrderedTaskQueue();

  const unsub = () => {
    // must clear the task queue, so no further tasks will be executed after an unsub
    taskQueue.clear();
    unsubs.forEach((u) => u());
  };

  const unsubs = stores.map((s) =>
    s.subscribe(() => {
      // same as initialVals for same reason
      const values = getValues(stores);
      taskQueue.add(() => cb(unsub, ...values));
    })
  );

  // must get the values at the exact time the function
  // is called, so we get the right values for that call
  const initialVals = getValues(stores);

  // initial run
  taskQueue.add(() => cb(unsub, ...initialVals));

  return unsub;
}

/**
 * Very similar to `$effect` - just doesn't run the provided callback initially.
 * Only runs the callback if any stores emit an update.
 */
export function $lazyEffect<S extends Store[], TCallback extends EffectCallback<S>>(
  cb: TCallback,
  stores: [...S]
): () => void {
  const taskQueue = new OrderedTaskQueue();

  const unsub = () => {
    // must clear the task queue, so no further tasks will be executed after an unsub
    taskQueue.clear();
    unsubs.forEach((u) => u());
  };

  const unsubs = stores.map((s) =>
    s.subscribe(() => {
      // must get the values at the exact time the function
      // is called, so we get the right values for that call
      const values = getValues(stores);
      taskQueue.add(() => cb(unsub, ...values));
    })
  );

  return unsub;
}

/**
 * @description
 * Creates a global store.
 * Just a more functional/React-y wrapper around the Observable class.
 *
 * Exposes the following methods:
 * - `get`: Get the current observed value.
 * - `set`: Sets the value - updating any entity listening to the value's changes.
 * - `subscribe`: Method used to subscribe to changes in the store from anywhere.
 *    Returns an `unsubscribe` method that can be called to remove the subscription and stop listening for changes.
 * - `useStore`: Hook used to listen to the value's changes. Returns value and setter for the value.
 * - `useDerived`: Convenience hook that can be used like useSelector - with the store's data.
 */
export function $store<T extends NonFunction<unknown>>(
  value: T | Observable<T> | ObservableParams<T>
): Store<T> {
  const observable = (() => {
    if (value instanceof Observable) return value;
    if (isObservableParams(value)) return new Observable(value);
    return new Observable<T>({ initialData: value });
  })();

  //
  // INIT
  //
  // useful to track previous value
  let prevVal = observable.data;
  // MUST call `set` once on initialization of store to make sure all side effects are in sync
  set(observable.data);

  //
  // UTILS
  //
  function get() {
    return observable.data;
  }

  function set(newValue: T | ((prev: T) => T)) {
    const computedVal =
      typeof newValue === 'function' ? (newValue as (prev: T) => T)(observable.data) : newValue;

    prevVal = observable.data;
    observable.setData(computedVal);
  }

  function subscribe(cb: (newVal: T, prevVal: T) => T): () => void {
    const func = (newVal: T) => cb(newVal, prevVal);
    observable.addUpdateListener(func);
    return () => observable.removeUpdateListener(func);
  }

  function eagerSubscribe(cb: (newVal: T, prevVal: T, unsubscribe: () => void) => T): () => void {
    // This function needs a callback that can receive an unsubscribe in its signature.
    // Otherwise, it's impossible to unsubscribe on the first run of func if desired.

    function func(newVal: T) {
      return cb(newVal, prevVal, unsubscribe);
    }

    function unsubscribe() {
      observable.removeUpdateListener(func);
    }

    observable.addUpdateListener(func);

    // immediate first call
    func(observable.data);

    return unsubscribe;
  }

  function useStore(): [T, typeof set] {
    const [val, setVal] = useState<T>(observable.data);

    useEffect(() => {
      const listener = (newVal: T) => setVal(newVal);

      observable.addUpdateListener(listener);

      return () => {
        observable.removeUpdateListener(listener);
      };
    }, []);

    return [val, set];
  }

  function useDerived<D = any>(cb: (val: T) => D): D {
    const [val, setVal] = useState<D>(cb(observable.data));

    useEffect(() => {
      const listener = (newVal: T) => setVal(cb(newVal));

      observable.addUpdateListener(listener);

      return () => {
        observable.removeUpdateListener(listener);
      };
    }, [cb]);

    return val;
  }

  return {
    get,
    set,
    subscribe,
    eagerSubscribe,
    useStore,
    useDerived,
  };
}

export class CallbackDebouncer {
  private running = false;
  private currentFunc: Function | null = null;
  private waitingFunc: Function | null = null;

  /** Get the function that should be run now */
  private getFunc(): Function | null {
    try {
      return this.currentFunc || this.waitingFunc;
    } finally {
      // after getting the most current function,
      // either we'll have the waiting function
      // in currentFunc, and waitingFunc will be null.
      // or both will be null
      this.currentFunc = this.waitingFunc;
      this.waitingFunc = null;
    }
  }

  /**
   * Does nothing if a function is currently getting execute,
   * Otherwise, calls the last saved function.
   */
  private async flush(): Promise<void> {
    if (this.running) return;

    const func = this.getFunc();
    if (!func) return; // nothing to call

    this.running = true;

    try {
      const res = func();
      if (isPromise(res)) await res;
    } catch (err) {
      console.error('Error caught in snipestate debouncer:>>', err);

      // nothing else should happen after an error - clearing all functions
      this.currentFunc = null;
      this.waitingFunc = null;
    }

    this.running = false;
    this.flush();
  }

  /**
   * Sets the callback to the queue and immediately attempts to execute.
   */
  public handle(cb: Function): void {
    this.waitingFunc = cb;
    this.flush();
  }
}

export type ObservableParams<T> = {
  initialData: T;
  /** If provided, allows you to extract computed value based on observed data. */
  get?(data: T): T;
  /** If provided, allows you to more accurately conditionally control how
   * data is set as well as provide side effects like saving in localStorage.
   * If function returns nothing, will be treated as side effect and the new
   * value in the callback will be the new value. If it returns something, that
   * returned value will be the new value.*/
  set?(newData: T, data: T): T | void;
};

