export function isObservableParams(obj: unknown): obj is ObservableParams<any> {
  return !!(typeof obj === 'object' && obj && ('get' in obj || 'set' in obj));
}

export class Observable<T> {
  private _callbacks = new Set<GenericCallback<T>>();
  private _get: ObservableParams<T>['get'];
  private _set: ObservableParams<T>['set'];
  protected _data: T;

  public constructor({ initialData, get, set }: ObservableParams<T>) {
    this._data = initialData;
    if (get) this._get = get;
    if (set) this._set = set;
  }

  public get data(): T {
    return this._get ? this._get(this._data) : this._data;
  }

  public setData(newData: T): void {
    // use setter function if it exists
    newData = this._set?.(newData, this._data) ?? newData;

    // ignore if data is shallow equal
    if (newData === this._data) return;

    // set data
    this._data = newData;

    // finally emit to listeners
    this.emitUpdate();
  }

  public addUpdateListener = (callback: GenericCallback<T>): void => {
    this._callbacks.add(callback);
  };

  public removeUpdateListener = (callback: GenericCallback<T>): void => {
    this._callbacks.delete(callback);
  };

  protected emitUpdate = (): void => {
    this._callbacks.forEach((cb) => cb(this._data));
  };
}

/**
 * Contains a queue to which functions can be added and executed in order - even if they're asynchronous.
 *
 * IMPORTANT: If there's an error in a callback added to the queue, the queue is cleared and execution stops.
 */
export class OrderedTaskQueue {
  private running = false;
  private callQueue: CallQueueFunction[] = [];
  private cleanupFunc: (() => any) | null = null;

  /**
   * Does nothing if a function is currently getting executed.
   * Otherwise, removes and calls the first function in the callback queue.
   */
  private async flush(): Promise<void> {
    if (this.running) return;

    const firstFunc = this.callQueue.shift();
    if (!firstFunc) return;

    this.running = true;

    try {
      this.cleanup();

      const res = firstFunc();
      if (isPromise(res)) await res;

      // save cleanup function to be run before next callback
      if (typeof res === 'function') this.cleanupFunc = res;
    } catch (err) {
      console.error('Error caught in snipestate queue:>>', err);

      // clear queue if we have an error
      this.clear();
    }

    // Setting running to false is what allows us to move
    // to the next function in the queue if one exists.
    this.running = false;
    this.flush();
  }

  /**
   * Adds the callback to the queue and immediately attempts to execute.
   */
  public add(cb: CallQueueFunction): void {
    this.callQueue.push(cb);
    this.flush();
  }

  /**
   * Clear the task queue. Can't stop a currently running task, but
   * no further tasks will be executed.
   */
  public clear(): void {
    this.callQueue = [];
  }

  /**
   * runs cleanupFunc and clears after finish, since
   * not all callbacks might return a cleanup function
   *
   * Must catch all to be non blocking
   */
  public cleanup(): void {
    try {
      this.cleanupFunc?.();
    } catch (err) {
      // TODO: Create some global setting that can allow devs to see snipestate errors in dev mode only
    } finally {
      this.cleanupFunc = null;
    }
  }
}

export class PromisifiedData<T = any> {
  public data?: T;
  public pending: boolean = true;
  public error?: unknown;

  public constructor(args?: { data?: T; pending?: boolean; error?: unknown }) {
    if (args) {
      this.data = args.data;
      this.pending = args.pending ?? this.pending;
      this.error = args.error;
    }
  }
}

export type Store<T = any> = {
  /** Retrieve the value of the observed data */
  get: () => T;
  /** Set value of observed data on a global scope - everyone using the useStore or useDerived hooks is notified */
  set: (val: NonFunction<T> | ((prev: T) => T)) => void;
  /** Lets us subscribe to changes from the store. When an update occurs, the callback function is run. Returns an "unsubscribe" function to stop listening to updates. */
  subscribe: (cb: (newVal: T, prevVal: T) => any) => () => void;
  /** Almost the same as `subscribe`, but it calls the callback right away as well. */
  eagerSubscribe: (cb: (newVal: T, prevVal: T, unsubscribe: () => void) => any) => () => void;
  /** Hook that lets you listen to changes in the observed data and set the data globaly - use like useState */
  useStore: () => [T, Store<T>['set']];
  /** More of a convenience hook if you need to use some derived value from the store's current data - use like useSelector */
  useDerived: <D>(cb: (val: T) => D) => D;
};

