export const ConfirmModal = () => {
  const [{ open, message: Message, title, modalProps, confirmText, cancelText, reverseOrder }] =
    $modalStore.useStore();

  return (
    <Modal
      {...modalProps}
      opened={open}
      onClose={cancelAction}
      title={typeof title === 'string' ? <Text>{title}</Text> : title}
      data-tw-ui-component="ConfirmModal"
      {...getMarketingProps(`tw-confirmation-modal`)}
    >
      {typeof Message === 'string' ? <Text>{Message}</Text> : typeof Message === 'function' ? <Message /> : Message}
      <Flex
        gap="xs"
        justify={reverseOrder ? 'flex-start' : 'flex-end'}
        direction={reverseOrder ? 'row-reverse' : 'row'}
      >
        <Button onClick={confirmAction}>{confirmText}</Button>
        <Button variant="white" onClick={cancelAction}>
          {cancelText}
        </Button>
      </Flex>
    </Modal>
  );
};

//
export const confirm = async ({
  title,
  message,
  modalProps,
  confirmText = 'Confirm',
  cancelText = 'Cancel',
  reverseOrder,
}: Omit<ModalStore, 'open' | 'confirmed'>) => {
  $modalStore.set((x) => ({
    ...x,
    open: true,
    title,
    message,
    modalProps: { ...DEFAULT_CONFIRM_MODAL_STORE_PROPS.modalProps, ...modalProps },
    confirmText,
    cancelText,
    reverseOrder,
  }));

  return new Promise((resolve) => {
    const unsubscribe = $modalStore.subscribe(({ confirmed }) => {
      try {
        resolve(!!confirmed);
      } finally {
        unsubscribe();
        $modalStore.set((x) => ({ ...x, open: false }));
      }
    });
  });
};

export const DEFAULT_CONFIRM_MODAL_STORE_PROPS = {
  open: false,
  title: '',
  message: '',
  confirmed: false,
  confirmText: 'Confirm',
  cancelText: 'Cancel',
  modalProps: { zIndex: Number.MAX_SAFE_INTEGER },
};

//
// STORE
//
export const $modalStore = $store<ModalStore>(DEFAULT_CONFIRM_MODAL_STORE_PROPS);

//
// ACTIONS
//
export function confirmAction() {
  $modalStore.set((x) => ({ ...x, confirmed: true }));
}

export function cancelAction() {
  $modalStore.set((x) => ({ ...x, confirmed: false }));
}

export type ModalStore = {
  open: boolean;
  title?: React.ReactNode;
  message?: React.FC | React.ReactNode;
  confirmed?: boolean;
  confirmText?: string;
  cancelText?: string;
  /** By default, confirm action is on the left. If true, this prop reverses their order. */
  reverseOrder?: boolean;
  modalProps?: Partial<Omit<ModalProps, 'onClose' | 'opened'>>;
};

