/** This hook uses `dedupComboboxData` under the hood to
 * make sure there are no duplicate values in the combobox. */
export function useDedupedComboboxData(data?: ComboboxData): ComboboxData {
  return useMemo(() => (!data ? emptyArray : dedupComboboxData(data)), [data]);
}

/** This hook uses `dedupComboboxStringData` under the hood to
 * make sure there are no duplicate values in the combobox. */
export function useDedupedComboboxStringData(data?: ComboboxStringData): ComboboxStringData {
  return useMemo(() => (!data ? emptyArray : dedupComboboxStringData(data)), [data]);
}

export function useDisclosure(initialValue: boolean) {
  const [opened, setOpened] = useState(initialValue);

  const open = useCallback(() => setOpened(true), []);
  const close = useCallback(() => setOpened(false), []);
  const toggle = useCallback(() => setOpened((x) => !x), []);

  return [opened, { open, close, toggle }] as const;
}

export function useHierarchicalList<T>(initialList: HierarchicalListItem<T>[]) {
  const [list, setList] = useState<HierarchicalListItem<T>[]>(initialList);
  const handler = useMemo(() => new HierarchicalList<T>(list), [list]);

  useEffect(() => {
    setList(initialList);
  }, [initialList]);

  useEffect(() => {
    const func = (l: HierarchicalListItem<T>[]) => setList([...l]);
    handler.addUpdateListener(func);
    return () => handler.removeUpdateListener(func);
  }, [handler]);

  return [list, handler] as const;
}

/** Utility hook that just makes it a bit easier to deal with list states in an optimized way */
export function useList<T>(initialList: T[]) {
  const [list, setList] = useState<T[]>(initialList);
  const handler = useMemo(() => new ListHandler<T>(list), [list]);

  useEffect(() => {
    handler.addUpdateListener(setList);
    return () => handler.removeUpdateListener(setList);
  }, [handler]);

  return [list, handler] as const;
}

export function useLocalStorage<T extends {} | any[]>({
  key,
  defaultValue,
}: {
  key: string;
  defaultValue: T;
}): [T, LocalStorageStateSetter<T>] {
  const storedState = useMemo(() => localStorage.getItem(key) || '""', [key]);
  const [state, setState] = useState<T>(JSON.parse(storedState) || defaultValue);

  const setLocalStorageState: LocalStorageStateSetter<T> = (value) => {
    const computedVal = typeof value === 'function' ? value(state) : value;
    setState(computedVal);
    const valStr = JSON.stringify(computedVal);
    localStorage.setItem(key, valStr);
  };

  useEffect(() => {
    setLocalStorageState(state);
  }, []);

  return [state, setLocalStorageState];
}

