/**
 * @description
 * Meant to be used like redux-toolkit useSelector, just for window resize events. Here's a simple example:
 *
 * `const isMobile = useSelectByWindowResize<boolean>(({ width }) => width < 600)`
 *
 * In the callback, you get access to width, height, prevWidth, and prevHeight. There are a lot of possibilities,
 * but the main benefit in using this hook is that it doesn't cause a rerender everytime the window is resized.
 * Just like useSelector, it only causes a rerender when what's being returned from the selector changes.
 */
export function useSelectByWindowResize<T>(cb: WindowResizeSelectorCallback<T>): T {
  const [state, setState] = useState<T>(() =>
    cb({ width: window.innerWidth, height: window.innerHeight })
  );

  useEffect(() => {
    const handleResize = () => {
      const res = cb({ width: window.innerWidth, height: window.innerHeight });
      if (!isEqual(state, res)) setState(res);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [state, cb]);

  return state;
}

 // so hook can determine which dimensions to look for

/**
 * @description
 * this hook takes in a map of custom dimensions and returns the name of the
 * dimension that currently matches the size of the window.
 * @param dimension Either 'width'  or 'height'. Determines what dimension the dimensionConstraints are being used on.
 * @param dimensionConstraints Map of dimension name to min size for that dimension.
 * @returns Name of the first object in the dimensions map that matches the current window width.
 */
export function useWindowSize<T extends BreakpointMap>(
  dimension: Dimension,
  map: T
): keyof T | 'unmapped' {
  return useSelectByWindowResize<keyof T | 'unmapped'>((dimensions) => {
    const size = dimensions[dimension];

    for (const key in map) {
      const [min, max] = map[key];
      if (size >= min && (typeof max !== 'number' || size < max)) return key;
    }

    return 'unmapped';
  });
}

