export function isDefined<T>(x: T | null | undefined): x is T {
  return x !== null && x !== undefined;
}

/**
 * @description Extracts the valid CSS color corresponding to the FormattedColor provided based on the dynamic colors.
 * If no shade is provided, uses theme defined primary shade which should be 6.
 */
export function extractCSSColor(clr: FormattedColor): string {
  if (isValidShadelessColor(clr)) {
    if (clr === 'transparent') return TRANSPARENT;
    return vars.colors[clr];
  }

  const [color, shade] = clr.split('.');

  if (isNaN(+shade) || +shade > 9 || +shade < 0) {
    return vars.colors[color]?.[PRIMARY_SHADE];
  }

  return vars.colors[color]?.[+shade as keyof (typeof vars.colors)[typeof color]];
}

export function ensureKeysAreCSSProperties<T extends keyof React.CSSProperties>(
  keys: ReadonlyArray<T>
) {
  return keys;
}

/**
 * @description Generally we try using overridden default mantine style props when trying to expose certain flexibility
 * for more custom styling, but in cases where some properties don't just exist in Mantine's style system, and we have
 * to add the styles ourselves, this function can come in handy.
 */
export function extractAndMapCustomPropsToStyle<T extends object>(
  props: T
): CustomStyleAndRestProps<T> {
  const result: CustomStyleAndRestProps<T> = {
    style: {},
    rest: {} as Omit<T, keyof TwCustomStyleSystemProps>,
  };

  for (const key in props) {
    if (isTwCustomStyleSystemField(key)) {
      result.style[key] = String(props[key]);
      continue;
    }
    // TODO: Find a better solution for this
    (result.rest as any)[key] = props[key];
  }

  return result;
}

/**
 * @description Can be used to always return an Icon element or null whether input is Icon or icon name
 */
export function extractIcon(
  icon?: JSX.Element | IconName | null,
  iconProps?: Omit<IconProps, 'name'>
) {
  if (!icon) return null;
  if (typeof icon !== 'string') return icon;
  return <Icon name={icon as IconName} {...iconProps} />;
}

export const isValidShadelessColor = (clr: string): clr is ShadelessColor => {
  return clr === 'white' || clr === 'black' || clr === 'transparent';
};

export const isValidFormattedColor = (clr: string): clr is FormattedColor => {
  if (isValidShadelessColor(clr)) return true;

  if (!clr.includes('.')) return false;

  const [name, shade] = clr.split('.');
  if (!(name in colors)) return false;

  const shadeNum = Number(shade);
  if (isNaN(shadeNum) || shadeNum < 0 || shadeNum > 9) return false;

  return true;
};

export const getActivitiesByCalc = async <T extends ActivitySettings>(
  arr: any[],
  metaData: ActivityMetaData,
  model: Model<T>,
  isDeleted: (item: any) => boolean = null
): Promise<ActivitySettings[]> => {
  try {
    const promises = arr.map(async (o): Promise<ActivitySettings> => {
      let res: ActivitySettings = null;
      const old: any = await getObjectHistory(o.id, metaData.level, model);
      if (old) {
        const diffObject: ActivityDiff = getActivityDiffObject(old, o);
        res = diffObject ? { id: o.id, ...metaData, ...diffObject } : null;
      } else {
        res = {
          id: o.id,
          old: null,
          new: o,
          changes: null,
          activityType: ActivityType.CREATED,
          ...metaData,
        };
      }
      if (res && isDeleted && isDeleted(o)) {
        res = {
          ...res,
          activityType: ActivityType.DELETED,
        };
      }
      return res;
    });
    const activities: ActivitySettings[] = await Promise.all(promises);
    return activities.filter((o) => o);
  } catch (error) {
    logger.error('calcActivitiesChanges failed', { metaData, error });
  }
};

export const getActivityDiffObject = (oldObj: any, newObj: any): ActivityDiff => {
  const specificPaths: String[] = [];
  const changes = deepDiff(oldObj, newObj);
  const flatChanges = Object.entries(changes).map(([key, value]) => {
    return { field: key, from: value['from'], to: value['to'] };
  });
  const filteredChanges = flatChanges.filter((value) => {
    return !(
      (value['from'] === null && value['to'] == undefined) ||
      (value['to'] === null && value['from'] == undefined)
    );
  });
  if (filteredChanges.length > 0) {
    return {
      old: oldObj,
      new: newObj,
      changes: filteredChanges,
      activityType: ActivityType.UPDATED,
    };
  }
  return null;
};

export const getObjectHistory = async <T extends ActivitySettings>(
  id: string,
  level: string,
  model: Model<T>
) => {
  try {
    const old: any = await model
      .findOne({ id, level }, { new: 1 }, { sort: { updatedAt: -1 } })
      .exec();
    return old ? old.new : null;
  } catch (e) {
    logger.error(`getObjectHistory failed, id: ${id} level: ${level} error: ${e}`);
  }
};

export const saveActivities = async (
  activities: ActivitySettings[],
  shopDomain: string,
  model: Model<any>,
  extendFilterField: string[] = []
) => {
  try {
    const docsToUpsert = activities.map((doc) => {
      const extendFilter: { [key: string]: any } = {};
      extendFilterField.forEach((name) => {
        extendFilter[name] = doc[name];
      });
      const filter = { id: doc.id, level: doc.level, ...extendFilter };
      return {
        replaceOne: {
          filter: filter,
          replacement: doc,
          upsert: true,
        },
      };
    });

    logger.info(`saveActivities for shop: ${shopDomain} activities length: ${activities.length}`);

    if (activities.length > 0) {
      const res = await model.bulkWrite(docsToUpsert);

      await callPubSub('new_activities_created', {
        shopId: shopDomain,
        serviceId,
        activities: activities,
      });
    }
  } catch (e) {
    logger.error(`saveActivities failed,  shop_domain: ${shopDomain} error: ${e}`);
  }
};

