export function defaultOpenApiOperation(
  path: string,
  method: string,
  openApi: OpenApi
): OpenAPIV3.OperationObject & { 'x-tw' } {
  const { security, overwriteExternalPath, interfaces, operation, deployment } = openApi || {};
  const serviceId = process.env.SERVICE_ID;
  return {
    operationId:
      operation?.operationId ||
      path
        .split('/')
        .filter(Boolean)
        .map((p) => {
          if (p.startsWith(':')) p = p.replace(':', '');
          return p;
        })
        .join('-') +
        '-' +
        method,
    responses: {
      200: {
        description: 'A successful response',
      },
    },
    security:
      security &&
      Object.keys(security)?.map((s) => {
        return { [s]: security[s] };
      }),
    parameters: [],
    tags: operation?.tags?.length ? operation?.tags : [serviceId],
    'x-tw': {
      pathPrefix: overwriteExternalPath?.prefix || serviceId,
      serviceId,
      interfaces,
      deployment,
    },
  };
}

export function getAlternativePath(route: string, apiConfigArgs: ApiConfigArgs) {
  if (apiConfigArgs?.openApi?.overwriteExternalPath) {
    const alternativePath = apiConfigArgs.openApi.overwriteExternalPath;
    let result;
    if (alternativePath.path) result = alternativePath.path;
    return result;
  }
}

export function getRateLimitPolicy(conf: RateLimitConfig) {
  return conf.map((def) => `${def.quota};w=${def.window}`).join(', ');
}

export function addRateLimits(op: TwOperationObject, conf: RateLimitConfig) {
  for (let status in op.responses) {
    const res = op.responses[status];
    if (status === '429') {
      res['headers'] = {
        ...res['headers'],
        ...rateLimitResponse['429']['headers'],
      };
    } else {
      res['headers'] = {
        ...res['headers'],
        ...rateLimitHeaders(conf),
      };
    }
  }
  if (!('429' in op.responses)) {
    op.responses = {
      ...op.responses,
      ...rateLimitResponse,
    };
  }
  op['x-tw'].rateLimits = serializeConf(conf);
}

export function endpointWrapper<P = any, R = any, B = any, Q = any>(
  fn: (req: Request<P, R, B, Q>, res: Response, next?: NextFunction) => any
) {
  const wrapped = async function wrapper(
    req: Request<P, R, B, Q>,
    res: Response,
    next: NextFunction
  ) {
    try {
      const result = await fn(req, res, next);
      // @ts-ignore
      // isMiddleware is set in /express/middleware.ts
      // allowAutoResponse in /express/expressRoutesParser.ts
      if (!res.headersSent && !fn.isMiddleware && wrapped.allowAutoResponse) {
        logger.warn(`endpointWrapper (${fn.name || '<anonymous>'}): no response sent.`);
        if (!process.env.TW_ALLOW_AUTO_RESPONSE) return result;
        if (result) {
          return res.json(result);
        } else {
          return res.status(204).end();
        }
      }
      return result;
    } catch (e) {
      logger.error('endpointWrapper', e);
      addErrorMessageToDatadog(e);
      if (!res.headersSent) {
        // @ts-ignore
        const traceId = req.traceId;
        if (e instanceof HttpErrorResponse || isLocal) {
          return res.status(e?.status || 500).json({ message: e?.message, traceId });
        }
        return res.status(500).json({ message: 'Internal Server Error', traceId });
      } else {
        res.end();
      }
    }
  };
  return wrapped;
}

export async function checkUserAccessToResource(
  user: FirebaseUser | HydraApp | undefined,
  serviceId: ServicesIdsWithSensory,
  accountIds: string | string[],
  options?: { ignoreAdmin?: boolean }
): Promise<CheckAuthResponse> {
  const { ignoreAdmin } = options || {};
  if (!user) return { result: true };
  const accounts = (Array.isArray(accountIds) ? accountIds : [accountIds]).filter(Boolean);
  if (!accounts.length || !serviceId) {
    logger.warn('denied: ', { serviceId, accountIds });
    return { result: false, message: 'Invalid Resource' };
  }
  if (user['admin'] && !(ignoreAdmin || Boolean(process.env.IGNORE_ADMIN))) {
    return { result: true };
  }
  const authType = getAuthType(user);
  const body: CheckAuthRequest = {
    type: 'user',
    serviceId,
    accountIds,
  };
  body.id = user['sub'];
  try {
    if (authType === 'app') {
      body.fromAccountId = user['ext']['claims']['accountId'];
    }
  } catch {
    // for auth0
    body.id = user['azp'];
    body.type = 'app';
  }
  try {
    const authResp = await callServiceEndpoint<CheckAuthResponse>('users', 'auth/check', body, {
      method: 'GET',
      log: false,
    });
    return authResp.data;
  } catch (e) {
    const authResp = e.response;
    if (authResp?.data && authResp.status === 403) {
      return authResp.data;
    }
    logger.error('checkUserAccessToResource', e);
    throw e;
  }
}

export function deny(res: Response, authResponse?: CheckAuthResponse) {
  const { message = 'Access Denied', code = 403 } = authResponse || {};
  return res.status(code).send(message);
}

