  const usersInShop = await admin.firestore().collection('users').doc(user.uid).get();

  const roles = usersInShop.data()?.shops[headers.shop_domain].accessRoles || [];

  console.log('roles', roles);
  if (roles.includes(perm)) {
    return func(...params);
  } else {
    return false;
  }
}

