export function makePrettyAxiosError(error: AxiosError | any) {
  if (!error.response) {
    return error;
  }
  const { response } = error;
  const { data, status, statusText } = response;
  const pretty = new Error();
  pretty.message =
    typeof data === 'string'
      ? data
      : `${error.message} [${status} ${statusText}]: ${JSON.stringify(data, null, 2)}`;
  pretty.stack = error.stack;
  return pretty;
}

export function getBigtableClient(args?: { projectId: string }) {
  const project = args?.projectId || projectId;
  if (!storageClients[project]) {
    storageClients[project] = new Bigtable({ projectId: project });
  }
  return storageClients[project];
}

export function getBQClient(args: { projectId?: string } = {}) {
  const { projectId = bqResourceProject } = args;
  if (clients[projectId]) return clients[projectId];
  const bigquery = new BigQuery({ projectId });
  clients[projectId] = bigquery;
  bigquery.query = (async (...args) => {
    let span: Span;
    const context = getStoreKey('context');
    const logParams = getStoreKey('logParams');
    const resourceName = context.req?.route?.path || logParams.wsEventName;
    let referer;
    if (context.req?.headers?.referer) {
      referer = new URL(context.req.headers.referer).pathname;
    }
    const appComponent = calculateAppComponent(referer);
    const jobTimeoutMs = context.uf ? 1000 * 60 * 2 : undefined;
    const shopId = logParams.shopId
      ? logParams.shopId.split('.myshopify.com')[0].replaceAll('.', '-').toLowerCase().slice(0, 62)
      : 'unknown';
    let options: Query = {
      jobTimeoutMs,
      labels: {
        'shop-id': shopId,
      },
    };
    if (typeof args[0] === 'string') {
      options = {
        ...options,
        query: args[0],
        ...args[1],
      };
    } else {
      options = {
        ...options,
        ...args[0],
      };
    }

    if (tracer) {
      span = tracer.startSpan('bq.job', {
        childOf: tracer.scope().active(),
        tags: {
          'resource.name': resourceName || 'bq.job',
          'service.name': `${serviceId}-bq`,
          'tw.shopId': logParams.shopId,
          'tw.traceId': context.traceId,
          'tw.userId': logParams.userId,
          'tw.referer': referer,
          'tw.appComponent': appComponent,
          'bq.bq_project_id': projectId,
          component: 'bq',
        },
      });
      span.setBaggageItem('service.name', `${serviceId}-bq`);
    }
    return safeActivateSpan(span, async () => {
      let jobId;
      const timer = new Timer();
      let success = true;
      try {
        timer.start();
        const [job] = await bigquery.createQueryJob(options);
        jobId = job.id;
        const [res] = await job.getQueryResults();
        timer.end();
        return [res, job];
      } catch (e) {
        success = false;
        jobId = e.response?.jobReference?.jobId;
        if (tracer) {
          addErrorMessageToDatadog(e);
        }
        logger.error(e);
        throw e;
      } finally {
        bigquery
          .job(jobId)
          .getMetadata()
          .then((metadata) => {
            // https://cloud.google.com/bigquery/docs/reference/rest/v2/Job
            try {
              if (span) {
                const statistics = metadata[0].statistics || {};
                const query = statistics.query || {};
                const shuffleOutputBytes = calculateShuffleOutputBytes(query);
                const tablesAsList = getReferencedTables(query);
                const tables = tablesAsList.join(':');
                const cacheHit = query.cacheHit || false;
                const biEngineMode = calculateBiEngineMode(statistics?.query);
                const uf = String(context.uf);
                const ddTags = {
                  bq_project_id: projectId,
                  resource_name: resourceName,
                  cache_hit: cacheHit,
                  tables: crypto.createHash('md5').update(tables).digest('hex'),
                  success: String(success),
                  biEngineMode,
                  uf,
                  referer,
                  appComponent,
                };
                span.setTag('bq.uf', uf);
                span.setTag('bq.jobId', jobId);
                span.setTag('bq.tables', tables);
                span.setTag('bq.cache_hit', String(cacheHit));
                span.setTag('bq.success', success);
                span.setTag('bq.bi_engine_mode', biEngineMode);
                span.setTag(
                  'tw.jobMetadata',
                  `http://devops.srv.whale3.io/bq/job?projectId=${projectId}&jobId=${jobId}`
                );
                span.setTag(
                  'tw.jobInfo',
                  `https://console.cloud.google.com/bigquery?project=${projectId}&j=bq:US:${jobId}&page=queryresults`
                );
                const { ghostTime, totalTime, queueTime, finalExecutionTime } =
                  calculateQueryTimes(statistics);
                const twTotalTime = timer.elapsed().ms;
                const increments = {
                  'bq.jobs': 1,
                  'bq.total_bytes_processed': statistics.totalBytesProcessed || 0,
                  'bq.estimated_max_slots': calculateEstimatedMaxSlots(query),
                  'bq.tw_total_time_ms': twTotalTime,
                  'bq.total_slot_ms': statistics.totalSlotMs,
                  'bq.ghost_time_ms': ghostTime,
                  'bq.total_time_ms': totalTime,
                  'bq.final_execution_duration_ms': finalExecutionTime,
                  'bq.queued_time_ms': queueTime,
                  'bq.shuffle_output_bytes': shuffleOutputBytes,
                };
                submitDDMetrics({
                  metrics: increments,
                  metricType: 'increment',
                  tags: ddTags,
                });
                if (!isLocal)
                  callPubSub(
                    'bigquery-job-stats',
                    {
                      shopId: logParams.shopId,
                      userId: logParams.userId,
                      userEmail: context.req?.user?.email || context.socket?.user?.email,
                      jobId,
                      traceId: context.traceId,
                      resourceName,
                      bqProjectId: projectId,
                      serviceId,
                      uf: context.uf,
                      referer,
                      twTotalTime,
                      success,
                    },
                    {},
                    { batching: { maxMessages: 100, maxMilliseconds: 10 * 1000 } }
                  ).catch((e) => {
                    logger.error(e);
                  });
                span.finish();
              }
            } catch (e) {
              logger.error(e);
              if (span) span.finish();
            }
          })
          .catch(() => {});
      }
    });
  }) as any;
  return bigquery;
}

export function getReferencedTables(query: any) {
  const referencedTables = query?.referencedTables || [];
  const referencedTableNames = referencedTables.map((t) => t.tableId);
  const uniqueTables = [...new Set(referencedTableNames)];
  return uniqueTables.sort();
}

export function calculateQueryTimes(statistics: any) {
  const ghostTime =
    statistics?.endTime - statistics?.startTime - statistics?.finalExecutionDurationMs;
  const totalTime = statistics?.endTime - statistics?.creationTime;
  const queueTime = statistics?.startTime - statistics?.creationTime;
  const finalExecutionTime = statistics?.finalExecutionDurationMs;
  return { ghostTime, totalTime, queueTime, finalExecutionTime };
}

export function calculateShuffleOutputBytes(query: any) {
  return query?.queryPlan?.reduce((acc, plan) => acc + Number(plan.shuffleOutputBytes), 0);
}

export function calculateBiEngineMode(query: any) {
  return query?.biEngineStatistics?.biEngineMode || 'disabled';
}

export function calculateAppComponent(referer: string): string {
  if (referer === undefined) {
    return 'unknown';
  }
  if (referer == '/chat') {
    return 'chat';
  }
  if (referer.startsWith('/dashboards')) {
    return 'dashboards';
  }
  if (referer == '/sql-editor') {
    return 'sql-editor';
  }
  return '2.0';
}

export function calculateEstimatedMaxSlots(query: any) {
  return (
    query?.timeline?.reduce((max, obj) => {
      return Number(obj?.activeUnits) > max ? Number(obj?.activeUnits) : max;
    }, Number(query?.timeline[0]?.activeUnits || 0)) || 0
  );
}

export async function callServiceEndpoint<R = any, T = any>(
  serviceId: string,
  endpoint: string,
  params: T,
  options: {
    projectId?: string;
    method?: 'GET' | 'POST' | 'DELETE' | 'PUT' | 'PATCH';
    headers?: Partial<IncomingHttpHeaders>;
    axiosRetryConfig?: IAxiosRetryConfig;
    log?: boolean;
    forceCloud?: boolean;
    bearerToken?: string;
    params?: any;
    deployment?: string;
    stack?: string;
    localPort?: number;
    keepAlive?: boolean;
    axiosInstance?: AxiosInstance;
    timeout?: number; // ms
  } = {}
): Promise<AxiosResponse<R, T>> {
  const {
    log = true,
    axiosRetryConfig,
    forceCloud,
    bearerToken,
    deployment,
    stack,
    axiosInstance,
    keepAlive = true,
    timeout,
  } = options;
  const local = isLocal && !forceCloud;
  const logParams = getStoreKey('logParams');
  const context = getStoreKey('context');
  const { baseUrl, audience } = await getBaseUrl(
    serviceId,
    options?.projectId || process.env.PROJECT_ID,
    {
      local,
      deployment,
      stack,
      localPort: options.localPort,
      useUfDeployment: context.uf,
    }
  );
  const client = axiosInstance || getAxiosClient({ axiosRetryConfig, keepAlive });
  const url = baseUrl + '/' + endpoint;
  const method = options?.method || 'POST';

  if (log)
    logger.info(
      `${isLocal ? new Date().toISOString() + ': ' : ''}requesting internal url: ${url} [${method}]`
    );

  try {
    const requestOptions: AxiosRequestConfig = {
      url,
      method,
      params: options.params || {},
      timeout,
    };
    if (method === 'GET') {
      requestOptions.params = options.params || params;
    } else {
      requestOptions.data = params;
    }

    if (isLocal && forceCloud) {
      requestOptions.params.forceCloud = 'true';
    }

    const addedHeaders: Partial<IncomingHttpHeaders> = {};
    if (logParams.shopId) addedHeaders['x-tw-shop-id'] = logParams.shopId;
    if (context.traceId) addedHeaders['X-Cloud-Trace-Context'] = context.traceId + '/0;o=1';
    if (context.uf) addedHeaders['x-tw-uf'] = 'true';
    if (context.req?.headers['referer']) addedHeaders['referer'] = context.req.headers['referer'];

    if (audience) {
      addedHeaders['authorization'] = `Bearer ${bearerToken || (await getIdToken(audience))}`;
    }

    if (!options.headers?.['user-agent']) {
      addedHeaders['user-agent'] = `${process.env.SERVICE_ID}/${process.env.TW_VERSION}`;
    }

    requestOptions.headers = {
      ...addedHeaders,
      'x-tw-service-id': process.env.SERVICE_ID,
      ...options.headers,
    };
    const response = await client.request<any, AxiosResponse<R, T>>(requestOptions);
    if (log) logger.info(`${isLocal ? new Date().toISOString() + ': ' : ''}done request to ${url}`);
    return response;
  } catch (err) {
    if (isLocal && err.code === '404') {
      logger.error(`Got 404 when trying to call ${serviceId}, Service might not be running`);
      return err;
    }
    logger.error(`callServiceEndpoint: Error calling ${url}`, isLocal ? '' : prettyAxiosError(err));
    throw err;
  }
}

