export function loadDataDog(
  args: { tracerOptions?: TracerOptions; disableDataDog?: boolean } = {}
) {
  let { tracerOptions = {}, disableDataDog } = args;
  const {
    profiling = isProd && !process.env.IS_SPOT ? true : false,
    runtimeMetrics = isProd && !process.env.IS_SPOT ? true : false,
  } = tracerOptions;
  const envOptions = process.env['TW_DD_CONFIG'];
  if (((isLocal || !isK8s) && !process.env.DD) || envOptions === '"false"' || disableDataDog) {
    console.log('not initing dd.');
    return;
  }
  tracer = require('dd-trace').tracer;

  let options: TracerOptions = {
    startupLogs: true,
    profiling,
    sampleRate: 1,
    flushInterval: 10000,
    logInjection: false,
    runtimeMetrics,
    env: projectId,
    service: serviceId,
    version: process.env.TW_VERSION || '1.0.0',
    tags: {
      triplewhale_com_deployment:
        process.env.TW_DEPLOYMENT || (isLocal ? process.env.USER : 'unknown'),
      'pulumi-stack': process.env.PULUMI_STACK_NAME,
      'pulumi-project': process.env.PULUMI_PROJECT_NAME,
      'tw.hostname': process.env.HOSTNAME || process.env.USER,
    },
  };

  if (envOptions) {
    try {
      options = { ...options, ...JSON.parse(envOptions) };
    } catch {}
  }
  if (tracerOptions) {
    options = { ...options, ...tracerOptions };
  }

  if (isLocal) console.log('initing dd with options: ', options);

  tracer.init(options);

  tracer.use('express', {
    blocklist: ['/ping'],
    middleware: false,
    hooks: {
      request: (span, req, res) => {
        span.addTags({
          'http.size': req?.socket?.bytesWritten,
        });
      },
    },
  });
  return tracer;
}

export function safeActivateSpan(span: Span, fn: (...args: any[]) => unknown) {
  if (span && tracer) {
    return tracer.scope().activate(span, fn);
  }
  return fn();
}

export function decodePubSubMessage(body: any): { data: any; attributes?: any } {
  // push
  if (body.message?.data) {
    let data = Buffer.from(body.message.data, 'base64').toString('utf-8');
    try {
      data = JSON.parse(data);
    } catch (e) {}
    return {
      data,
      attributes: body.message.attributes,
    };
  }
  // pull, if someone pushed a falsey value as push, default to obj
  return {
    data: JSON.parse(Buffer.from(body.data, 'base64').toString('utf-8')) || {},
    attributes: body.attributes || {},
  };
}

export function deepDiff(fromObject: any, toObject: any, specificPaths?: string[]) {
  const changes = {};
  const buildPath = (path, __, key) => (_.isUndefined(path) ? key : `${path}.${key}`);

  let obj1 = {};
  let obj2 = {};
  if (_.isArray(specificPaths) && !_.isEmpty(specificPaths)) {
    for (const path of specificPaths) {
      if (_.has(fromObject, path)) {
        _.set(obj1, path, _.get(fromObject, path));
      } else if (_.has(toObject, path)) {
        changes[path] = { to: _.get(toObject, path) };
      }
      if (_.has(toObject, path)) {
        _.set(obj2, path, _.get(toObject, path));
      } else if (_.has(fromObject, path)) {
        changes[path] = { from: _.get(fromObject, path) };
      }
    }
  } else {
    obj1 = fromObject;
    obj2 = toObject;
  }

  const walk = (fromObject, toObject, path?) => {
    for (const key of _.keys(fromObject)) {
      const currentPath = buildPath(path, fromObject, key);
      if (!_.has(toObject, key)) {
        changes[currentPath] = { from: _.get(fromObject, key) };
      }
    }

    for (const [key, to] of _.entries(toObject)) {
      const currentPath = buildPath(path, toObject, key);
      if (!_.has(fromObject, key)) {
        changes[currentPath] = { to };
      } else {
        const from = _.get(fromObject, key);
        if (!_.isEqual(from, to)) {
          if (_.isObjectLike(to) && _.isObjectLike(from)) {
            walk(from, to, currentPath);
          } else {
            changes[currentPath] = { from, to };
          }
        }
      }
    }
  };

  walk(obj1, obj2);

  return changes;
}

