export function concurrencyLimit(limit: number, mode: 'queue' | 'throttle' = 'throttle') {
  let current = 0;
  const queue: { req: Request; res: Response; next: NextFunction }[] = [];
  function finishReq() {
    current--;
    if (mode === 'queue' && queue.length) {
      queue.shift().next();
    }
  }
  function handleConcurrencyLimit(req: Request, res: Response, next: NextFunction) {
    res.on('close', () => {
      if (mode === 'throttle' && res[IGNORE]) {
        return;
      }
      finishReq();
    });

    if (current < limit) {
      current++;
      return next();
    }
    if (mode === 'throttle') {
      logger.debug(`concurrencyLimit throttling req, ${current} ${limit}`);
      res[IGNORE] = true;
      return res.status(429).send('Reached max current.');
    }
    if (mode === 'queue') {
      logger.debug(`concurrencyLimit queuing req, ${current} ${limit}`);
      queue.push({ req, res, next });
    }
  }
  return handleConcurrencyLimit;
}

export function demoToRealShopMiddleware(_shopId?: (req) => string) {
  async function replaceDemoShop(req: RequestWithUser, res: Response, next: NextFunction) {
    const user = req.user;
    if (!user?.admin) {
      return next();
    }
    const shopId = !_shopId ? req.headers?.[TW_SHOP_ID_HEADER]?.toString() : _shopId(req);
    if (!shopId) {
      return next();
    }
    const realShopId = await getRealShopId(shopId, user);

    if (realShopId !== shopId) {
      req.body = JSON.parse(JSON.stringify(req.body).replace(new RegExp(shopId, 'g'), realShopId));

      if (req.query) {
        Object.keys(req.query).forEach((key) => {
          if (typeof req.query[key] === 'string') {
            req.query[key] = req.query[key].toString().replace(new RegExp(shopId, 'g'), realShopId);
          }
        });
      }

      if (req.headers[TW_SHOP_ID_HEADER]) {
        req.headers[TW_SHOP_ID_HEADER] = realShopId;
      }
    }
    next();
  }
  return endpointWrapper(replaceDemoShop);
}

