export function removeServiceIdFromPath(serviceId, path) {
  let result = path;
  const urlParts = path.split('/');
  if (urlParts[1] === serviceId) {
    urlParts[1] = '';
    urlParts.shift();
    result = urlParts.join('/');
  }
  return result;
}

export const extractProductHandleFromUrl = (url: string) => {
  if (!validUrl(url)) {
    return '';
  }
  const urlObj = new URL(url);
  const { pathname } = urlObj;
  const pathParts = pathname.split('/');

  if (pathParts.includes('products')) {
    return pathParts.pop();
  }

  // if (pathParts.includes('collections')) {
  //   return pathParts.pop();
  // }

  return '';
};

/**
 * @description Each time a shop changes its plan or gets an addon, a pubsub is emitted from the subscription manager
 * with an object of type `FeatureFlagDiffValueMap`.  Multiple services listen to this pub sub and run their changes
 * based on that object.  This function takes in a map of feature flags and their corresponding `FFChangeHandler`
 * callbacks.  If one of the updated feature flags from the diff exists in the provided map, the callback for that
 * feature flag will be run.  Otherwise, it's ignored.
 */
export const getFFChangePubSubHandler =
  (featureFlagHandlerMap: FFChangeHandlerMap) => async (req: Request, res: Response) => {
    const { shopId, featureFlagDiffMap }: FFChangedDTO = req.body.data;
    logger.info('featureFlagChanged', { shopId, featureFlagDiffMap });

    try {
      const changes = Object.keys(featureFlagHandlerMap).map((featureFlag: FeatureFlag) => {
        // ignore if featureFlag doesn't exist in diff map or is undefined
        if (!featureFlagDiffMap[featureFlag]) return;

        const diffValue = featureFlagDiffMap[featureFlag];
        const changeHandler = featureFlagHandlerMap[featureFlag];
        return changeHandler(shopId, diffValue);
      });

      await Promise.all(changes);
    } catch (err) {
      // This is just extra precaution - errors should be handled in the provided callbacks.
      logger.error('Error in ff change pubsub handler:>>', err.message, err);
    } finally {
      return res.send();
    }
  };

export async function checkPeriod(periodRequest: dataHealthRequest) {
  // @ts-ignore
  const {
    shopId,
    start,
    end,
    jobId,
    jobType,
    healthCheckType = HealthCheckType.AdsMetrics,
    accountIds,
  } = periodRequest;
  const serviceHealthCheckConf = services[periodRequest.serviceId].healthCheckConf;

  logger.info(
    `checkPeriod ${jobId ? `jobId ${jobId}` : ''} ${
      jobType ? `jobType ${jobType}` : ''
    } shopId ${shopId} start ${start} end ${end}`
  );

  async function checkDayInternal(accountId: string, day: string) {
    const list = [];
    const isInternalDeltaError = (dataA, dataB) => {
      const factor = 0.005;
      const { spend: spendA } = dataA || {};
      const { spend: spendB } = dataB || {};
      return Math.abs(spendA / spendB - 1) > factor;
    };
    try {
      const checkParam: MetricsQueryStringParams = {
        start: day,
        end: day,
        service_id: periodRequest.serviceId,
        data_type: 'ads-metrics',
        forceBigTable: true, // TODO temp until the data will be updated in BQ
        shopId,
        account_ids: [accountId],
        granularity: 'total',
      };

      const { data: dayFamilyCheck } = await callServiceEndpoint<
        ApiMetricsResponse,
        MetricsQueryStringParams
      >(
        'metrics-table',
        'get-metrics',
        {
          ...checkParam,
          family: 'day',
        },
        { method: 'POST' }
      );

      const { data: hoursFamilyCheck } = await callServiceEndpoint<
        ApiMetricsResponse,
        MetricsQueryStringParams
      >(
        'metrics-table',
        'get-metrics',
        {
          ...checkParam,
          family: 'hours',
        },
        { method: 'POST' }
      );

      return {
        hoursFamilyCheck,
        dayFamilyCheck,
        check: !isInternalDeltaError(
          hoursFamilyCheck.data[0]?.metricsBreakdown[0]?.metrics,
          dayFamilyCheck.data[0]?.metricsBreakdown[0]?.metrics
        ),
      };
    } catch (e) {
      throw e;
    }
  }

  async function checkPeriodData(shopData, period: periodObject) {
    const allAccounts = services[periodRequest.serviceId].getAccounts(shopData);
    const accounts = allAccounts.filter(
      (acc) => !accountIds || !accountIds.length || accountIds.includes(acc.id)
    );
    if (moment(period.start).isSame(period.end)) {
      const checkPromises = accounts.map(async (acc) => {
        return await checkDayInternal(acc.id, period.start);
      });

      const accountCheck = await Promise.all(checkPromises);

      let hasInternalDelta = false;
      accountCheck.forEach((c) => {
        if (!c.check) hasInternalDelta = true;
      });

      if (hasInternalDelta) return CHECK_STATUS.INTERNAL_DELTA;
    }

    const params: dataHealthRequest = {
      serviceId: periodRequest.serviceId,
      shopId,
      start: period.start,
      sendSlack: false,
      softImport: false,
      jobId: periodRequest.jobId,
      end: period.end,
      factor: periodRequest.factor,
      healthCheckType,
      accountIds,
    };

    let healthStatus: Record<ServicesIds, DataHealthFullReport[]>;
    try {
      healthStatus = (
        await callServiceEndpoint<Record<ServicesIds, DataHealthFullReport[]>, dataHealthRequest>(
          'internal',
          'data-health-check',
          params,
          {
            method: 'POST',
          }
        )
      ).data;
    } catch (e) {
      throw e;
    }
    if (!healthStatus?.[periodRequest.serviceId]) return CHECK_STATUS.UNKNOWN;

    for (const { id } of accounts) {
      const accountHealthStatus: DataHealthFullReport = healthStatus?.[
        periodRequest.serviceId
      ]?.find((x) => x.accountId === id);

      if (!accountHealthStatus) {
        logger.warn(
          `shop ${shopId} account ${id} ${jobId ? `jobId ${jobId}` : ''} check for start ${
            period.start
          } end ${period.end} is unknown!`
        );
        return CHECK_STATUS.UNKNOWN;
      }
      if (
        accountHealthStatus &&
        isDeltaError(
          accountHealthStatus,
          periodRequest.factor,
          moment(period.end).diff(period.start, 'days'),
          undefined
        )
      ) {
        const notEmptyFields = Object.keys(accountHealthStatus.fieldsComparison).some(
          (field) => accountHealthStatus.fieldsComparison[field].internal
        );
        if (!notEmptyFields) {
          logger.warn(
            `shop ${shopId} account ${id} ${jobId ? `jobId ${jobId}` : ''} check for start ${
              period.start
            } end ${period.end} internal data is ZERO!`
          );
          return CHECK_STATUS.INTERNAL_ZERO;
        }

        logger.warn(
          `shop ${shopId} account ${id} ${jobId ? `jobId ${jobId}` : ''} check for start ${
            period.start
          } end ${period.end} is with DELTA!`
        );
        return CHECK_STATUS.DELTA;
      }
    }

    logger.info(
      `shop ${shopId} ${jobId ? `jobId ${jobId}` : ''} check for start ${period.start} end ${
        period.end
      } is GOOD!`
    );
    return CHECK_STATUS.GOOD;
  }

  const shopData = await getShopData(shopId, { mongo: periodRequest?.jobType !== 'initial' });

  let stack: periodObject[] = [];
  let newStack: periodObject[] = [];
  let deltaDaysList: importDay[] = [];
  let periodsList: any[] = [];

  const dateRangeLimit = services[serviceId].dateRangeLimit;

  if (dateRangeLimit) {
    let periodStart = moment(start);
    let periodEnd = moment(end);

    while (periodStart <= periodEnd) {
      let partialPeriodEnd = moment
        .min(periodEnd, moment(periodStart).add(dateRangeLimit - 1, 'days'))
        .clone();

      stack.push({
        start: periodStart.format('YYYY-MM-DD'),
        end: partialPeriodEnd.format('YYYY-MM-DD'),
      });
      periodStart = partialPeriodEnd.add(1, 'days');
    }
  } else {
    stack.push({ start, end });
  }

  async function runSinglePeriodCheck(period: periodObject) {
    const daysDiff = moment(period.end).diff(moment(period.start), 'days');

    try {
      let periodStatus: CHECK_STATUS = await checkPeriodData(shopData, period);
      periodsList.push({ ...period, status: periodStatus });
      switch (periodStatus) {
        case CHECK_STATUS.GOOD:
          return;
        // continue;
        case CHECK_STATUS.UNKNOWN:
        case CHECK_STATUS.INTERNAL_ZERO:
          const days = moment(period.end).diff(moment(period.start), 'days');
          for (let i = 0; i <= days; i++) {
            deltaDaysList.push({
              day: moment(period.end).subtract(i, 'days').format('YYYY-MM-DD'),
              status: periodStatus,
            });
          }
          if (periodStatus === CHECK_STATUS.INTERNAL_ZERO) return;
          else {
            logger.warn(
              `can't get health status for ${shopId} ${jobId ? `jobId ${jobId}` : ''} start ${
                period.start
              } end ${period.end}. return all period with status UNKNOWN.`
            );
            return;
            // throw `can't get health status for ${shopId} ${jobId ? `jobId ${jobId}` : ''} start ${
            //   period.start
            // } end ${period.end}`;
          }

        // continue;
      }

      switch (daysDiff) {
        // not neccery becouse the next option, we not adding single day period to the stack
        case 0:
          deltaDaysList.push({ day: period.start, status: periodStatus });
          return;
      }
    } catch (e) {
      throw e;
    }

    const splitPeriodDay = moment(period.start).add(Math.floor(daysDiff / 2), 'days');

    newStack.push({ start: period.start, end: splitPeriodDay.format('YYYY-MM-DD') });
    newStack.push({ start: splitPeriodDay.add(1, 'days').format('YYYY-MM-DD'), end: period.end });
  }

  logger.info(
    `checkPeriod: start checking. isSerialPeriodsChecks: ${serviceHealthCheckConf?.isSerialPeriodsChecks}`,
    { shopId, jobId }
  );

  while (stack.length > 0) {
    if (serviceHealthCheckConf?.isSerialPeriodsChecks) {
      for (const period of stack) {
        await runSinglePeriodCheck(period);
      }
    } else {
      const promises = stack.map(async (period: periodObject) => runSinglePeriodCheck(period));
      await Promise.all(promises);
    }

    stack = newStack;
    newStack = [];
  }
  const daysDiff = moment(end).diff(moment(start), 'days') + 1;

  logger.info(
    `shopId ${shopId} ${
      jobId ? `jobId ${jobId}` : ''
    } days in period: ${daysDiff}. periods checks: ${periodsList.length}. days with delta: ${
      deltaDaysList.length
    }`
  );

  return {
    deltaDaysList: deltaDaysList.sort((a, b) => {
      return b.day.localeCompare(a.day);
    }),
    periodsList,
  };
}

export async function startJob(args: startJobOptions): Promise<startJobResponse> {
  const req: Request = args.req as any;
  const {
    defaultEndpoint = 'import-day-data',
    queueName = `${serviceId}-import-day-data`,
    endpoints,
  } = args;

  const data = req.body.data as FetchChunkData;
  const taskName = req.headers['x-cloudtasks-taskname'];
  const logger = getJobLogger(data, taskName);

  logger.info('start job request');
  const jobManagerData = await getJob(data.jobID);
  // if (jobManagerData?.status === 'in_progress') {
  //   logger.warn(
  //     `${serviceId} start-job shop ${data.shopDomain} jobID ${data.jobID} already running ignoring this call`
  //   );
  //   throw `${serviceId} start-job shop ${data.shopDomain} jobID ${data.jobID} already running ignoring this call`;
  // }

  if (serviceId !== req.body.attributes['serviceId']) {
    logger.warn(`expected service id ${serviceId} but got: ${req.body.attributes['serviceId']}`);
    throw new Error(
      `expected service id ${serviceId} but got: ${req.body.attributes['serviceId']}`
    );
  }

  const endpoint =
    endpoints?.find((ep) => ep.conditionString === data[ep.conditionField])?.endpoint ||
    defaultEndpoint;

  logger.info(`checkBefore from api ${data.checkBefore} jobId ${data.jobID}`);

  if (data.jobType === 'initial' && data.checkBefore) {
    try {
      const timestamp = new Date().getTime();
      logger.info(
        `start initial import for shop ${data.shopDomain} checkPeriod before jobId ${data.jobID}`
      );
      await updateJob(data.jobID, {
        status: 'in_progress',
      });

      const { deltaDaysList, periodsList } = await checkPeriod({
        shopId: data.shopDomain,
        start: moment(data.day).subtract(data.total, 'days').format('YYYY-MM-DD'),
        end: data.day,
        jobId: data.jobID,
        jobType: data.jobType,
        serviceId: serviceId,
        healthCheckType: data.healthCheckType,
        accountIds: data.accountIds,
      });

      logger.info(
        `Initial import for shop ${data.shopDomain} jobId ${
          data.jobID
        } checkPeriod before done #days ${deltaDaysList.length} days:${JSON.stringify(
          deltaDaysList
        )} , Took: ${new Date().getTime() - timestamp}ms`
      );
      logger.info(
        `Initial import for shop ${data.shopDomain} jobId ${
          data.jobID
        } checkPeriod before done, periodsList ${JSON.stringify(periodsList)}`
      );

      if (deltaDaysList.length === 0) {
        logger.info(
          `Finish import for shop ${data.shopDomain} jobId ${data.jobID} 0 days to import`
        );
      }

      await updateJob(data.jobID, {
        total: deltaDaysList.length,
      });

      data.days = deltaDaysList;
      data.day = null;
      data.total = null;
      data.checkBefore = false;
    } catch (e) {
      logger.error(
        `error while checkPeriod before for shop ${data.shopDomain} jobid ${data.jobID}`,
        e
      );
    }
  }

  return { data, endpoint, queueName };
}

export async function importDayFetcher(
  req: Request,
  res: Response,
  fetchFunction: (args: any) => Promise<any>
) {
  let body = req.body as FetchChunkData;
  const queueName = req.headers['x-cloudtasks-queuename'] as string;
  const taskName = req.headers['x-cloudtasks-taskname'];
  let { shopData } = body;
  const { jobType, index, shopDomain, jobID, dataType, forceExternalFetch, days, accountIds } =
    body;
  shopData = shopData ?? (await getShopData(shopDomain, { mongo: jobType !== 'initial' }));
  let { total, day } = body;
  const logger = getJobLogger(body, taskName);
  let timer;

  const preconditionResult = await checkPrecondition(req);
  if (!preconditionResult.continue) {
    logger.info(
      `Precondition continue for jobID ${jobID} shopDomain ${shopDomain} will not import`
    );
    return res.send('ok');
  }

  try {
    if (days && total) {
      const message = `when using days, total and day are manage internally, don't send them jobID ${jobID}`;
      logger.error(message);
      const result = await onFailure(req);
      return res.status(418).send(result);
    }

    if (!total && days) {
      if (days.length === 0) {
        // in case the health check find 0 days to import - just close the job
        logger.info(`import day JobId ${jobID} shopDomain ${shopDomain} days is 0 done the job`);
        await onSuccess(req);
        return res.send('ok');
      }
      total = days.length;
      (day as any) = days[index - 1]?.day ?? days[index - 1];
      logger.debug(`import day ${day} index ${index} days ${JSON.stringify(days)} JobId ${jobID}`);
      logger.info(`import day ${day} index ${index} JobId ${jobID}`);
    }

    if (!shopDomain) {
      const message = `error no shopDomain (${shopData}) JobId ${jobID}`;
      logger.error(message);
      const result = await onFailure(req);
      return res.status(418).send(result);
    }

    await updateJob(jobID, {
      currentIndex: index,
      status: 'in_progress',
      shopDomain,
      jobType,
    });

    logger.info(
      `starting import day shopDomain ${shopDomain} day ${day} type ${jobType} jobId ${jobID}`
    );

    let checkBefore: boolean = body.checkBefore !== false;
    if (days) {
      checkBefore = days[index - 1]?.status === CHECK_STATUS.UNKNOWN || false;
    }

    await retry(
      async () => {
        logger.info(
          `Calling fetchFunction import day shopDomain ${shopDomain} day ${day} total ${total} type ${jobType} jobId ${jobID} checkBefore ${checkBefore}`
        );
        await fetchFunction({ ...body, total, day, checkBefore });
      },
      {
        retries: 1,
        maxTimeout: 2 * 60 * 1000, // max 2 minutes between retries
        onRetry: (e) => {
          let msg;
          try {
            msg = JSON.stringify(e);
          } catch {}
          logger.warn(`Doing retry on error jobId ${jobID}: ${msg ?? e}`);
        },
      }
    );
    await updateLastImport(shopDomain, serviceId, day ? moment(day).toDate() : moment().toDate());
    await onSuccess(req);
    return res.send('ok');
  } catch (e) {
    logger.warn(
      `importDayFetcher general error shopDomain ${shopDomain} jobID ${jobID} jobType ${jobType}:  error: ${JSON.stringify(
        e
      )}`
    );
    clearTimeout(timer);
    const result = await onFailure(req, e);
    return res.status(418).send(result);
  }
}

export const facebookGetValueFromAction = (
  data: FacebookActionStat,
  attributionWindow: string[]
) => {
  if (!data) return 0;
  let val = 0;
  attributionWindow.forEach((window) => {
    if (data[window]) {
      val += +data[window] || 0;
    }
  });
  return val;
};

export const getAccountsWithDelta = async (
  checkBefore: boolean,
  serviceId: ServicesIds,
  shopDomain: string,
  day: string,
  accounts: any[],
  jobID: string
) => {
  let accountsWithDelta;
  const params: dataHealthRequest = {
    serviceId,
    shopId: shopDomain,
    start: day,
    sendSlack: false,
    softImport: false,
    end: day,
    accountIds: accounts.map(({ id }) => id),
  };
  if (!checkBefore) {
    return accounts;
  }
  let healthCheckOk = true;
  let healthStatus: Record<ServicesIds, DataHealthFullReport[]>;
  try {
    logger.info(`healthStatus check before reimport day ${day} shop ${shopDomain} jobID ${jobID}`);
    healthStatus = (
      await callServiceEndpoint<Record<ServicesIds, DataHealthFullReport[]>, dataHealthRequest>(
        'internal',
        'data-health-check',
        params,
        {
          method: 'POST',
        }
      )
    ).data;
  } catch (e) {
    healthCheckOk = false;
    logger.error(
      `ERROR healthStatus check before reimport day ${day} shop ${shopDomain} jobID ${jobID} error: ${e.message}`
    );
  }

  accountsWithDelta = accounts.filter(({ id }) => {
    const accountHealthStatus: DataHealthFullReport = healthStatus?.[serviceId]?.find(
      (x) => x.accountId === id
    );
    if (
      healthCheckOk &&
      accountHealthStatus &&
      !isDeltaError(
        accountHealthStatus,
        undefined, // factor, default 0.01
        undefined, // diffDays, default 1
        1
      )
    ) {
      logger.info(
        `Nothing change for account ${id} day ${day} shop ${shopDomain} jobID ${jobID} will not import, continue`
      );
      return false;
    } else {
      return true;
    }
  });

  return accountsWithDelta;
};

export const isDeltaError = (
  fullReport: DataHealthFullReport,
  factor: number = 0.03,
  diffDays: number = 0,
  minDelta?: number,
  healthCheckType: HealthCheckType = HealthCheckType.AdsMetrics
) => {
  const logExtraData = {
    shopId: fullReport.shopId,
    factor,
    diffDays,
    minDelta,
    serviceId: fullReport.serviceId,
  };
  logger.info(`isDeltaError: started.`, logExtraData);

  const serviceHealthCheckConf = services[fullReport.serviceId].healthCheckConf?.fields;

  if (!serviceHealthCheckConf) {
    logger.warn(`isDeltaError: health-check conf is not defined for service.`, logExtraData);
    return false;
  }
  const criticalFields = Object.entries(serviceHealthCheckConf).filter(
    ([_, field]) => field.isCritical
  );

  factor = factor / (diffDays + 1);

  for (const [field, conf] of criticalFields) {
    const fieldMinDelta = minDelta || conf.minDelta || DEFAULT_MIN_DELTA;
    const internalNum = fullReport.fieldsComparison[field]?.internal;
    const delta = fullReport.fieldsComparison[field]?.delta;

    if (
      internalNum === undefined ||
      internalNum === null ||
      delta === undefined ||
      delta === null
    ) {
      logger.info(`isDeltaError: field: ${field}, return true undefined checking.`, logExtraData);
      return true;
    }
    if (!internalNum && Math.abs(delta) > 0) {
      logger.info(
        `isDeltaError: field: ${field}, return true internal 0 or null but not delta.`,
        logExtraData
      );
      return true;
    }
    const actualCheck =
      Math.abs(+delta?.toFixed(2) / +internalNum?.toFixed(2)) > factor &&
      Math.abs(delta) >= fieldMinDelta;
    if (actualCheck) {
      logger.info(
        `isDeltaError: field: ${field}, Math.abs(+delta?.toFixed(2) / +num?.toFixed(2)) = ${Math.abs(
          +delta?.toFixed(2) / +internalNum?.toFixed(2)
        )} 
                  > factor = ${factor} = ${
          Math.abs(+delta?.toFixed(2) / +internalNum?.toFixed(2)) > factor
        } && Math.abs(delta) 
                  >= ${fieldMinDelta} = ${
          Math.abs(delta) >= fieldMinDelta
        } equal - res ${actualCheck}.`,
        logExtraData
      );
      return true;
    }
  }

  logger.info(`isDeltaError: ended. return false end of the function.`, logExtraData);
  return false;
};

export async function checkPrecondition(
  req: Request,
  args?: { timeoutHandle?: number }
): Promise<preconditionResult> {
  const body = req.body as FetchChunkData;
  const retryCount = +req.headers['x-cloudtasks-taskretrycount'];
  const taskName = req.headers['x-cloudtasks-taskname'];
  const { jobType, index, shopDomain, jobID, shopData, day, dataType } = body;
  const { timeoutHandle } = args || {};
  let timer: NodeJS.Timeout;
  const logger = getJobLogger(body, taskName);

  const jobManagerData = await getJob(jobID);

  // when starting `index` is 1 and jobManagerData.currentIndex is 0

  // Check if canceled
  if (jobManagerData.requestCancel) {
    await updateJob(jobID, {
      status: 'done',
      canceledAt: Date.now(),
      successful: false,
      shopDomain,
      jobType,
    });
    const message = `job was canceled jobID ${jobID}`;
    logger.warn(message);
    return { continue: false, message };
  }

  // check if duplicate
  if (jobManagerData.currentIndex === index) {
    // might still be a retry
    if (retryCount === 0) {
      const message = `this might be a duplicate jobID ${jobID}`;
      logger.warn(message);
      return { continue: false, message };
    }
  }

  // check starting at earlier index then current
  if (index < jobManagerData.currentIndex) {
    // this will choke wild forks
    const message = `got a request to start with index ${index} but job manager is already on ${jobManagerData.currentIndex} jobID ${jobID}`;
    logger.warn(message);
    return { continue: false, message };
  }

  // Check if forked due to timeout ;)
  if (retryCount > 0 && req.headers['x-cloudtasks-taskpreviousresponse'] === '0') {
    // we have a retry that is not a 4xx or 5xx error so if current index was started we
    // prob had a timeout
    if (jobManagerData.currentIndex === index) {
      // https://cloud.google.com/tasks/docs/creating-http-target-tasks#handler
      const message = `We got a retry that is a fork, not retrying. jobID ${jobID} headers: ${JSON.stringify(
        req.headers
      )}`;
      logger.error(message);
      return { continue: false, message };
    }
    logger.info(`network related retry jobID ${jobID}`);
  }
  if (
    !isProviderConnectedToShop(
      await getShopData(shopDomain, { mongo: jobType !== 'initial' }),
      serviceId
    )
  ) {
    await updateJob(jobID, {
      status: 'failed',
      failedAt: Date.now(),
      successful: false,
      shopDomain,
      jobType,
    });
    const message = `No ${serviceId} Access Token or Accounts for shop ${shopDomain} jobID ${jobID}`;
    logger.info(message);
    return { continue: false, message };
  }

  if (timeoutHandle) {
    timer = preRequestTimeout(timeoutHandle, async () => {
      const jobManagerData = await getJob(jobID);

      if (!(jobManagerData.currentIndex === index)) {
        // this is mostly for shopify products that run in a single loop, and we do not want to
        // restart them, if they fail so be it.
        // this does work for the rest bcuz most of the times when we get a timeout, it's not bcuz
        // the job is still runinng but bcuz it got stuck and will never recover,
        // but sometimes imports might take morw than an hour, just something to keep
        // in mind.
        logger.warn(
          `getting close to timeout, but job manager has different index, ${jobManagerData.currentIndex} ${index} jobID ${jobID}`
        );
        return;
      }

      logger.warn(
        `very close to timeout so updating job and resending current task jobID ${jobID}`
      );
      await updateJob(jobID, { currentIndex: index - 1 });
      // recall this
      await callImport(req, body, day, index);
    });
  }
  logger.info(`precondition ${shopDomain} day ${day} type ${jobType} jobID ${jobID}`);
  req['timer'] = timer;
  return { continue: true, timer };
}

export async function onSuccess(req: Request) {
  const body = req.body as FetchChunkData;
  const taskName = req.headers['x-cloudtasks-taskname'];
  const { jobType, index, shopDomain, jobID, day, days } = body;
  let { total } = body;
  const logger = getJobLogger(body, taskName);

  if (req['timer']) clearTimeout(req['timer']);

  if (days?.length) {
    total = days?.length;
  }

  // Check if done
  if (index >= total) {
    if (jobType === 'initial') {
      try {
        await callServiceEndpoint(
          'internal',
          'data-health-check/recheck-health-by-reports',
          { jobId: jobID },
          { method: 'POST' }
        );
      } catch (error) {
        logger.error(`error updating data health report after job done ${jobID}`, { error });
      }
    }
    await updateJob(jobID, {
      status: 'done',
      finishedAt: Date.now(),
      successful: true,
      shopDomain,
      jobType,
    });
    logger.info(`done job jobId ${jobID}`);
    return { success: true };
  }

  // call next

  const nextDay = days ? null : moment(day).subtract(1, 'days').format('YYYY-MM-DD');
  // const nextDay = moment(day).subtract(1, 'days').format('YYYY-MM-DD');
  logger.info(
    `import nextDay shopDomain ${shopDomain} nextDay ${
      days ? days[index].day : nextDay
    } type ${jobType} jobId ${jobID}`
  );

  await callImport(req, body, nextDay, index + 1);
  logger.info(`finished import`);
}

export async function onFailure(req: Request, e?) {
  const body = req.body as FetchChunkData;
  const taskName = req.headers['x-cloudtasks-taskname'];
  const { jobType, shopDomain, jobID } = body;
  const logger = getJobLogger(body, taskName);
  if (req['timer']) clearTimeout(req['timer']);
  await updateJob(jobID, {
    successful: false,
    status: 'failed',
    failedAt: Date.now(),
    shopDomain,
    jobType,
  });
  if (e) {
    logger.error(
      `onFailure shopDomain ${shopDomain} jobID ${jobID} jobType ${jobType},  error: ${JSON.stringify(
        e
      )}`
    );
  }
  return { success: false };
}

