export function getJobLogger(jobData: any, taskName?) {
  const { jobID, index, shopDomain, dataType, day, jobType } = jobData;
  const loggerOptions = {
    name: jobID || 'unknown',
    jobID,
    index,
    day,
    jobType: jobType || 'unknown',
    shopDomain,
    serviceId,
    dataType,
  };
  if (taskName) {
    loggerOptions['taskName'] = taskName;
  }
  const logger = getLogger({ options: loggerOptions });
  return logger;
}

export function preRequestTimeout(timeoutHandle, handleTimeout: () => any) {
  const timer = setTimeout(handleTimeout, timeoutHandle);
  return timer;
}

export async function callImport(req: Request, body: FetchChunkData, day, index) {
  const { shopDomain, jobID, dataType, days } = body;
  const queueName = req.headers['x-cloudtasks-queuename'] as string;

  await addTaskToQueue(
    queueName,
    serviceId,
    `${req.path}?day=${
      days ? days[index - 1].day : day
    }&shop=${shopDomain}&dataType=${dataType}&jobID=${jobID}`,
    {
      ...req.body,
      day: day,
      index: index,
    },
    { dispatchDeadline: 1800, deployment: process.env.TW_DEPLOYMENT }
  );
}

export function buildCollectionCondition({
  collectionName,
  subCollectionName,
  propertyFieldName,
  propertyType,
  comparator,
  values,
}: {
  collectionName: string;
  subCollectionName?: string;
  propertyFieldName: string;
  propertyType: FilterPropertyType;
  comparator: FilterComparator;
  values;
}): string {
  if (propertyFieldName === 'count') {
    return buildCondition({
      propertyFieldName: `ARRAY_LENGTH(${collectionName})`,
      propertyType,
      comparator,
      values,
    });
  }

  const valueCondition = buildCondition({ propertyFieldName, propertyType, comparator, values });
  const unnestExpression = `UNNEST(${collectionName}) ${
    subCollectionName ? `, UNNEST(${subCollectionName})` : ''
  } ${subCollectionName || collectionName}`;
  const countExpression = `SELECT COUNT(${propertyFieldName}) FROM ${unnestExpression} where ${valueCondition}`;

  if (isNegativeComparator(comparator)) {
    return `(${countExpression}) = ARRAY_LENGTH(${collectionName})`;
  } else {
    return `(${countExpression}) > 0`;
  }
}

