export function handleValueSpecialChars(value, comparator?: FilterComparator) {
  const replace = (val) => {
    const newVal = val.replace(/\'/g, "\\'");
    if (comparator === FilterComparator.CONTAIN || comparator === FilterComparator.NOT_CONTAIN) {
      return newVal.replace(/_/g, '\\_');
    }
    return newVal;
  };

  if (typeof value === 'string') {
    return replace(value);
  }

  if (Array.isArray(value)) {
    return value.map((val) => {
      if (typeof val === 'string') {
        return replace(val);
      }

      return val;
    });
  }

  return value;
}

export const backupFsCollection = async (
  collectionName: string,
  includeSubCollections: boolean = true
) => {
  logger.info(
    `Backing up collection ${collectionName} to GCS, includeSubCollections: ${includeSubCollections}`
  );
  const data: { [docId: string]: any } = {};
  try {
    const collectionSnapshot = await firestore().collection(collectionName).get();
    const limit = pLimit(100);
    await Promise.all(
      collectionSnapshot.docs.map(async (doc) =>
        limit(async () => {
          const docData = doc.data();

          if (includeSubCollections) {
            const subCollectionRef = await doc.ref.listCollections();
            const subCollections = {};
            await Promise.all(
              subCollectionRef.map(async (sub) => {
                const subCollectionData: { [subdocId: string]: any } = {};
                const subCollectionSnapshot = await sub.get();
                subCollectionSnapshot.forEach((subDoc) => {
                  subCollectionData[subDoc.id] = subDoc.data();
                });
                subCollections[sub.id] = subCollectionData;
              })
            );
            docData.subCollections = subCollections;
          }

          data[doc.id] = docData;
        })
      )
    );

    const options: SaveOptions = {
      contentType: 'application/json',
    };
    await saveToBucket(
      `${collectionName}-docs-backups-${process.env.PROJECT_ID}`,
      `${collectionName}.json`,
      data,
      options
    );
    logger.info(`Backup of collection ${collectionName} to GCS completed`);
  } catch (error) {
    logger.error(`Error while backing up collection ${collectionName} to GCS`, error);
    throw error;
  }
};

export const restoreFsCollection = async (
  collectionName: string,
  restoredCollectionData: {
    [docId: string]: { [key: string]: any };
  }
) => {
  try {
    const batchArray: firestore.WriteBatch[] = [];
    const BATCH_SIZE = 500; // Firestore batch write limit
    let operationCounter = 0;
    let batchIndex = 0;
    batchArray.push(firestore().batch());

    const promises = [];

    for (const [docId, docData] of Object.entries(restoredCollectionData)) {
      const docRef = firestore().collection(collectionName).doc(docId);
      const { subCollections, ...rest } = docData as any;
      batchArray[batchIndex].set(docRef, rest, { merge: true });
      operationCounter++;

      if (subCollections) {
        for (const [subCollectionName, subDocs] of Object.entries(subCollections)) {
          for (const [subDocId, subDocData] of Object.entries(subDocs)) {
            const subDocRef = docRef.collection(subCollectionName).doc(subDocId);
            batchArray[batchIndex].set(subDocRef, subDocData, { merge: true });
            operationCounter++;

            if (operationCounter >= BATCH_SIZE) {
              batchArray.push(firestore().batch());
              batchIndex++;
              operationCounter = 0;
            }
          }
        }
      }
    }

    batchArray.forEach((batch) => promises.push(batch.commit()));
    await Promise.all(promises);

    logger.info(`Firestore collection restored successfully ${collectionName}.`);
  } catch (error) {
    logger.error('Error while restoring Firestore collection', { error, collectionName });
    throw new Error('Error while restoring Firestore collection');
  }
};

