export function getStorageClient(
  opts: { forceCloud?: boolean; storageOptions?: Partial<StorageOptions> } = {}
) {
  const forceCloud = opts.forceCloud || process.env.FORCE_CLOUD === 'true';
  const key = forceCloud ? 'forceCloud' : 'plain';
  if (!storageClients[key]) {
    const options: StorageOptions = {
      projectId: process.env.PROJECT_ID,
      ...opts.storageOptions,
    };
    if (isLocal && !forceCloud) options.apiEndpoint = 'http://localhost:8086';
    storageClients[key] = new Storage(options);
  }
  return storageClients[key];
}

export async function downloadFile(
  bucketName: string,
  name: string,
  additionalOptions?: { forceCloud?: boolean; generation?: number }
) {
  const client = getStorageClient({ forceCloud: additionalOptions?.forceCloud });
  const dataAsString = (
    await client
      .bucket(bucketName)
      .file(name, { generation: additionalOptions?.generation })
      .download()
  )[0].toString();
  const data = JSON.parse(dataAsString);
  return data;
}

export async function saveToBucket<T>(
  bucketName: string,
  filename: string,
  data: T,
  options: SaveOptions,
  bucketOptions?: BucketOptions,
  additionalOptions?: { forceCloud: boolean },
  storageOptions?: Partial<StorageOptions>
) {
  const storageClient = getStorageClient({
    forceCloud: additionalOptions?.forceCloud,
    storageOptions,
  });
  // https://github.com/googleapis/google-cloud-node/issues/654
  if (process.env.IS_LOCAL) {
    options.validation = false;
  }
  const bucket = storageClient.bucket(bucketName, bucketOptions);
  const stringData = typeof(data) === 'string' ? data : JSON.stringify(data);
  return await bucket.file(filename).save(stringData, {
    // we want to support upload in 8 KB/s (1 Kb/s very slow), so:
    timeout: 120000 /* 120 seconds per chunk */,
    // chunkSize: 1024 * 1024 /* 1MB per chunk */,

    ...options,
  });
}

export function getGeneratedHours(day: string, timezone: string) {
  const hoursArray: string[] = [];
  let currentTime = moment.tz(day, timezone).startOf('day');
  const isToday = moment.tz(day, timezone).isSame(moment().tz(timezone), 'day');
  const endTime = isToday ? moment().tz(timezone) : moment.tz(day, timezone).endOf('day');
  while (currentTime <= endTime) {
    hoursArray.push(moment.tz(currentTime, timezone).format('YYYY-MM-DDTHH'));
    currentTime = moment.tz(currentTime, timezone).add(1, 'hour');
  }
  return hoursArray;
}

export function getGeneratedDates(
  startDate: string,
  endDate: string,
  granularity: 'hour' | 'day' | 'week' | 'month',
  timezone: string
) {
  if (granularity === 'hour') return getGeneratedHours(startDate, timezone);
  const dateArray: string[] = [];
  let currentDate = moment.tz(startDate, timezone);
  const stopDate = moment.tz(endDate, timezone);
  const momentGranularity = granularity === 'week' ? 'isoWeek' : granularity;
  while (currentDate <= stopDate) {
    dateArray.push(
      moment.tz(currentDate, timezone).startOf(momentGranularity).format('YYYY-MM-DD')
    );
    currentDate = moment.tz(currentDate, timezone).add(1, granularity);
  }
  return dateArray;
}

