export async function checkPeriod(periodRequest: IntegrationDataHealthRequest)
{
  const { accountId, start, end, jobId } = periodRequest;
  const serviceHealthCheckConf = services[periodRequest.serviceId].healthCheckConf;

  logger.info(
    `checkPeriod ${jobId ? `jobId ${jobId}` : ''} accountId ${accountId} start ${start} end ${end}`
  );

  async function checkDayInternal(accountId: string, day: string) {
    const list = [];
    const isInternalDeltaError = (dataA, dataB) => {
      const factor = 0.005;
      const { spend: spendA } = dataA || {};
      const { spend: spendB } = dataB || {};
      return Math.abs(spendA / spendB - 1) > factor;
    };
    try {
      const checkParam: MetricsQueryStringParams = {
        start: day,
        end: day,
        service_id: periodRequest.serviceId,
        data_type: 'ads-metrics',
        account_ids: [accountId],
        granularity: 'total',
        forceBigTable: true
      };

      const { data: dayFamilyCheck } = await callServiceEndpoint<
        ApiMetricsResponse,
        MetricsQueryStringParams
      >(
        'metrics-table',
        'get-metrics',
        {
          ...checkParam,
          family: 'day'
        },
        { method: 'POST' }
      );

      const { data: hoursFamilyCheck } = await callServiceEndpoint<
        ApiMetricsResponse,
        MetricsQueryStringParams
      >(
        'metrics-table',
        'get-metrics',
        {
          ...checkParam,
          family: 'hours'
        },
        { method: 'POST' }
      );

      return {
        hoursFamilyCheck,
        dayFamilyCheck,
        check: !isInternalDeltaError(
          hoursFamilyCheck.data[0]?.metricsBreakdown[0]?.metrics,
          dayFamilyCheck.data[0]?.metricsBreakdown[0]?.metrics
        )
      };
    } catch (e) {
      throw e;
    }
  }

  async function checkPeriodData(accountId, period: periodObject) {
    const accountCheck = await checkDayInternal(accountId, period.start);

    if (!accountCheck.check) {
      return CHECK_STATUS.INTERNAL_DELTA;
    }

    const params: IntegrationDataHealthRequest = {
      serviceId: periodRequest.serviceId,
      accountId,
      start: period.start,
      sendSlack: false,
      softImport: false,
      // @ts-ignore
      jobId: periodRequest.jobId,
      end: period.end,
      factor: periodRequest.factor
    };

    let healthStatus: Record<ServicesIds, IntegrationDataHealthFullReport[]>;
    try {
      healthStatus = (
        await callServiceEndpoint<
          Record<ServicesIds, IntegrationDataHealthFullReport[]>,
          IntegrationDataHealthRequest
        >('internal', 'data-health-check', params, {
          method: 'POST'
        })
      ).data;
    } catch (e) {
      throw e;
    }
    if (!healthStatus?.[periodRequest.serviceId]) return CHECK_STATUS.UNKNOWN;

    const accountHealthStatus: IntegrationDataHealthFullReport = healthStatus?.[
      periodRequest.serviceId
      ]?.find((x) => x.accountId === accountId);

    if (!accountHealthStatus) {
      logger.warn(
        `accountId ${accountId} account ${accountId} ${
          jobId ? `jobId ${jobId}` : ''
        } check for start ${period.start} end ${period.end} is unknown!`
      );
      return CHECK_STATUS.UNKNOWN;
    }
    if (
      accountHealthStatus &&
      isDeltaError(
        accountHealthStatus,
        periodRequest.factor,
        moment(period.end).diff(period.start, 'days')
      )
    ) {
      const notEmptyFields = Object.keys(accountHealthStatus.fieldsComparison).some(
        (field) => accountHealthStatus.fieldsComparison[field].internal
      );
      if (!notEmptyFields) {
        logger.warn(
          `accountId ${accountId} account ${accountId} ${
            jobId ? `jobId ${jobId}` : ''
          } check for start ${period.start} end ${period.end} internal data is ZERO!`
        );
        return CHECK_STATUS.INTERNAL_ZERO;
      }

      logger.warn(
        `accountId ${accountId} account ${accountId} ${
          jobId ? `jobId ${jobId}` : ''
        } check for start ${period.start} end ${period.end} is with DELTA!`
      );
      return CHECK_STATUS.DELTA;
    }

    logger.info(
      `accountId ${accountId} ${jobId ? `jobId ${jobId}` : ''} check for start ${
        period.start
      } end ${period.end} is GOOD!`
    );
    return CHECK_STATUS.GOOD;
  }

  const accountData = await getServiceAccountData(accountId);

  let stack: periodObject[] = [];
  let newStack: periodObject[] = [];
  let deltaDaysList: importDay[] = [];
  let periodsList: any[] = [];

  const dateRangeLimit = services[serviceId].dateRangeLimit;

  if (dateRangeLimit) {
    let periodStart = moment(start);
    let periodEnd = moment(end);

    while (periodStart <= periodEnd) {
      let partialPeriodEnd = moment
        .min(periodEnd, moment(periodStart).add(dateRangeLimit - 1, 'days'))
        .clone();

      stack.push({
        start: periodStart.format('YYYY-MM-DD'),
        end: partialPeriodEnd.format('YYYY-MM-DD')
      });
      periodStart = partialPeriodEnd.add(1, 'days');
    }
  } else {
    stack.push({ start, end });
  }

  async function runSinglePeriodCheck(period: periodObject) {
    const daysDiff = moment(period.end).diff(moment(period.start), 'days');

    try {
      let periodStatus: CHECK_STATUS = await checkPeriodData(accountData, period);
      periodsList.push({ ...period, status: periodStatus });
      switch (periodStatus) {
        case CHECK_STATUS.GOOD:
          return;
        // continue;
        case CHECK_STATUS.UNKNOWN:
        case CHECK_STATUS.INTERNAL_ZERO:
          const days = moment(period.end).diff(moment(period.start), 'days');
          for (let i = 0; i <= days; i++) {
            deltaDaysList.push({
              day: moment(period.end).subtract(i, 'days').format('YYYY-MM-DD'),
              status: periodStatus
            });
          }
          if (periodStatus === CHECK_STATUS.INTERNAL_ZERO) return;
          else {
            logger.warn(
              `can't get health status for accountId ${accountId} ${
                jobId ? `jobId ${jobId}` : ''
              } start ${period.start} end ${period.end}. return all period with status UNKNOWN.`
            );
            return;
            // throw `can't get health status for ${shopId} ${jobId ? `jobId ${jobId}` : ''} start ${
            //   period.start
            // } end ${period.end}`;
          }

        // continue;
      }

      switch (daysDiff) {
        // not neccery becouse the next option, we not adding single day period to the stack
        case 0:
          deltaDaysList.push({ day: period.start, status: periodStatus });
          return;
      }
    } catch (e) {
      throw e;
    }

    const splitPeriodDay = moment(period.start).add(Math.floor(daysDiff / 2), 'days');

    newStack.push({ start: period.start, end: splitPeriodDay.format('YYYY-MM-DD') });
    newStack.push({ start: splitPeriodDay.add(1, 'days').format('YYYY-MM-DD'), end: period.end });
  }

  logger.info(
    `checkPeriod: start checking. isSerialPeriodsChecks: ${serviceHealthCheckConf?.isSerialPeriodsChecks}`,
    { accountId, jobId }
  );

  while (stack.length > 0) {
    if (serviceHealthCheckConf?.isSerialPeriodsChecks) {
      for (const period of stack) {
        await runSinglePeriodCheck(period);
      }
    } else {
      const promises = stack.map(async (period: periodObject) => runSinglePeriodCheck(period));
      await Promise.all(promises);
    }

    stack = newStack;
    newStack = [];
  }
  const daysDiff = moment(end).diff(moment(start), 'days') + 1;

  logger.info(
    `accountId ${accountId} ${
      jobId ? `jobId ${jobId}` : ''
    } days in period: ${daysDiff}. periods checks: ${periodsList.length}. days with delta: ${
      deltaDaysList.length
    }`
  );
  return {
    deltaDaysList: deltaDaysList.sort((a, b) => {
      return b.day.localeCompare(a.day);
    }),
    periodsList
  };
}

export async function startJob(args: startJobOptions): Promise<IntegrationStartJobResponse> {
  const req: Request = args.req as any;
  const {
    defaultEndpoint = 'import-day-data',
    queueName = `${serviceId}-import-day-data`,
    endpoints,
  } = args;

  const data = req.body.data as IntegrationFetchChunkData;
  const taskName = req.headers['x-cloudtasks-taskname'];
  const logger = getJobLogger(data, taskName);

  logger.info('start job request');
  const jobManagerData = await getJob(data.jobID);

  if (serviceId !== req.body.attributes['serviceId']) {
    logger.warn(`expected service id ${serviceId} but got: ${req.body.attributes['serviceId']}`);
    throw new Error(
      `expected service id ${serviceId} but got: ${req.body.attributes['serviceId']}`
    );
  }
  if (serviceId !== data.serviceId) {
    logger.warn(
      `expected service id ${serviceId} but got accountData service id: ${req.body.attributes['serviceId']}`
    );
    throw new Error(
      `expected service id ${serviceId} but got accountData service id: ${req.body.attributes['serviceId']}`
    );
  }

  const endpoint =
    endpoints?.find((ep) => ep.conditionString === data[ep.conditionField])?.endpoint ||
    defaultEndpoint;

  logger.info(`checkBefore from api ${data.checkBefore} jobId ${data.jobID}`);

  if (data.jobType === 'initial' && data.checkBefore) {
    try {
      const timestamp = new Date().getTime();
      logger.info(
        `start initial import for service ${data.serviceId} accountId ${data.accountId} checkPeriod before jobId ${data.jobID}`
      );
      await updateJob(data.jobID, {
        status: 'in_progress',
      });

      const { deltaDaysList, periodsList } = await checkPeriod({
        accountId: data.accountId,
        start: moment(data.day).subtract(data.total, 'days').format('YYYY-MM-DD'),
        end: data.day,
        jobId: data.jobID,
        serviceId: serviceId,
      });

      logger.info(
        `Initial import for accountId ${data.accountId} jobId ${
          data.jobID
        } checkPeriod before done #days ${deltaDaysList.length} days:${JSON.stringify(
          deltaDaysList
        )} , Took: ${new Date().getTime() - timestamp}ms`
      );
      logger.info(
        `Initial import for accountId ${data.accountId} jobId ${
          data.jobID
        } checkPeriod before done, periodsList ${JSON.stringify(periodsList)}`
      );

      if (deltaDaysList.length === 0) {
        logger.info(
          `Finish import for accountId ${data.accountId} jobId ${data.jobID} 0 days to import`
        );
      }

      await updateJob(data.jobID, {
        total: deltaDaysList.length,
      });

      data.days = deltaDaysList;
      data.day = null;
      data.total = null;
      data.checkBefore = false;
    } catch (e) {
      logger.error(
        `error while checkPeriod before for accountId ${data.accountId} jobid ${data.jobID}`,
        e
      );
    }
  }

  return { data, endpoint, queueName };
}

export async function importDayFetcher(
  req: Request,
  res: Response,
  fetchFunction: (args: any) => Promise<any>
) {
  let body = req.body as IntegrationFetchChunkData;
  const queueName = req.headers['x-cloudtasks-queuename'] as string;
  const taskName = req.headers['x-cloudtasks-taskname'];
  let { accountData } = body;
  const { jobType, index, accountId, jobID, dataType, forceExternalFetch, days } = body;
  accountData = accountData ?? (await getServiceAccountData(accountId));
  let { total, day } = body;
  const logger = getJobLogger(body, taskName);
  let timer;

  const preconditionResult = await checkPrecondition(req);
  if (!preconditionResult.continue) return res.send('ok');

  try {
    if (days && total) {
      const message = `when using days, total and day are manage internally, don't send them jobID ${jobID}`;
      logger.error(message, accountData);
      const result = await onFailure(req);
      return res.status(418).send(result);
    }

    if (!total && days) {
      if (days.length === 0) {
        // in case the health check find 0 days to import - just close the job
        logger.info(`import day JobId ${jobID} accountId ${accountId} days is 0 done the job`);
        await onSuccess(req);
        return res.send('ok');
      }
      total = days.length;
      (day as any) = days[index - 1]?.day ?? days[index - 1];
      logger.info(
        `import accountId ${accountId} day ${day} index ${index} days ${JSON.stringify(
          days
        )} JobId ${jobID}`
      );
    }

    if (!accountId) {
      const message = `error no accountId (${accountId}) JobId ${jobID}`;
      logger.error(message);
      const result = await onFailure(req);
      return res.status(418).send(result);
    }

    await updateJob(jobID, {
      currentIndex: index,
      status: 'in_progress',
      accountId,
      jobType,
    });

    logger.info(
      `starting import day accountId ${accountId} day ${day} type ${jobType} jobId ${jobID}`
    );

    let checkBefore: boolean = body.checkBefore !== false;
    if (days) {
      checkBefore = days[index - 1]?.status === CHECK_STATUS.UNKNOWN || false;
    }

    await retry(
      async () => {
        logger.info(
          `Calling fetchFunction import day accountId ${accountId} day ${day} total ${total} type ${jobType} jobId ${jobID} checkBefore ${checkBefore}`
        );
        await fetchFunction({ ...body, total, day, checkBefore });
      },
      {
        retries: 1,
        maxTimeout: 2 * 60 * 1000, // max 2 minutes between retries
        onRetry: (e) => {
          let msg;
          try {
            msg = JSON.stringify(e);
          } catch {}
          logger.warn(`Doing retry on error jobId ${jobID}: ${msg ?? e}`);
        },
      }
    );
    try {
      await updateServiceLastUpdated(
        accountId,
        serviceId,
        day ? moment(day).toISOString() : moment().toISOString()
      );
    } catch (e) {
      logger.error(`importDayFetcher updateServiceLastUpdated failed ERROR `, e);
    }
    await onSuccess(req);
    return res.send('ok');
  } catch (e) {
    logger.warn(
      `importDayFetcher general error accountId ${accountId} jobID ${jobID} jobType ${jobType}:  error:`,
      e
    );
    clearTimeout(timer);
    const result = await onFailure(req, e);
    return res.status(418).send(result);
  }
}

export const getAccountsWithDelta = async (checkBefore: boolean, serviceId: ServicesIds, shopDomain: string, day: string, accounts: any[], jobID: string) => {
  let accountsWithDelta;
  const params: dataHealthRequest = {
    serviceId,
    shopId: shopDomain,
    start: day,
    sendSlack: false,
    softImport: false,
    end: day
  };
  if (!checkBefore) {
    return accounts;
  }
  let healthCheckOk = true;
  let healthStatus: Record<ServicesIds, DataHealthFullReport[]>;
  try {
    logger.info(
      `healthStatus check before reimport day ${day} shop ${shopDomain} jobID ${jobID}`
    );
    healthStatus = (
      await callServiceEndpoint<Record<ServicesIds, DataHealthFullReport[]>, dataHealthRequest>(
        'internal',
        'data-health-check',
        params,
        {
          method: 'POST'
        }
      )
    ).data;
  } catch (e) {
    healthCheckOk = false;
    logger.error(
      `ERROR healthStatus check before reimport day ${day} shop ${shopDomain} jobID ${jobID} error: ${e.message}`
    );
  }

  accountsWithDelta = accounts.filter(({ id }) => {
    const accountHealthStatus: DataHealthFullReport = healthStatus?.[serviceId]?.find((x) => x.accountId === id);
    if (
      healthCheckOk &&
      accountHealthStatus &&
      !isDeltaError(
        accountHealthStatus,
        undefined, // factor, default 0.01
        undefined, // diffDays, default 1
        1
      )
    ) {
      logger.info(
        `Nothing change for account ${id} day ${day} shop ${shopDomain} jobID ${jobID} will not import, continue`
      );
      return false;
    } else {
      return true;
    }
  });

  return accountsWithDelta;
};

export const isDeltaError = (
  fullReport: IntegrationDataHealthFullReport,
  factor: number = 0.03,
  diffDays: number = 0,
  minDelta?: number
) => {
  const logExtraData = {
    accountId: fullReport.accountId,
    factor,
    diffDays,
    minDelta,
    serviceId: fullReport.serviceId,
  };
  logger.info(`isDeltaError: started.`, logExtraData);

  const serviceHealthCheckConf = services[fullReport.serviceId].healthCheckConf?.fields;

  if (!serviceHealthCheckConf) {
    logger.warn(`isDeltaError: health-check conf is not defined for service.`, logExtraData);
    return false;
  }
  const criticalFields = Object.entries(serviceHealthCheckConf).filter(
    ([_, field]) => field.isCritical
  );

  factor = factor / (diffDays + 1);

  for (const [field, conf] of criticalFields) {
    const fieldMinDelta = minDelta || conf.minDelta || DEFAULT_MIN_DELTA;
    const internalNum = fullReport.fieldsComparison[field]?.internal;
    const delta = fullReport.fieldsComparison[field]?.delta;

    if (
      internalNum === undefined ||
      internalNum === null ||
      delta === undefined ||
      delta === null
    ) {
      logger.info(`isDeltaError: field: ${field}, return true undefined checking.`, logExtraData);
      return true;
    }
    if (!internalNum && Math.abs(delta) > 0) {
      logger.info(
        `isDeltaError: field: ${field}, return true internal 0 or null but not delta.`,
        logExtraData
      );
      return true;
    }
    const actualCheck =
      Math.abs(+delta?.toFixed(2) / +internalNum?.toFixed(2)) > factor &&
      Math.abs(delta) >= fieldMinDelta;
    if (actualCheck) {
      logger.info(
        `isDeltaError: field: ${field}, Math.abs(+delta?.toFixed(2) / +num?.toFixed(2)) = ${Math.abs(
          +delta?.toFixed(2) / +internalNum?.toFixed(2)
        )} 
                  > factor = ${factor} = ${
          Math.abs(+delta?.toFixed(2) / +internalNum?.toFixed(2)) > factor
        } && Math.abs(delta) 
                  >= ${fieldMinDelta} = ${
          Math.abs(delta) >= fieldMinDelta
        } equal - res ${actualCheck}.`,
        logExtraData
      );
      return true;
    }
  }

  logger.info(`isDeltaError: ended. return false end of the function.`, logExtraData);
  return false;
};

export async function checkPrecondition(
  req: Request,
  args?: { timeoutHandle?: number }
): Promise<preconditionResult> {
  const body = req.body as IntegrationFetchChunkData;
  const retryCount = +req.headers['x-cloudtasks-taskretrycount'];
  const taskName = req.headers['x-cloudtasks-taskname'];
  const { jobType, index, accountId, jobID, accountData, day, dataType } = body;
  const { timeoutHandle } = args || {};
  let timer: NodeJS.Timeout;
  const logger = getJobLogger(body, taskName);

  const jobManagerData = await getJob(jobID);

  // when starting `index` is 1 and jobManagerData.currentIndex is 0

  // Check if canceled
  if (jobManagerData.requestCancel) {
    await updateJob(jobID, {
      status: 'done',
      canceledAt: Date.now(),
      successful: false,
      accountId,
      jobType,
    });
    const message = `job was canceled jobID ${jobID}`;
    logger.warn(message);
    return { continue: false, message };
  }

  // check if duplicate
  if (jobManagerData.currentIndex === index) {
    // might still be a retry
    if (retryCount === 0) {
      const message = `this might be a duplicate jobID ${jobID}`;
      logger.warn(message);
      return { continue: false, message };
    }
  }

  // check starting at earlier index then current
  if (index < jobManagerData.currentIndex) {
    // this will choke wild forks
    const message = `got a request to start with index ${index} but job manager is already on ${jobManagerData.currentIndex} jobID ${jobID}`;
    logger.warn(message);
    return { continue: false, message };
  }

  // Check if forked due to timeout ;)
  if (retryCount > 0 && req.headers['x-cloudtasks-taskpreviousresponse'] === '0') {
    // we have a retry that is not a 4xx or 5xx error so if current index was started we
    // prob had a timeout
    if (jobManagerData.currentIndex === index) {
      // https://cloud.google.com/tasks/docs/creating-http-target-tasks#handler
      const message = `We got a retry that is a fork, not retrying. jobID ${jobID} headers: ${JSON.stringify(
        req.headers
      )}`;
      logger.error(message);
      return { continue: false, message };
    }
    logger.info(`network related retry jobID ${jobID}`);
  }

  // TODO: check if we need this
  // if (!(await getIsServiceConnected(await getShopData(shopDomain), serviceId))) {
  //   const message = `No ${serviceId} Access Token or Accounts for shop ${shopDomain} jobID ${jobID}`;
  //   logger.info(message);
  //   return { continue: false, message };
  // }

  if (timeoutHandle) {
    timer = preRequestTimeout(timeoutHandle, async () => {
      const jobManagerData = await getJob(jobID);

      if (!(jobManagerData.currentIndex === index)) {
        // this is mostly for shopify products that run in a single loop, and we do not want to
        // restart them, if they fail so be it.
        // this does work for the rest bcuz most of the times when we get a timeout, it's not bcuz
        // the job is still runinng but bcuz it got stuck and will never recover,
        // but sometimes imports might take morw than an hour, just something to keep
        // in mind.
        logger.warn(
          `getting close to timeout, but job manager has different index, ${jobManagerData.currentIndex} ${index} jobID ${jobID}`
        );
        return;
      }

      logger.warn(
        `very close to timeout so updating job and resending current task jobID ${jobID}`
      );
      await updateJob(jobID, { currentIndex: index - 1 });
      // recall this
      await callImport(req, body, day, index);
    });
  }
  logger.info(`precondition ${accountId} day ${day} type ${jobType} jobID ${jobID}`);
  req['timer'] = timer;
  return { continue: true, timer };
}

export async function onSuccess(req: Request) {
  const body = req.body as IntegrationFetchChunkData;
  const taskName = req.headers['x-cloudtasks-taskname'];
  const { jobType, index, accountId, jobID, day, days } = body;
  let { total } = body;
  const logger = getJobLogger(body, taskName);

  if (req['timer']) clearTimeout(req['timer']);

  if (days?.length) {
    total = days?.length;
  }

  // Check if done
  if (index >= total) {
    await updateJob(jobID, {
      status: 'done',
      finishedAt: Date.now(),
      successful: true,
      accountId,
      jobType,
    });
    logger.info(`done job jobId ${jobID}`);
    return { success: true };
  }

  // call next

  const nextDay = days ? null : moment(day).subtract(1, 'days').format('YYYY-MM-DD');
  // const nextDay = moment(day).subtract(1, 'days').format('YYYY-MM-DD');
  logger.info(
    `import nextDay shopDomain ${accountId} nextDay ${
      days ? days[index].day : nextDay
    } type ${jobType} jobId ${jobID}`
  );

  await callImport(req, body, nextDay, index + 1);
  logger.info(`finished import`);
}

export async function onFailure(req: Request, e?) {
  const body = req.body as IntegrationFetchChunkData;
  const taskName = req.headers['x-cloudtasks-taskname'];
  const { jobType, accountId, jobID } = body;
  const logger = getJobLogger(body, taskName);
  if (req['timer']) clearTimeout(req['timer']);
  await updateJob(jobID, {
    successful: false,
    status: 'failed',
    failedAt: Date.now(),
    accountId,
    jobType,
  });
  if (e) {
    logger.error(`onFailure accountId ${accountId} jobID ${jobID} jobType ${jobType},  error:`, e);
  }
  return { success: false };
}

