export function getLogger(args?: {
  options?: Omit<LoggerOptions, 'name'>;
  throttleArgs?: ThrottleArgs;
}): bunyan {
  let { options = {}, throttleArgs } = args || {};
  let { level, name = process.env.SERVICE_ID || 'none' } = options as LoggerOptions;
  if (!throttleArgs && process.env.LOG_THROTTLE) {
    try {
      throttleArgs = JSON.parse(process.env.LOG_THROTTLE);
    } catch (e) {
      console.error('failed to parse LOG_THROTTLE');
    }
  }
  level = level || (process.env.LOG_LEVEL as LogLevel);
  if (!level) {
    if (isLocal) {
      level = 'trace';
    } else if (isStaging) {
      level = 'debug';
    } else {
      level = 'info';
    }
  }
  const streams: bunyan.Stream[] = [{ type: 'raw', stream: decoratedStream({ throttleArgs }) }];
  const logger = bunyan.createLogger({
    streams,
    ...options,
    level,
    name,
    serializers: { err: bunyan.stdSerializers.err },
  });
  return logger;
}

export function setLogContext(logContext: Record<string, any>, args?: { addToDD?: boolean }) {
  const logParams = getStoreKey('logParams');
  Object.assign(logParams, logContext);
  const parentLogParams = getStoreKey('context').parentLogParams;
  if (parentLogParams) {
    Object.assign(parentLogParams, logContext);
  }
  if (args?.addToDD && tracer) {
    tracer.scope().active().addTags(logContext);
  }
}

export function mapCamelToSnake<SrcType, DestType extends Record<string, any>>(
  obj: SrcType
): DestType {
  const result: Partial<DestType> = {};
  Object.keys(obj).forEach((key) => {
    const value = (obj as any)[key];
    if (value !== undefined) {
      const snakeKey = camelToSnake(key);
      (result as any)[snakeKey] = value;
    }
  });
  return result as DestType;
}

export const mergeAndSumObjectsToNumbersOnly: any = (objects: any[]) => {
  const res = {};
  objects.forEach((obj) => {
    Object.entries(obj).forEach(([key, value]) => {
      if (isNaN(Number(value))) {
        return;
      }
      if (!res[key]) {
        res[key] = 0;
      }
      res[key] += Number(value) || 0;
    });
  });
  return res;
};

export async function connectToMongoDb(args?: {
  autoIndex?: boolean;
  useLocal?: boolean;
  name?: string;
  setAsDefault?: boolean;
  defaultDb?: string;
  connectionOptions?: ConnectOptions;
  connectionString?: string;
}) {
  let connectionRetries = 0;
  let {
    autoIndex,
    useLocal,
    name,
    setAsDefault = true,
    defaultDb = process.env.SERVICE_ID,
    connectionOptions = {},
    connectionString = getSecret('MONGODB_URL'),
  } = args || {};

  if (!name) name = 'default';
  if (connections[name]) {
    logger.warn('mongodb: already connected');
    return connections[name];
  }

  if (isLocal && useLocal) {
    connectionString = `mongodb://localhost:27017/${defaultDb}`;
  }

  if (!connectionString) return;

  const options: mongoose.ConnectOptions = {
    authSource: 'admin',
    family: 4,
    autoIndex: autoIndex ? autoIndex : false,
    retryWrites: true,
    writeConcern: { w: 'majority' },
    ...connectionOptions,
  };

  while (connectionRetries < 10) {
    let connection: Connection;
    try {
      if (!connections[name]) {
        if (setAsDefault) {
          mongoose.connect(connectionString, options);
          connection = mongoose.connection;
        } else {
          connection = mongoose.createConnection(connectionString, options);
        }
        connections[name] = connection;
        await connection.asPromise();
      } else {
        connection = connections[name];
        await connection.getClient()?.connect();
        connection.setClient(connection.getClient());
      }
      logger.info(`MongoDB Connected: ${connection.host}`);
      return connection;
    } catch (e) {
      logger.error('MONGODB connection error: ', e);
      await sleep(1000);
      connectionRetries++;
    }
  }
}

