export function createCluster(mainFunction, args?: { workers?: number }) {
  const { workers = 1 } = args || {};
  if (cluster.isPrimary && !isLocal) {
    logger.info(`Primary (pid:${process.pid}) started. Launching ${workers} workers.`);

    // Fork workers.
    for (let i = 0; i < workers; i++) {
      cluster.fork();
    }

    cluster.on('exit', (worker, code, signal) => {
      logger.warn(
        `worker (pid:${worker.process.pid}) exited with code ${code} and signal ${signal}`
      );
      if (!Object.keys(cluster.workers).length) {
        logger.error('All workers exited, exiting...');
        process.exit(1);
      }
    });

    process.on('SIGTERM', () => {
      logger.info('Received SIGTERM, terminating workers...');
      for (const id in cluster.workers) {
        cluster.workers[id].kill('SIGTERM');
      }
    });
  } else {
    logger.info(`Worker (pid:${process.pid}) started`);
    mainFunction();
  }
}

export function eventLoopMonitor(thresholdMs: number = 100) {
  const cache = new Map<number, [number, number]>();
  function before(asyncId: number) {
    cache.set(asyncId, process.hrtime());
  }

  function after(asyncId: number) {
    const cached = cache.get(asyncId);
    if (cached == null) {
      return;
    }
    cache.delete(asyncId);

    const ms = process.hrtime(cached)[0];
    if (ms > thresholdMs) {
      logger.warn(`Event loop was blocked for ${ms}ms`);

      if (tracer) {
        const span = tracer.startSpan('EventLoopBlock', {
          childOf: tracer.scope().active(),
          startTime: new Date().getTime() - ms,
          tags: {
            hostname: os.hostname(),
          },
        });
        span.finish();
      }
    }
  }

  const asyncHook = createHook({ before, after });

  asyncHook.enable();
  return asyncHook;
}

export function createLruCache<T extends (...args: any[]) => any>(args: {
  options?: Partial<LRUCache.Options<any, any, any>>;
  func: T;
  encode: (...args: Parameters<T>) => string;
}) {
  const { options, func, encode } = args;
  const cache = new LRUCache({
    ttl: 5 * 60 * 1000, // 1 minute
    max: 10000,
    ttlAutopurge: false,
    ignoreFetchAbort: true,
    allowStaleOnFetchAbort: true,
    ...options,
  });
  const fetch = async (...args: Parameters<T>) => {
    const key = encode(...args);
    let res = cache.get(key);
    if (res) return res;
    res = await func(...args);
    cache.set(key, res);
    return res;
  };
  return { cache, fetch };
}

