export function addTags(sql: string, tags?: Record<GCPTags, string>): string {
  const allTags = { ...tags };
  const context = getStoreKey('context');
  if (context.req) {
    allTags.action = context.req.method;
    allTags.framework = 'express';
    allTags.route = context.req?.route?.path;
  }
  // https://google.github.io/sqlcommenter/spec/#format
  const tagsString = Object.entries(allTags)
    .map(([key, value]) => `${key}=${encodeURI(value)}`)
    .join(',');
  return `${sql} /* ${tagsString} */`;
}

export function convertNamedParams(parameterizedSql: string, params: Record<string, any>) {
  const [text, values] = Object.entries(params).reduce(
    (acc, [key, value]) => {
      const [sql, array, index] = acc;
      if (!sql.includes(`:${key}`)) {
        return acc;
      }
      return [
        sql.replaceAll(`:${key}`, `$${index}`),
        [...array, value],
        index + 1,
      ] as QueryReducerArray;
    },
    [parameterizedSql, [], 1] as QueryReducerArray
  );
  return { text, values };
}

